    }
    return s;
}

vec3 soundfunc( int samp, float time )
{
    float t = time*0.1;
    float ot = t*0.02;
    vec2 fs = vec2(0.);
    float s = 0.;
    float stime = time+1.;
    s = wop1(tone(scale(fract(stime/2./20.)*4.),time),time,0.1)*0.5;
    float bps = 0.5;
    float am = 1.;
    for (int i=0;i<6;i++) {
        s += bong(tone(scale(fract(stime/12./2.+float(i))*4.*bps)-24.,time),time,bps)*am;
        am *= 0.7;
        bps *= 2.;
    }
    bps = 2.;
    am = 0.7;
    for (int i=0;i<6;i++) {
        s += wop(tone(scale(fract(stime/8./2.+float(i))*4.*bps)-2.,time),time,bps)*am;
        am *= 0.7;
        bps *= 2.;
    }
    fs = vec2(s)*0.5;
    return vec3( fs.x , fs.y , ot );
}
vec2 soundfreq( int samp, float time )
{
    return soundfunc(samp,time).xy;
}

