#define PI 3.141592654

precision highp float;

// wave frequency, not Hz
const float freq = 30.;

// speed of wave propagation
const float speed = 5.;

// power of wave suppresion over distance
const float diming = 5.;

// maximum thickness of wave 
const float window = .25;

// maximum texture displacement value
const float maximum = .1;

// add more wave sources here
const vec2[] points = vec2[](
    vec2(.1, .1),
    vec2(.9, .2),
    vec2(.3, .4)
);

// just for visualization purpose, remove it later
float deubgColor(float x) {
    return .5 * (x + 1.);
}

float suppresion(float len, float power) {
    return 1. / pow(abs(len) + 1., power);
}

float delta(float duration) {
    if (sin(duration * iTime) <= 0.)
        return 0.;
    return fract(duration * iTime / PI);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float delta = delta(.5);
    vec2 ratio = vec2(iResolution.x / iResolution.y, 1.);

    float dAmplitude = 0.;
    for (int i = 0; i < points.length(); i++) {
        vec2 center = points[i];
        vec2 uv = (fragCoord.xy / iResolution.xy) - center;
        float r = length(uv * ratio);

        float phase = delta * speed;
        float amplitude = suppresion(r, diming) * cos(freq * (r - phase));        

        float sx = phase - window;
        float ex = sx - window;

        if (r > sx || r < ex) {
            float dist = min(abs(sx - r), abs(ex - r));
            float sp = 1. - smoothstep(0., window, dist);
            amplitude *= sp;
        }
        dAmplitude += amplitude;
    }


    float displace_k  = dAmplitude * maximum;
    vec2 uv = (fragCoord.xy / iResolution.xy);
    vec2 displaced_uv = vec2(uv.xy + displace_k);

    fragColor = texture(iChannel0, displaced_uv);

    // just for visualization purpose, remove it later
    if (uv.x < 0.5) fragColor = vec4(vec3(deubgColor(dAmplitude)), 1.);
}

//Day 4 Lambmeow
//This one was all over the place, was planning on adding a proper fresnel on the reflection but couldnt get it work
//so total refraction it is.
//Lopea 2022

#define time (mod(iTime, 50.))
float cir(vec3 p, float d)
{

   p = mod (p + vec3(6.,6.,10.), vec3(12, 12.,20.)) - vec3(6., 6.,10.);
   p.y += 2.5;
   p.x = abs(p.x) - 3.;

   vec3 q = abs(p - vec3(0,5., 0)) - vec3(1,2., 1);
   float ang = radians(180.);
   p.xy *= mat2(cos(ang), -sin(ang), sin(ang),cos(ang));
   p.y *= .78;
  vec3 qa = abs(p);

   float tr =  max(qa.z-1.,max(qa.x*0.866025+p.y*0.5,-p.y)-2.*0.5);
   return min(tr, length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.));
}

float box(vec3 p, vec3 b, float r)
{
    vec3 q = abs(p) - b;

    return length(max(p, 0.)) + min(max(q.x,max(q.y,q.z)), 0.);
}

float four(vec3 p)
{
    return length(p) - 1.;
}

vec2 ss(vec3 p)
{

    vec2 res = vec2(100, -1); 

    float a = cir(p, 1.);

    res.x = min(a, res.x);

    if(a < 0.01)
       res.y = 1.;       
    float b = four(p - vec3(0, 0, time *  10.));
    res.x = min(res.x, b);

    if(abs(res.x- b) < 0.01)
        res.y = 0.;

    return res;

}

vec3 nn(vec3 p)
{
    vec3 n = vec3(0);
    for(int i = min(iFrame, 0); i < 4; i ++)
    {
        vec3 e = 0.5773* (2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*ss(p +e*0.0001).x;
    }
    return normalize(n);
}
vec2 rt(vec3 ro, vec3 rd, inout vec3 p)
{
    vec2 dd = vec2(0);
    float ll = 0.;

    for(int i = 0; i < 200; i ++)
    {
       p = ro + rd  * ll;

       dd = ss(p);

       ll += dd.x;

       if(dd.x < 0.001 || ll > 100.)
           break;
    }

    if(dd.x > 0.001)
        p = vec3(-1);
    return dd;
}


vec4 rfl( vec3 ro, vec3 rd, inout vec3 p)
{
    vec2 dd;

    for(int i = 0; i < 2; i ++)
    {
    dd = rt(p, rd, p); 
    if(dd.x > 0.01)
        return vec4(rd, -1);
        vec3 n = nn(p);
        rd = (refract(n, rd, 0.55));
       p += rd * 0.001;
   }
   return vec4(rd, dd.y);
}

vec4 bb(vec3 p, vec3 rd, float id,vec3 ro)
{

  float y = max(.5 - rd.y, 0.);
  vec4 bg = vec4((1.- y) * vec3(0.5, 0.5,.8), 1.) +  vec4(y * vec3(0.2, 1., 0.8), 1.);
  if(id == 1.)
  {
     vec3 n = nn(p);
     vec3 ll = normalize(vec3(1, 1 , 1));
     float ndl = max(dot(ll, n), 0.);
     float noise = texture(iChannel0, floor(p.xy)/10000. + sin(time + 3.) * .001).r; 
     return mix(vec4(noise * vec3(.2,.5, .5),1.), bg, vec4( ndl * length(ro - p)/100.));
  }
  return bg;
}

vec4 gc(vec3 ro, vec3 rd)
{
   vec3 p = ro;
   vec3 rp = p;


   //first hit
   vec3 op = p;
   vec2 dd = rt(ro, rd, op);


   if(dd.x < 0.001)
   {
   if(dd.y == 0.)
   {
   vec4 refl = rfl(p,rd, p);
   vec4 crel = bb(p, refl.xyz, refl.w, ro);
   vec3 n = nn (p);
