#define pi 3.14159265
#define tau pi*2.

//https://www.shadertoy.com/view/ttBXRG
float staircase( in float x, in float k )
{
    float i = floor(x);
    float f = fract(x);

    float a = 0.5*pow(2.0*((f<0.5)?f:1.0-f), k);
    f = (f<0.5)?a:1.0-a;

    return i+f;
}


float FtoS(float f,float time) {
    return sin(f*tau*time);
}

vec2 mainSound( int samp, float time )
{
    float f = 140.;
    float a = FtoS(f,time);
    f += sin(time*0.1);
    a += FtoS(f*0.1,time);
    float ii = 1.;
    f *= 2.;
    for (int i=0;i<60;i++) {
        f += sin(time*0.08*ii)*20.1;
        a *= 1.7;
        a *= 0.475;
        a += FtoS(f+a*0.001,time);
        ii *= 1.5*0.5;
    }
    a = clamp(a*0.003,-1.,1.)/4.;
    f = 60.;
    float bpm = 34.;
    float am = 1.32;
    float s = staircase(time/4.,0.01)+1.;
    //int s = int(time/4.);
    //s = exp(s);
    //s = int(time)/s;
    for (int i=0;i<7;i++) {
        if (i > int(s)) {break;}
        if (i > 1) {
            am *= sin(time*((bpm/4.)/60.))*0.5+0.5;
            //bpm += sin(time*0.1)*f*0.01;
            //am *= 0.5;
        }
        //a = FtoS(f,time);
        ///a = FtoS(f,time)*sin(time*pi*(bpm/60.))*exp(-3.*fract(time*(bpm/60.)));

        a += FtoS(f,time)*exp(-3.*(fract(time*(bpm/60.))))*am;
        //f *= 1.25+sin(time*0.3)*0.5;
        f *= 1.05;
        f = fract(f/400.+time*0.1)*100.;
        f -= fract(time*0.15)*1.;
        //am *= 0.8+sin(time)*0.2;
        bpm *= 2.;
    }
    a /= 8.;
    return vec2(a);
}

