float sdTriPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

vec2 FigurasTriangulos(vec3 p)
{   vec2 res= vec2(9999.0, -1.0);
    p.x= opRep1D(p.x, 30.0);
    p.z= opRep1D(p.z, 30.0);
    float j;
    for(float i=0.0;i<5.0;i++)
    {   float d1= sdTriPrism(p-vec3(0.0*4.0,i,i+0.0*4.0), vec2(8.0,2.0) );
        float d2= sdTriPrism(p-vec3(1.0*4.0,i,i+1.0*4.0), vec2(8.0,2.0) );
        float d3= sdTriPrism(p-vec3(2.0*4.0,i,i+2.0*4.0), vec2(8.0,2.0) );
        res =opU2(res, vec2(d1,100.0));
        res =opU2(res, vec2(d2,100.0));
        res =opU2(res, vec2(d3,100.0));
        j=j+1.0;
    }
  return res;
}   

//https://www.shadertoy.com/view/flfczs....Created by MelisaHot in 2022-03-28
const mat2 m2 = mat2(1.6,-1.2,1.2, 1.6);

float cosNoise( in vec2 p )
{ return 0.4*( sin(p.x) + sin(p.y) );}

float Tierra( in vec3 pos )
{  float h = 0.0;
    vec2 q = pos.xz*0.5;    
    float s = 0.5;
    for( int i=0; i<6; i++ )
    {   h += s*cosNoise( q ); 
        q = m2*q*0.9-iTime; 
        q += vec2(2.,5.);
        s *= 0.5 + 0.2*h;
    }
 h *= 2.0;
 return pos.y - h;
}

vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);  vec3 p0=p;
	float planeDist1 = p.y-1.0+Tierra(p);  //piso inf
    res =opU2(res, vec2(planeDist1,100.0));
    vec2 fig1= FigurasTriangulos(p);
    vec2 fig2= FigurasTriangulos(p-vec3(15.0,-5.0,0.0));
    res =opU2(res, fig1);
    res =opU2(res, fig2);
    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
   for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }
    return object;
}

float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 9999.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
      float hit = GetDist(p + lightPos * dist).x;
      res = min(res, hit / (dist * lightSize));
      dist += hit;
      if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {    
      float hr = 0.01 + float(i) * 0.5 / 4.0;
      float dd = GetDist(nor * hr + pos).x;
      occ += (hr - dd)*sca;
      sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 lightColor, float t) 
{   vec3 lightPos=lp;
    vec3 worldPos = ro + rd * t;
    vec3 V = -rd;
    vec3 N = GetNormal(worldPos);
    vec3 L = normalize (lightPos - worldPos);
    vec3 R = reflect (-L, N);

    float lightDist = max(length(L), .001);
    float atten=1.0 / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    L /= (lightDist*atten);

    float shadow = getSoftShadow(worldPos, normalize(lightPos));// shadows
    float occ = occlusion(worldPos, N);// occ
    vec3 ambient= 0.5*lightColor*clamp(0.5 + 0.5*N.y, 0.0, 1.0)*N.y;
    vec3 fresnel =  lightColor *  pow(clamp(1.0 + dot(rd, N), 0.0, 1.0), 2.0);;
    float diff       = clamp(dot(N, L), 0.0, 1.0);

    diff = pow(diff, 4.) * 2.;
    vec3 diffuse =  lightColor * diff;
    float shininess=8.0;
    float specular    = pow(max(dot(R, V), 0.0), shininess);

    vec3 back = 0.5 * lightColor * clamp(dot(N, -L), 0.0, 1.0); // back
    vec3 colOut = occ*lightColor*(ambient+diffuse*shadow+.35 +back) + vec3(.7,.9,1)*specular*specular;       
    return colOut;
}

vec3 getColorTextura( vec3 p, vec3 nor,  int i)
{	if (i==100 )
    { vec3 col=tex3D(iChannel0, p/32., nor); return col*2.0; }
	if (i==101 ) { return tex3D(iChannel1, p/32., nor); }
}

