precision highp float;

float mag(vec2 z) {
    return length(z);
}

float mag2(vec2 z) {
    return dot(z, z);
}

float arg(vec2 z) {
    return atan(z.y, z.x);
}

vec2 conj(vec2 z) {
    return vec2(z.x, -z.y);
}

vec2 flip(vec2 z) {
    return vec2(-z.x, z.y);
}

vec2 swap(vec2 z) {
    return vec2(z.y, z.x);
}

vec2 rabs(vec2 z) {
    return vec2(abs(z.x), z.y);
}

vec2 iabs(vec2 z) {
    return vec2(z.x, abs(z.y));
}

vec2 cis(float t) {
    return vec2(cos(t), sin(t));
}

vec2 mul(vec2 z, vec2 w) {
    return z.x * w + z.y * vec2(-w.y, w.x);
}

vec2 mulI(vec2 z) {
    return vec2(-z.y, z.x);
}

vec2 div(vec2 z, vec2 w) {
    return (w.x * z + w.y * vec2(z.y, -z.x)) / mag2(w);
}

vec2 rec(vec2 z) {
    return conj(z) / mag2(z);
}

vec2 squ(vec2 z) {
    return vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y);
}

vec2 cube(vec2 z) {
    return z * vec2(z.x * z.x - 3.0 * z.y * z.y, 3.0 * z.x * z.x - z.y * z.y);
}

vec2 power(vec2 z, float p) {
    return pow(mag2(z), p * 0.5) * cis(p * arg(z));
}

vec2 powervec2(vec2 z, vec2 w) {
    float r = mag2(z);
    float argz = arg(z);
    float argzw = w.x * argz + 0.5 * w.y * log(r);
    return pow(r, w.x * 0.5) * exp(-w.y * argz) * cis(argzw);
}

vec2 sqrtp(vec2 z) {
    float r = mag(z);
    return vec2(1.0, sign(z.y)) * sqrt(0.5 * vec2(r + z.x, r - z.x));
}

vec2 powexp(vec2 z) {
    return exp(z.x) * cis(z.y);
}

vec2 ln(vec2 z) {
    return vec2(log(mag2(z)) * 0.5, arg(z));
}

vec2 logb(vec2 z, vec2 b) {
    return div(ln(z), ln(b));
}

vec2 sine(vec2 z) {
    return vec2(sin(z.x) * cosh(z.y), cos(z.x) * sinh(z.y));
}

vec2 cosine(vec2 z) {
    return vec2(cos(z.x) * cosh(z.y), -sin(z.x) * sinh(z.y));
}

vec2 sub_tan(vec2 z) {
    vec2 squ_exp = powexp(mulI(z + z));
    return div(squ_exp - vec2(1.0, 0.0), mulI(squ_exp + vec2(1.0, 0.0)));
}

vec2 tangent(vec2 z) {
    if (z.y < -21.0) {
        return conj(sub_tan(conj(z)));
    } else {
        return sub_tan(z);
    }
}

vec2 sub_cot(vec2 z) {
    vec2 squ_exp = powexp(mulI(z + z));
    return div(mulI(squ_exp + vec2(1.0, 0.0)), squ_exp - vec2(1.0, 0.0));
}

vec2 cot(vec2 z) {
    if (z.y < -21.0) {
        return conj(sub_cot(conj(z)));
    } else {
        return sub_cot(z);
    }
}

vec2 sec(vec2 z) {
    return rec(cosine(z));
}

vec2 csc(vec2 z) {
    return rec(sine(z));
}

vec2 arcsin(vec2 z) {
    return mulI(ln(sqrtp(vec2(1.0, 0) - squ(z)) - mulI(z)));
}

vec2 arccos(vec2 z) {
    return -mulI(ln(mulI(sqrtp(vec2(1.0, 0) - squ(z))) + z));
}

vec2 arctan(vec2 z) {
    return -0.5 * mulI(ln(div(vec2(0.0, 1.0) - z, vec2(0.0, 1.0) + z)));
}

vec2 arccot(vec2 z) {
    return -0.5 * mulI(ln(div(z + vec2(0.0, 1.0), z - vec2(0.0, 1.0))));
}

vec2 arcsec(vec2 z) {
    return arccos(rec(z));
}

vec2 arccsc(vec2 z) {
    return arcsin(rec(z));
}

vec2 sinhyp(vec2 z) {
    return vec2(sinh(z.x) * cos(z.y), cosh(z.x) * sin(z.y));
}

vec2 coshyp(vec2 z) {
    return vec2(cosh(z.x) * cos(z.y), sinh(z.x) * sin(z.y));
}

vec2 sub_tanh(vec2 z) {
    vec2 squ_exp = powexp(z + z);
    return div(squ_exp - vec2(1.0, 0.0), squ_exp + vec2(1.0, 0.0));
}

vec2 tanhyp(vec2 z) {
    if (z.x > 21.0) {
        return flip(sub_tanh(flip(z)));
    } else {
        return sub_tanh(z);
    }
}

vec2 sub_coth(vec2 z) {
    vec2 squ_exp = powexp(z + z);
    return div(squ_exp + vec2(1.0, 0.0), squ_exp - vec2(1.0, 0.0));
}

vec2 coth(vec2 z) {
    if (z.x > 21.0) {
        return flip(sub_coth(flip(z)));
    } else {
        return sub_coth(z);
    }
}

vec2 sech(vec2 z) {
    return rec(coshyp(z));
}

vec2 csch(vec2 z) {
    return rec(sinhyp(z));
}

vec2 arcsinh(vec2 z) {
    return ln(z + sqrtp(squ(z) + vec2(1.0, 0.0)));
}

vec2 arccosh(vec2 z) {
    return ln(z + mul(sqrtp(z + vec2(1.0, 0.0)), sqrtp(z - vec2(1.0, 0.0))));
}

vec2 arctanh(vec2 z) {
    return 0.5 * ln(div(vec2(1.0, 0.0) + z, vec2(1.0, 0.0) - z));
}

vec2 acoth(vec2 z) {
    return 0.5 * ln(div(z + vec2(1.0, 0.0), z - vec2(1.0, 0.0)));
}

vec2 asech(vec2 z) {
    return arccosh(rec(z));
}

vec2 acsch(vec2 z) {
    return arcsinh(rec(z));
}

vec2 linear(vec2 z, vec2 a, vec2 b) {
    return mul(z, a) + b;
}

vec2 quadratic(vec2 z, vec2 a, vec2 b, vec2 c) {
    return mul(squ(z), a) + mul(z, b) + c;
}

vec2 cubic(vec2 z, vec2 a, vec2 b, vec2 c, vec2 d) {
    return mul(cube(z), a) + mul(squ(z), b) + mul(z, c) + d;
}

vec2 linearroot(vec2 z, vec2 a, vec2 b) {
