    for (float i = 0.0; i < max_iter; i += 1.0) {
        if (r > 64.0) {
            break;
        }

        z = power(ln(z), exponent) + c;

        r = mag2(z);
        iter += 1.0;
    }
    //}

    if (iter >= max_iter) {// || !b) {
        fragColor = vec4(vec3(0.0), 1.0);
    } else {
