}

float h21 (vec2 uv, float sc) {
    uv = mod(uv, sc);
    return fract(sin(dot(uv, vec2(12.9898, 78.233)))*43758.5453123);
}

float sBox(vec2 p, vec2 b, float r){
  vec2 d = abs(p) - b + r;
  return min(max(d.x, d.y), 0.) + length(max(d, 0.)) - r;
}

float sdSegment(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

#define ss(a, b) 1. - smoothstep(0., a, b)

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{            
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 ouv = uv;

    const float sc = 8.;

    uv *= sc;
    uv -= vec2(1, 0.125) * iTime;

    vec2 ipos = floor(uv);
    vec2 fpos = uv - ipos - 0.5;

    // cp = edges we use, eps = all edges   
    vec2[4] eps = vec2[4](vec2(-.5, .5), vec2(.5, .5), vec2(.5, -.5), vec2(-.5, -.5));
    vec2[4] cp = eps; 

    float h = h21(ipos, 301.);

    // Threshold + edge bits
    float thr = 0.7;
    vec4 bits;
    for(int i = 0; i < 4; i++) 
        bits[i] = h21(ipos + eps[i], 301.) > thr ? 1. : 0.;

    // Number of edges used
    int iNum = 0;

    // Count iNum + replace 0-iNum entries of cp with edges used
    for(int i = 0; i < 4; i++) 
        if(bits[i] == 1.) cp[iNum++] = eps[i];       

    // Pipes
    float ln = 1e5;

    // Width of stuff
    float lw = 0.15;

    // Flag tiles with two end points on diagonals as curved
    int bend = (iNum==2 && length(cp[0] - cp[1]) < sqrt(2.)) ? 1 : 0;

    // Non-diagonal neighbours (check all 4 sides)
    for(int i = 0; i < 4; i++){
        if(bits[i] > 0.5 && bend == 0) {
            if (h > 0.125)
                ln = min(ln, sdSegment(fpos, vec2(0), eps[i]) - lw);
            else
                ln = min(ln, sdSegment(fpos, 0.5 * eps[i], eps[i]) - lw);
        }
    } 

    // Only 2 diagonal neighbours - 2 variations
    if(bend==1) {
        if (h > 0.125) {
            vec2 pnt = 0.5 * (cp[0] + cp[1]);
            vec2 dif = (fpos - pnt) * Rot(pi/4.);
            ln = min(ln, abs(sBox(dif, sqrt(2.) * vec2(.25), .125)) - lw);  
        } else {
            ln = min(ln, sdSegment(fpos, 0.5 * cp[0], cp[0]) - lw);
            ln = min(ln, sdSegment(fpos, 0.5 * cp[1], cp[1]) - lw);
        }         
    }

    // Overlaps (I think)
    float ln2 = 1e5;
    if (bits[2] == 1.) {
        ln2 = min(ln2, sdSegment(fpos, vec2(1,0), vec2(0,-1)) - lw);
    }

    if (bits[0] == 1.) {
        ln2 = min(ln2, sdSegment(fpos, vec2(-1,0), vec2(0,1)) - lw);
    }

    vec3 col = vec3(0,0,0.05);

    float k = sc / iResolution.y;

    vec3 dcol = vec3(1);
    vec3 lcol = vec3(0,0,0.1);

    // The pipes.
    col = mix(col, dcol, (ss(k, ln - 0.04)));
    col = mix(col, lcol, ss(k, ln));
    col = mix(col, dcol, ss(k, ln + .04));

    // Overlapping outlines
    col = mix(col, lcol, ss(k, ln2));
    col = mix(col, dcol, ss(k, ln2 + .04));

    // i dont know
    vec3 col2 = mix(col, vec3(0.1), ss(k,ln + 0.));
    col = mix(col, col2, 0.5);

    // Yellow from the edges
    float s = exp(-40. * (0.5 - mlength(ouv * vec2(iResolution.y/iResolution.x,1))));
    vec3 e = vec3(0.5);
    col *= pal(s + 1.65, e, e, e, 0.4 * vec3(0,1,2)/3.);

    // Subtle vignette.
    uv = fragCoord/iResolution.xy;
    col *= pow(16.*(1. - uv.x)*(1. - uv.y)*uv.x*uv.y, 1./16.)*1.05;

    // Rough gamma correction before presenting to the screen.
    fragColor = vec4(sqrt(max(col, 0.)), 1);

}

