// segment with disc ends: seamless distance to segment. from https://www.shadertoy.com/view/llySRh
float line(vec2 p, vec2 a,vec2 b, out float h) { 
    p -= a, b -= a;
    h = dot(p, b) / dot(b, b);                          // proj coord on line
    return length(p - b * clamp(h,0.,1.));              // dist to segment
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) /R.y;

    float h, d = line(U, vec2(0,-.4), vec2(0,.6) , h ); // basic line SDF. get d,h
    h /= 1.;                                            // h = normalized height along line
    d -= .3*(1.-.5*h);                                  // make dist decrease with height
    d =   smoothstep( .2, -.2, d )                      // trace the shape
        * (1.-h);                                       // incorporate the gradient
    O = vec4( 1. - d );                                 // image

    if (iMouse.z>0.) O.r = sin(100.*O.r);               // click to see isolines
}

//=======================================================
//= Pre-Filtered Cosine (with a gaussian filter) 
//=======================================================
//== Code       : https://www.shadertoy.com/view/NtlcWf
//== Comparison : https://www.shadertoy.com/view/NlscWf
//== Details    : https://h4w0.frama.io/pages/posts/2022-04-01-FilteredCosine.html
//=======================================================
//== For a box filtered cosine -> see IQ's article :
//== https://iquilezles.org/articles/bandlimiting
//== And in action :  https://www.shadertoy.com/view/WtScDt
//=======================================================
//== Author  : Arthur Cavalier
//=======================================================





//--------------------------------------------------------------------------------------------------------
//-- Main ------------------------------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec2 coords = ((2.*(fragCoord.xy)-iResolution.xy) / iResolution.y);
    vec2 mouse = clamp(iMouse.xy/iResolution.xy, 0., 1.);

    vec3 origin = vec3( 0.5+3.5*cos(6.0*mouse.x), 1.0 + 2.0*mouse.y, -0.5+3.5*sin(6.0*mouse) );
    vec3 target = vec3( 0.0, -0.8, -0.0 );
    vec3 direction = compute_camera_ray_direction(coords, origin, target, 0.0);

    // Screen Ratio
    vec2 screen = fragCoord.xy/iResolution.xy;
    fragColor = vec4(1.);  


    float hit = plane_intersection(origin,direction,vec4(0.,1.,0.,3.));
    if(hit>0.0)
    {
        vec3  hit_position = (origin + hit*direction);

        vec2  x = hit_position.xz;
        vec2  F = m_2_pi * F0 * vec2( cos(T0), sin(T0) ); 
        float harmonic = cosine(x,1.0,F);


        if(screen.x<0.33)
        {
            harmonic = prefiltered_cosine(x,1.0,F);
        }
        else if(screen.x<0.66)
        {
            harmonic = cosine(x,1.0,F);
        }
        else 
        {
            vec4 convergence = texture(iChannel0,screen);
            vec3 result = convergence.rgb/vec3(convergence.a);  
            harmonic = result.x;
        }

        fragColor = vec4(vec3(harmonic),1.); 
	}

    fragColor.rgb *= smoothstep( 0.0, 0.003, abs(screen.x-0.33) );
    fragColor.rgb *= smoothstep( 0.0, 0.003, abs(screen.x-0.66) );
}

