//=======================================================
//= Pre-Filtered Cosine (gaussian filter)
//=======================================================
//== Code       : https://www.shadertoy.com/view/NtlcWf
//== Comparison : https://www.shadertoy.com/view/NlscWf
//== Details    : https://h4w0.frama.io/pages/posts/2022-04-01-FilteredCosine.html
//=======================================================
//== For a box filtered cosine -> see IQ's article :
//== https://iquilezles.org/articles/bandlimiting
//== And in action :  https://www.shadertoy.com/view/WtScDt
//=======================================================
//== Author  : Arthur Cavalier
//=======================================================

#define REMAP_0_1

float cosine(vec2 x, float alpha, vec2 f)
{
    return alpha * cos(dot(x,f));
}

float prefiltered_cosine(vec2 x, float alpha, vec2 f)
{
    mat2  J = 0.5*mat2(dFdx(x),dFdy(x));
    mat2  S = J*transpose(J);
    return alpha * cos(dot(x,f)) * exp(-0.5*dot(f,S*f));
}


//--------------------------------------------------------------------------------------------------------
//-- Main ------------------------------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coords = ((2.*fragCoord.xy-iResolution.xy) / iResolution.y);
    vec2 mouse = clamp(iMouse.xy/iResolution.xy, 0., 1.);

    // IQ - Raymarching Primitives https://www.shadertoy.com/view/Xds3zN Camera controls
    vec3 origin = vec3( 0.5+3.5*cos(0.1*iTime + 6.0*mouse.x), 1.0 + 2.0*mouse.y, -0.5+3.5*sin(0.1*iTime + 6.0*mouse) );
    vec3 target = vec3( 0., -0.4, -0. );
    vec3 direction = compute_camera_ray_direction(coords, origin, target, 0.0);

    // Background Color
    fragColor = vec4(vec3(1.),1.);

    // Harmonic Parameters
    float F0 = 2.0 + sin(0.5*iTime);
    float T0 = 0.0;
    vec2  F = m_2_pi * F0 * vec2( cos(T0), sin(T0) ); 

    float hit = plane_intersection(origin,direction,vec4(0.,1.,0.,20.));
    if(hit>0.0)
    {
        vec3 hit_position = (origin + hit*direction);
        vec2 x = hit_position.xz/5.;
        float harmonic = 0.; 
        if(coords.x<0.)
        {
            harmonic = cosine(x,1.0,F);
        }
        else
        {
            harmonic = prefiltered_cosine(x,1.0,F);
        }

        fragColor.rgb = vec3(harmonic);

        #ifdef REMAP_0_1
        fragColor.rgb = vec3(0.5+0.5*harmonic);
        #endif
    }
    fragColor.rgb *= smoothstep( 0.0, 0.01, abs(coords.x) );
}

