//=======================================================
//= Normal Distribution Function : GGX Anisotropic 
//=======================================================
//== Slope distributions :
//== *- Beckmann Isotropic 	 : https://www.shadertoy.com/view/WtGXRt
//== *- Beckmann Anisotropic : https://www.shadertoy.com/view/WlGXRt
//== *- GGX Isotropic 		 : https://www.shadertoy.com/view/WlKXDm
//== *- GGX Anisotropic 	 : https://www.shadertoy.com/view/3tyXRt
//== In-Engine ready NDFs:
//== *- GGX Isotropic 	     : https://www.shadertoy.com/view/flsyWX
//== *- GGX Anisotropic 	 : https://www.shadertoy.com/view/NtlyWX
//== *- Beckmann Isotropic   : https://www.shadertoy.com/view/fllcWX
//== *- Beckmann Anisotropic : https://www.shadertoy.com/view/ftsyWX
//== Related stuff:
//== *- Student-t Aniso NDF  : https://www.shadertoy.com/view/sdjXWw
//== *- Specular AA          : https://www.shadertoy.com/view/WssyR7
//== *- LEADR mapping        : https://www.shadertoy.com/view/WdXfRn
//=======================================================
//== Shader references:
//== *- @nimitz's "Cheap orthonormal basis" on Shadertoy
//==	https://www.shadertoy.com/view/4sSSW3
//== *- @iq's intersectors ( Sphere )
//== 	https://iquilezles.org/articles/intersectors
//== *- UI courtesy of Good's https://www.shadertoy.com/view/XlG3WD
//=======================================================
//== Arthur Cavalier
//=======================================================

//-----------------------------------------------------------------------------
//-- Constants ----------------------------------------------------------------
const vec4  UNIT_SPHERE     = vec4(0.,0.,0.,1.);
const float LIGHT_INTENSITY = 2.;
const vec3  LIGHT_DIRECTION = normalize(vec3(-0.5,1.5,0.1));
const vec3  BACKGROUND_COL  = vec3(0.0);


//-----------------------------------------------------------------------------
//-- UI Functions -------------------------------------------------------------
/* Access */
float readFloat(float address) { return texture(iChannel0, (floor(vec2(address, 1))+0.5) / iChannelResolution[0].xy).r; }
/* Sliders */
float slider_roughness() { return(readFloat(1.)); }
float slider_metalness()  { return(readFloat(2.) > 0.5 ? 1. : 0.); }
float slicer_reflectance(){ return(clamp(readFloat(3.),0.35,1.0)); }

//-----------------------------------------------------------------------------
//-- Beckmann -----------------------------------------------------------------

float beckmann_isotropic_ndf(float alpha, float NoH)
{
	float cosThetaSqr = NoH*NoH;
	float alphaSqr = alpha*alpha;
	float exponent = (1.0-cosThetaSqr)/(alphaSqr*cosThetaSqr);
	float denom = m_pi * alphaSqr * cosThetaSqr * cosThetaSqr;
	return( exp(-exponent) / denom );
}

float beckmann_isotropic_lambda(float alpha, float NoV)
{
    float lambda = 0.f;
    float cosThetaSqr = NoV*NoV;
    float sinThetaSqr = 1.f-cosThetaSqr;
    float tanThetaSqr = sinThetaSqr / cosThetaSqr;

	float nu = 	1.f / sqrt( alpha * sqrt(tanThetaSqr) );
	if(nu < 1.6f){
        lambda = (1.f - 1.259f*nu + 0.396f*nu*nu) / (3.535f*nu + 2.181f*nu*nu); 
	}
	return(lambda);
}

float beckmann_isotropic_visibility(float alpha, float NoV, float NoL)
{
    float lambda_wo = beckmann_isotropic_lambda(alpha,NoV); 
	float lambda_wi = beckmann_isotropic_lambda(alpha,NoL); 
	float denom = (1.f + lambda_wo + lambda_wi)*NoL*NoV*4.f;
	return( 1.f / denom );
}


//-----------------------------------------------------------------------------
//-- Fresnel ------------------------------------------------------------------
vec3 fresnel_schlick(in float cosTheta, in vec3 F0)
{
    return F0 + (1. - F0) * pow(1. - cosTheta, 5.);
}


//-----------------------------------------------------------------------------
//-- World Functions ----------------------------------------------------------
float 	get_light_intensity()            { return LIGHT_INTENSITY; }
vec3 	get_light_direction()            { return LIGHT_DIRECTION; }
vec3 	get_background_color(in vec3 rd) { return BACKGROUND_COL; }


//-----------------------------------------------------------------------------
//-- Render Functions -----------------------------------------------------------
vec3 render(in vec3 ro, in vec3 rd)
{



    vec3  color         = get_background_color(rd);
    float sphere_hit    = sphere_get_hit(ro,rd,UNIT_SPHERE);
    if(sphere_hit > 0.0) 
    {
        /* Retrieve Scene data */
        vec3    P = ro + sphere_hit * rd;
        vec3    L = get_light_direction();
        vec3    N = sphere_get_normal(P,UNIT_SPHERE);
        vec3    V = -rd;

        /* Material Data */

        /* 1. Perceptual Roughness Mapping \f$ \alpha^2 \f$  */
        float alpha = slider_roughness() * m_sqrt_2;
        alpha *= alpha;
        /* 3. Gold F0 */ 
        vec3 albedo = vec3(1.00, 0.85, 0.57); /* sRGB */
        vec3 linear_albedo =  pow(albedo,vec3(2.2));
        /* 4. Metalness */ 
        float metalness = slider_metalness();
        /* 5. Reflectance */ 
        float reflectance = slicer_reflectance();

        /* Computing HalfVector */
        vec3 H = normalize(L+V);

        /* Computing Dots */
        /* 1. Distribution dot Terms */
        float NoH = dot(N,H);
        /* 2. Visibility dot Terms */
        float NoV = dot(N,V);
        float NoL = dot(N,L);

        /* Computing Normal Distribution Term \f$ D(\omega_h) \f$ */
        float D   = beckmann_isotropic_ndf( alpha, NoH );

        /* Computing Visibility Term */
        /* \f$ \frac{G(\omega_o,\omega_i,\alpha_t,\alpha_b)}{4(\omega_g\cdot\omega_i)(\omega_g\cdot\omega_o)} \f$ */
        float Vis = beckmann_isotropic_visibility(alpha, NoV, NoL); 

        /* Computing Fresnel Term */
        /* Using reflectance mapping as in Filament PBR Pipeline */
        vec3 F0 = 0.16*reflectance*reflectance*(1.0-metalness) + linear_albedo*metalness;
        vec3 F = fresnel_schlick(max(dot(V,H), 0.0), F0);

        /* Lighting */
        vec3 specular_microfacet = D * Vis * F;
        vec3 diffuse_lambert = m_i_pi*(1.-metalness)*linear_albedo;
        vec3 diffuse_factor  = vec3(1.)-F; 

        /* Final color */
        color = max(NoL,0.) * get_light_intensity() * ( diffuse_factor*diffuse_lambert + specular_microfacet);
    }
    return color;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screen_coords = ( 2.*gl_FragCoord.xy - iResolution.xy ) / iResolution.y;
    vec3 ro = vec3( 3. * cos(iTime), 0.5 , 3. * sin(iTime) );
    vec3 rd = compute_camera_ray_direction(screen_coords, ro, vec3(0.), 0.);
    fragColor = vec4( render(ro,rd) , 1.);


    // UI
    vec4 ui = texture(iChannel0, fragCoord/iResolution.xy);
    fragColor = mix(fragColor, ui, ui.a);

    // Tonemapping
    fragColor.rgb = fragColor.rgb / (fragColor.rgb + vec3(1.0)); //-- Reinhard Tonemapping
    fragColor.rgb = pow(fragColor.rgb,vec3(0.4545));			 //-- Gamma Correction
}

