//-----------------------------------------------------------------------------
//-- Constants ----------------------------------------------------------------
const float m_pi        = 3.14159265359f; /* MathConstant: PI        */
const float m_2_pi      = 6.28318530718f; /* MathConstant: 2 PI      */
const float m_i_pi      = 0.31830988618f; /* MathConstant: 1 / PI    */
const float m_sqrt_2    = 1.41421356237f; /* MathConstant: sqrt(2)   */
const float m_i_sqrt_2  = 0.70710678119f; /* MathConstant: 1/sqrt(2) */
const float m_eps_3f    = 0.001f;            
const float m_eps_4f    = 0.0001f;    

//-----------------------------------------------------------------------------
//-- Ray ----------------------------------------------------------------------
vec3 compute_camera_ray_direction(in vec2 screen_coordinates, in vec3 origin, in vec3 target, float cr )
{
	vec3 cw = normalize(target-origin);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw )*normalize(vec3(screen_coordinates,2.0));
}

//-----------------------------------------------------------------------------
//-- Sphere Intersection Functions --------------------------------------------
float sphere_get_hit(in vec3 ro, in vec3 rd, vec4 sph )
{
    vec3 oc = ro - sph.xyz;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - sph.w*sph.w;
    float h = b*b - c;
    if( h<0.0 ) return(-1.0);
    h = sqrt( h );
    return min( -b-h, -b+h );
}

vec3 sphere_get_normal( in vec3 pos, in vec4 sph )
{
    return normalize(pos-sph.xyz);
}

//-----------------------------------------------------------------------------
//-- Orthonormal Basis Function -----------------------------------------------
//-- @nimitz's "Cheap orthonormal basis" on Shadertoy
//-- https://www.shadertoy.com/view/4sSSW3
mat3 orthonormal_basis(in vec3 n, out vec3 u, out vec3 v)
{
    vec3 f,r;
    if(n.z < -0.999999)
    {
        u = vec3(0 , -1, 0);
        v = vec3(-1, 0, 0);
    }
    else
    {
        float a = 1./(1. + n.z);
        float b = -n.x*n.y*a;
        u = normalize(vec3(1. - n.x*n.x*a, b, -n.x));
        v = normalize(vec3(b, 1. - n.y*n.y*a , -n.y));
    }
    return( mat3(u,v,n) );
}

