//=======================================================
//= Normal Distribution Function : GGX Anisotropic 
//=======================================================
//== Slope distributions :
//== *- Beckmann Isotropic 	 : https://www.shadertoy.com/view/WtGXRt
//== *- Beckmann Anisotropic : https://www.shadertoy.com/view/WlGXRt
//== *- GGX Isotropic 		 : https://www.shadertoy.com/view/WlKXDm
//== *- GGX Anisotropic 	 : https://www.shadertoy.com/view/3tyXRt
//== Engine ready NDFs:
//== *- GGX Isotropic 	     : https://www.shadertoy.com/view/flsyWX
//== *- GGX Anisotropic 	 : https://www.shadertoy.com/view/NtlyWX
//== *- Beckmann Isotropic   : https://www.shadertoy.com/view/fllcWX
//== *- Beckmann Anisotropic : https://www.shadertoy.com/view/ftsyWX
//== Related stuff:
//== *- Student-t Aniso NDF  : https://www.shadertoy.com/view/sdjXWw
//== *- Specular AA          : https://www.shadertoy.com/view/WssyR7
//== *- LEADR mapping        : https://www.shadertoy.com/view/WdXfRn
//=======================================================
//== Shader references:
//== *- @nimitz's "Cheap orthonormal basis" on Shadertoy
//==	https://www.shadertoy.com/view/4sSSW3
//== *- @iq's intersectors ( Sphere )
//== 	https://iquilezles.org/articles/intersectors
//== *- UI courtesy of Good's https://www.shadertoy.com/view/XlG3WD
//=======================================================
//== Arthur Cavalier
//=======================================================

//-----------------------------------------------------------------------------
//-- Constants ----------------------------------------------------------------
const vec4  UNIT_SPHERE     = vec4(0.,0.,0.,1.);
const float LIGHT_INTENSITY = 2.;
const vec3  LIGHT_DIRECTION = normalize(vec3(-0.5,1.5,0.1));
const vec3  BACKGROUND_COL  = vec3(0.0);


//-----------------------------------------------------------------------------
//-- UI Functions -------------------------------------------------------------
/* Access */
float readFloat(float address) { return texture(iChannel0, (floor(vec2(address, 1))+0.5) / iChannelResolution[0].xy).r; }
/* Sliders */
float slider_anisotropy() { return(readFloat(1.)); }
float slider_roughness()  { return(readFloat(2.)); }
float slider_metalness()  { return(readFloat(3.) > 0.5 ? 1. : 0.); }
float slicer_reflectance(){ return(clamp(readFloat(4.),0.35,1.0)); }


//-----------------------------------------------------------------------------
//-- GGX ----------------------------------------------------------------------

/** 
 * @brief GGX Anisotropic Distribution
 * @param alpha_t   Roughness in   tangent direction
 * @param alpha_b   Roughness in bitangent direction
 * @param ToH       Dot product between   tangent vector and halfvector
 * @param BoH       Dot product between bitangent vector and halfvector
 * @param NoH       Dot product between    normal vector and halfvector
 * 
 * \f$ D(h;\alpha_t,\alpha_b) = \frac{1}{\pi\alpha_t\alpha_b}\frac{1}{ \big( (n\cdot h)^2 + \frac{(t \cdot h)^2}{\alpha_t^2} + \frac{(b\cdot h)^2}{\alpha_b^2} \big)^2 } \f$
 */
float ggx_anisotropic_ndf(float alpha_t, float alpha_b,
                          float ToH, float BoH, float NoH) 
{
	vec3 v = vec3(alpha_b*ToH,alpha_t*BoH, alpha_t*alpha_b*NoH);
	float v2 = dot(v,v);
	float a2 = alpha_t*alpha_b;
	float w2 = a2/v2;
	return( a2*w2*w2*m_i_pi );
}


/** 
 * @brief GGX Anisotropic Visibility Term
 * @param alpha_t   Roughness in   tangent direction
 * @param alpha_b   Roughness in bitangent direction
 * @param ToV       Dot product between   tangent vector and view vector
 * @param BoV       Dot product between bitangent vector and view vector
 * @param NoV       Dot product between    normal vector and view vector
 * @param ToL       Dot product between   tangent vector and light vector
 * @param BoL       Dot product between bitangent vector and light vector
 * @param NoL       Dot product between    normal vector and light vector
 * 
 * Visibility term correspond to the following relation :
 * \f$ V(\omega_o,\omega_i,\alpha_t,\alpha_b) = \frac{G(\omega_o,\omega_i,\alpha_t,\alpha_b)}{4(\omega_g\cdot\omega_i)(\omega_g\cdot\omega_o)} \f$
 * Yielding :
 * \f$ V(\omega_i,\omega_o,\alpha_t,\alpha_b)=\frac{1}{2}\frac{1}{((\omega_g\cdot\omega_i)\hat{\Lambda}(\omega_o)+(\omega_g\cdot\omega_o)\hat{\Lambda}(\omega_i))} \f$
 * Using \f$\hat{\Lambda}(\omega) &= \sqrt{\alpha^2_t(\omega_t\cdot\omega)^2+\alpha^2_b(\omega_b\cdot\omega)^2+(\omega_g\cdot\omega)^2} \f$
 * 
 */
float ggx_anisotropic_visibility(float alpha_t, float alpha_b,
								 float ToV, float BoV, float NoV,
    							 float ToL, float BoL, float NoL) 
{  
    float lambdaV = NoL * length(vec3(alpha_t * ToV, alpha_b * BoV, NoV));
    float lambdaL = NoV * length(vec3(alpha_t * ToL, alpha_b * BoL, NoL));
    float v = 0.5f / (lambdaV + lambdaL);
    return clamp(v,0.0f,1.0f);
}


//-----------------------------------------------------------------------------
//-- Fresnel ------------------------------------------------------------------
vec3 fresnel_schlick(in float cosTheta, in vec3 F0)
{
    return F0 + (1. - F0) * pow(1. - cosTheta, 5.);
}


//-----------------------------------------------------------------------------
//-- World Functions ----------------------------------------------------------
float 	get_light_intensity()            { return LIGHT_INTENSITY; }
vec3 	get_light_direction()            { return LIGHT_DIRECTION; }
vec3 	get_background_color(in vec3 rd) { return BACKGROUND_COL; }


//-----------------------------------------------------------------------------
//-- Render Functions -----------------------------------------------------------
vec3 render(in vec3 ro, in vec3 rd)
{



    vec3  color         = get_background_color(rd);
    float sphere_hit    = sphere_get_hit(ro,rd,UNIT_SPHERE);
    if(sphere_hit > 0.0) 
    {
        /* Retrieve Scene data */
        vec3    P = ro + sphere_hit * rd;
        vec3    L = get_light_direction();
        vec3    N = sphere_get_normal(P,UNIT_SPHERE);
        vec3    V = -rd;

        /* Material Data */
        /* 1. Remapped between [-1;1] : negative -> bitangent, positive -> tangent */
        float anisotropy = slider_anisotropy()*2.-1.;
        /* 2. Perceptual Roughness Mapping \f$ \alpha^2 \f$  */
        float alpha = slider_roughness();
        float alpha_x = max(alpha * alpha * (1.0 + anisotropy), 0.001);
        float alpha_y = max(alpha * alpha * (1.0 - anisotropy), 0.001);
        /* 3. Gold F0 */ 
        vec3 albedo = vec3(1.00, 0.85, 0.57); /* sRGB */
        vec3 linear_albedo =  pow(albedo,vec3(2.2));
        /* 4. Metalness */ 
        float metalness = slider_metalness();
        /* 5. Reflectance */ 
        float reflectance = slicer_reflectance();

        /* Computing Tangent/Bitangent */
        vec3 T,B;
        orthonormal_basis(N, T, B);

        /* Computing HalfVector */
        vec3 H = normalize(L+V);

        /* Computing Dots */
        /* 1. Distribution dot Terms */
        float ToH = dot(T,H);
        float BoH = dot(B,H);
        float NoH = dot(N,H);
        /* 2. Visibility dot Terms */
        float ToV = dot(T,V);
        float BoV = dot(B,V);
        float NoV = dot(N,V);
        float ToL = dot(T,L);
        float BoL = dot(B,L);
        float NoL = dot(N,L);

        /* Computing Normal Distribution Term \f$ D(\omega_h) \f$ */
        float D   = ggx_anisotropic_ndf( alpha_x, alpha_y, ToH, BoH, NoH );

        /* Computing Visibility Term */
        /* \f$ \frac{G(\omega_o,\omega_i,\alpha_t,\alpha_b)}{4(\omega_g\cdot\omega_i)(\omega_g\cdot\omega_o)} \f$ */
        float Vis = ggx_anisotropic_visibility(
            alpha_x, alpha_y,
            ToV, BoV, NoV,
            ToL, BoL, NoL
        ); 

        /* Computing Fresnel Term */
        /* Using reflectance mapping as in Filament PBR Pipeline */
        vec3 F0 = 0.16*reflectance*reflectance*(1.0-metalness) + linear_albedo*metalness;
        vec3 F = fresnel_schlick(max(dot(V,H), 0.0), F0);

        /* Lighting */
        vec3 specular_microfacet = D * Vis * F;
        vec3 diffuse_lambert = m_i_pi*(1.-metalness)*linear_albedo;
        vec3 diffuse_factor  = vec3(1.)-F; 

        /* Final color */
        color = max(NoL,0.) * get_light_intensity() * ( diffuse_factor*diffuse_lambert + specular_microfacet);
    }
    return color;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screen_coords = ( 2.*gl_FragCoord.xy - iResolution.xy ) / iResolution.y;
    vec3 ro = vec3( 3. * cos(iTime), 0.5 , 3. * sin(iTime) );
    vec3 rd = compute_camera_ray_direction(screen_coords, ro, vec3(0.), 0.);
    fragColor = vec4( render(ro,rd) , 1.);


    // UI
    vec4 ui = texture(iChannel0, fragCoord/iResolution.xy);
    fragColor = mix(fragColor, ui, ui.a);

    // Tonemapping
    fragColor.rgb = fragColor.rgb / (fragColor.rgb + vec3(1.0)); //-- Reinhard Tonemapping
    fragColor.rgb = pow(fragColor.rgb,vec3(0.4545));			 //-- Gamma Correction
}

