//Day 3 
//Daily Shader Challenge
//not my intended goal for this shader, but it looked so cool i had to add it in.
//the spiral is actually a mistake with a triangle, but it ended up like this instead with some floor() tomfoolery
//my goal is to come back to this and maybe add some blur to see if i can get those artifacts out
//lopea (https://github.com/lopea)

float tr(vec2 pt)
{

    const float k = sqrt(3.0);
    pt.x = abs(pt.x) - 1.0;
    pt.y = pt.y + 1.0/k;
    if( pt.x+k*pt.y>0.0 ) 
        pt=vec2(pt.x-k*pt.y,-k*pt.x-pt.y)/2.0;
    pt.x -= clamp( pt.x, -2.0, 0.0 );
    return -length(pt)*sign(pt.y);
}

float three(vec2 p)
{
    p.y -= 0.26;
    p.x += 0.12;
    float norm = min(length(p) - 0.5, length(p- vec2(0, -0.6)) - 0.5);
    p-= vec2(-.5, 0);
    float diff = min(length(p) - 0.5, length(p- vec2(0, -0.6)) - 0.5);
    return max(-max(-diff, norm), max(-diff, norm));
}


float ss(vec3 p)
{

     //turn this one on and it will look cool as hell
    float ang = iTime + p.z * 6.;

   // float ang = iTime + floor(p.z * 2.25 - .352622);

    p.xy *= mat2(cos(ang),-sin(ang), sin(ang),cos(ang));
    p.z = mod(p.z +3., 6.) -3.;
    float t = max(-tr(p.xy), tr(p.xy/1.5) *1.5);

    vec2 w = vec2(t, abs(p.z) -.25);
    float res = min(max(w.x, w.y),0.) + length(max(w,.0));

    return res -0.001;
}
vec2 rt(vec3 ro, vec3 rd, inout vec3 pp)
{
    float ll = 0., dd = 0., cc = 100.;

    for(int i = 0; i < 255;i ++)
    {
        pp = ro + rd * ll;
        dd = ss(pp);
        ll += dd;
        cc = min(dd, cc);
        if(dd < 0.01 || ll > 300.)
        break;
    }

    return vec2( dd, cc);
}

vec3 nn (vec3 p)
{
    vec3 n = vec3 (0);

    for(int i = min(iFrame, 0); i < 4; i++)
    {
        vec3 e = 0.5773* (2. * vec3((((i+3)>>1)&1), ((i>>1)&1),(i&1)) -1.);
        n += e* ss(p +e *0.001);
    }
    return normalize(n);
}
vec4 gc(vec3 ro, vec3 rd)
{
    vec3 p = vec3(ro);
    vec2 dc = rt(ro,rd, p);
    vec3 n = nn(p);
    vec3 col = vec3(0.8, 0.1, 1.);

    if(dc.x <0.01)
    {
        return vec4(max((n.b) * col, 0.),1.);
    }

    return vec4((1. + 0.23 * dc.y * dc.y) * col, 1.);
}


void mainImage(out vec4 c, in vec2 f)
{
    vec2 r = iResolution.xy, u = (2. *f  -r)/r.y;

    vec3 ro = vec3(0,0,-33.33/3. + iTime * 6.), rd = vec3(normalize(vec3(u,3. * 10.)));


    c = max(gc(ro,rd) * (length(u) -0.5), 0.) + step(three((u + texture(iChannel0, vec2(f/r + iTime * .01)).xy * 0.015 )/0.5) * 0.5,0.01) ;
}

////////////////////////////////////////////////////////////////////////////////
//
// A Cubic, Osculated by Parabolas.
// Inspired by Dan Bach: https://twitter.com/dansmath/status/1508205090561159172
//
// Matthew Arcus, mla, 2022.
//
// A cubic curve y = x³+kx has a family of osculating
// parabolas (osculating at x = t):
//
// y = 3tx² + (k-3t²)x + t³
//
// Regarding this as a cubic equation in t, for given x,y:
//
// t³ - 3xt² + 3x²t + kx - y = 0
// ∴ (t-x)³ + x³ + kx - y = 0
// ∴ t = x+∛(y - x³ - kx) is single real root
//
// So exactly 1 parabola passes through each point in the plane.
//
////////////////////////////////////////////////////////////////////////////////

float k = -1.0; // Cubic parameter
float N = 20.0; // Parabola spacing

float lwidth = 0.002;
float scale = 1.0;

vec3 map(vec2 p) {
  return vec3(scale*(2.0*p - iResolution.xy) / iResolution.y, 1);
}

float cuberoot(float x) {
  float s = sign(x);
  x = pow(abs(x),1.0/3.0);
  return s*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec3 p = map(fragCoord.xy);
  float px = fwidth(p.x);
  if (iMouse.x != 0.0) {
    vec2 m = iMouse.xy/iResolution.xy;
    k = 5.0*(m.x-0.5);
    N = 40.0*m.y;
  }
  vec3 color = vec3(0);
  float x = p.x, y = p.y;
  float x2 = x*x, x3 = x*x2;
  // Cubic curve is x³+kx = 0
  // Find osculating parabola at (x,y)
  float t0 = x+cuberoot(y-x*(x2+k));
  float t = t0;
  t *= N;
  t -= iTime;
  t = round(t);
  t += iTime;
  t /= N;
  float t2 = t*t, t3 = t*t2;
  float d = 3.0*t*x2 + (k-3.0*t2)*x + t3 - y;
  float dx = 6.0*t*x+(k-3.0*t2);
  d /= sqrt(dx*dx+1.0); // dy = 1
  vec3 lcol = h2rgb(atan(t0)/PI);
  color = mix(lcol,color,smoothstep(0.0,px,abs(d)-lwidth));
  if (!key(CHAR_X)) {
    d = x3 + k*x - y;
    dx = 3.0*x2+k;
    d /= sqrt(dx*dx+1.0);
    color = mix(vec3(1),color,smoothstep(0.0,max(px,3.0*lwidth),abs(d)));
  }
  color = pow(color,vec3(0.4545));
  fragColor = vec4(color,1);
}

