// Plento (Cole Peterson)

#define ss(a, b, t) smoothstep(a, b, t)
#define m vec2(R.x/R.y*(iMouse.x/R.x-.5),iMouse.y/R.y-.5)
#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))
#define R iResolution.xy

vec2 hash22( vec2 x ){
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float gdns(vec2 p ){
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = smoothstep(0., 1., f);

    vec2 a = hash22(i);
    vec2 b = hash22( i + vec2(1.,0.));
    vec2 c = hash22( i + vec2(.0,1.));
    vec2 d = hash22( i + vec2(1));

    float nse = mix( mix( dot( a, f - vec2(0.0,0.0) ), 
                     dot( b, f - vec2(1.0,0.0) ), u.x),
                mix( dot( c, f - vec2(0.0,1.0) ), 
                     dot( d, f - vec2(1) ), u.x), u.y);
    return nse + 0.5;
}

float octnse(vec2 p, int oct){
    float a = 1.;
    float n = 0.;

    for(int i = 0; i < oct; i++){
     	n += gdns(p) * a;	
        p*=2.;
        a *= .5;
    }

    return n;
}

void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;
    float y = uv.y;
    uv*=3.5;
    uv *= rot(-iTime*.1);

    vec3 col = vec3(0);

    float scale = 1., N = 25.;
    for(float i = 0.; i < N; i++){
        vec2 p = uv * scale;

        p *= rot(i*.01);
        p += iTime*.52;

        float ns = octnse(p*0.4, 4);
        float h = 0.5 + (i / N) * 0.75;
        float ns2 = ss(h, h + .04 , ns);

        vec3 nc = mix(vec3(0), .5 + .4*cos(vec3(7., 6., 2.)*i*0.08 - iTime*0.4+2.8), ns2);
        float fog = max(ss(0.0, 0.9, i / N), 0.02);

        if(i == N - 1.)  nc -= .4*vec3(ss(.3, 4.3, ns*ns*ns));

        col = mix(col, nc*fog, ns2);

        scale += 0.03;
    }

    col *= step(abs(y), .44);

    col = 1.-exp(-col);
    f = vec4(col, 1.0);

}

