float fn(float x, float p) {
    float f = fract(exp2(p) * x + 0.25) - 0.5;
    return step(f, 0.) / exp2(p);
}

float fn2(float x) {
    float f = 0.;
    float n = 2.; //>2. works fine
    for (float i = 1.; i < 2. * n + 1.; i+=2.) {
        float f1 = fn(x, i);
        float f2 = fn(x, i + 1.);
        f = abs(f - abs(f1 - f2));
    }
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 ouv = uv;
    uv.y *= 2.4;
    uv.x -= 0.05 * iTime;
    uv.y += 0.3 * cos(6. * floor(4. * uv.x) + 0.5 * iTime);

    float y = uv.y;

    float k = 4. / iResolution.y;

    float sgn = 0.125 * sign(uv.y);
    float a = 0.5 + (0.25) * cos(floor(16. * uv.x) * 3.14159 /3. + iTime);
    float s = 0.5 * smoothstep(-k, k, -uv.y -a + fn2(uv.x + 0.25));
    float s2 = 0.5 * smoothstep(-k, k, uv.y-a + fn2(uv.x));

    float c = 0.6 - 1.5* abs(s-s2);
    c = abs(c - exp(-0.4 - 5. * ouv.y - ouv.x));
    c = clamp(c, 0., 1.2); //oversaturated but its ok
    vec3 col = vec3(0.95,0.42,0.15);
    col = mix(col, 1. - col, 0.5 + 0.5 * cos(-1. * ouv.y + 0.2 * ouv.x + iTime));
    col *= c;

    fragColor = vec4(col,1.0);
}
