const float max_dist = 10.0;
const float other_side_bright = 0.25;

#define ENABLE_TORUS_OUTLINE 1 // set to 0 to disable outline

float to_toroid_sdf(in vec3 pt) {
  float to_toroid_spine = length(pt.xz) - 0.75;
  float to_toroid = length(vec2(to_toroid_spine, pt.y)) - 0.25;
  return to_toroid;
}

float sdf(in vec3 pt) {
  float to_toroid_spine = length(pt.xz) - 0.75;
  float to_toroid = length(vec2(to_toroid_spine, pt.y)) - 0.25;
  float angle = atan(pt.x, pt.z);
  vec2 p1 = vec2(sin(0.5 * angle), cos(0.5 *angle));
  vec2 p2 = vec2(cos(0.5 * angle), -sin(0.5 *angle));
  float d1 = dot(vec2(to_toroid_spine, pt.y), p1);
  float d2 = dot(vec2(to_toroid_spine, pt.y), p2);
  float max_angle_dist = min(max(d1, d2), max(-d1, -d2));
  return max(to_toroid, max_angle_dist);
}

float sdf_other_side(in vec3 pt) {
  float to_toroid_spine = length(pt.xz) - 0.75;
  float to_toroid = length(vec2(to_toroid_spine, pt.y)) - 0.25;
  float angle = atan(pt.x, pt.z);
  vec2 p1 = vec2(sin(0.5 * angle), cos(0.5 *angle));
  vec2 p2 = vec2(cos(0.5 * angle), -sin(0.5 *angle));
  float d1 = dot(vec2(to_toroid_spine, pt.y), p1);
  float d2 = dot(vec2(to_toroid_spine, pt.y), p2);
  float max_angle_dist = min(max(-d1, d2), max(d1, -d2));
  return max(to_toroid, max_angle_dist);
}

float rough_raycast(in vec3 orig, in vec3 dir) {
  float accum = 0.0;
  float d = to_toroid_sdf(orig);
  for (int i = 0; i < 256; ++i) {
    accum = accum + 0.9 * d;
    vec3 pt = orig + accum * dir;
    d = to_toroid_sdf(pt);
    if (abs(d) < 1.0e-2) {
      return accum;
    }
    if (d > max_dist) {
      return max_dist + 1.0;
    }
  }
  return accum;
}


float raycast(in vec3 orig, in vec3 dir, float start_guess) {
  float accum = start_guess;
  if (accum > max_dist) {
    return accum;
  }
  float d = sdf(orig + accum * dir);
  for (int i = 0; i < 256; ++i) {
    accum = accum + 0.7 * d;
    vec3 pt = orig + accum * dir;
    d = sdf(pt);
    if (abs(d) < 1.0e-6) {
      return accum;
    }
    if (accum > max_dist) {
      return max_dist + 1.0;
    }
  }
  return accum;
}

float raycast_other_side(in vec3 orig, in vec3 dir, float start_guess) {
  float accum = start_guess;
  if (accum > max_dist) {
    return accum;
  }
  float d = sdf_other_side(orig + dir * accum);
  for (int i = 0; i < 256; ++i) {
    accum = accum + 0.7 * d;
    vec3 pt = orig + accum * dir;
    d = sdf_other_side(pt);
    if (d < 1.0e-6) {
      return accum;
    }
    if (d > max_dist) {
      return max_dist + 1.0;
    }
  }
  return accum;
}


vec3 get_norm(in vec3 pt) {
  float f = sdf(pt);
  const float h = 1.0e-3;
  return normalize(vec3(sdf(pt + vec3(h, 0.0, 0.0)) - f,
                        sdf(pt + vec3(0.0, h, 0.0)) - f,
                        sdf(pt + vec3(0.0, 0.0, h)) - f));
}

vec3 get_norm_other_side(in vec3 pt) {
  float f = sdf_other_side(pt);
  const float h = 1.0e-3;
  return normalize(vec3(sdf_other_side(pt + vec3(h, 0.0, 0.0)) - f,
                        sdf_other_side(pt + vec3(0.0, h, 0.0)) - f,
                        sdf_other_side(pt + vec3(0.0, 0.0, h)) - f));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float uv_scale = min(iResolution.x, iResolution.y);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / uv_scale;

    vec3 orig = vec3(0.0, 0.0, -3.0);
    vec3 ray = normalize(vec3(uv, 0) - orig);

    float theta_tilt = 2.0 * iMouse.y / iResolution.y - 1.0;
    float ct = cos(theta_tilt);
    float st = sin(theta_tilt);
    mat3 tilt = mat3(1.0, 0.0, 0.0,
                       0.0, ct, -st,
                       0.0, st, ct);

    float phi_yaw = 8.0 * (2.0 * iMouse.x / iResolution.x - 1.0);
    float cp = cos(phi_yaw);
    float sp = sin(phi_yaw);
    mat3 yaw = mat3(cp, 0.0, -sp,
                    0.0, 1.0, 0.0,
                    sp, 0.0, cp);
    orig = yaw * tilt * orig;
    ray = yaw * tilt * ray;

    float bright = 0.95;

    vec3 overlay = vec3(0.5);

    vec3 col = vec3(0.0);
    vec3 light = -vec3(0.8 * ray.x + 0.6 * ray.z, ray.y, 0.8 * ray.z - 0.6 * ray.x);

    float start_guess = rough_raycast(orig, ray);

    float dist = raycast(orig, ray, start_guess);
    if (dist < max_dist) {
      overlay = vec3(0.0);
      vec3 n = get_norm(orig + dist * ray);
      // pixel color
      vec3 bounce = normalize(reflect(ray, n));
      col = 0.5 * bounce + 0.5;
      float bounce_dot = max(0.0, dot(bounce, light));
      bounce_dot = bounce_dot * bounce_dot;
      bounce_dot = bounce_dot * bounce_dot;
      col += 0.5 * bounce_dot;
      bright = 1.0;
    }

#if ENABLE_TORUS_OUTLINE
    float dist_other_side = raycast_other_side(orig, ray, start_guess);

    if (dist_other_side < max_dist && dist_other_side < dist) {
      if (dist >= max_dist) {
        overlay = vec3(0.0);
      }
      bright = 1.0 - other_side_bright;
      vec3 hit_pt = orig + dist_other_side * ray;
      vec3 n = get_norm_other_side(hit_pt);
      vec3 bounce = normalize(reflect(ray, n));
      float stripe = mod(atan(hit_pt.x, hit_pt.z)/3.141592654, 0.1);
      overlay += max(0.75, dot(bounce, light)) * 0.3 * step(0.015, stripe);
    }
#endif

    // Output to screen
    fragColor = vec4(bright * col + overlay,1.0);
}

