#define GLOW_SAMPLES 40
#define GLOW_DISTANCE 0.05
#define GLOW_POW 1.2
#define GLOW_OPACITY 1.5

#define sat(a) clamp(a, 0., 1.)
mat2 r2d(float a) { float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }

