vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}


vec3 neon(float val, vec3 color) {
	float r = clamp(val, 0.0, 1.0);
    float r2 = r * r;
    float r4 = r2 * r2;
    float r16 = r4 * r4;
    vec3 c = color;
    vec3 c2 = pow(color, vec3(4.0)); // A darker, more saturated version of color

	vec3 outp = vec3(0.0);
	outp += c2 * r2; // Darker color falloff
	outp += c * r4; // Specified Color main part
	outp += vec3(1.0) * r16; // White core
	return outp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 delta = vec2(0.001, 0.01);
    float heat_below = texture(iChannel0, uv + vec2(0.0, -0.01), 2.0).a;
    float airflow_velocity = heat_below;
    delta *= airflow_velocity;

    float heat_below_left = texture(iChannel0, uv + vec2(delta.x, -delta.y)).a;
    float heat_below_right = texture(iChannel0, uv + vec2(-delta.x, -delta.y)).a;

    vec3 rand = hash33(vec3(fragCoord, iTime)) - 0.5;
    vec3 rand1 = hash33(vec3(iTime));

    float diff = (heat_below_right - heat_below_left);
    float ratio = clamp(-diff * 1.0 + rand1.x * 0.5, -0.5, 0.5);
    float heat = mix(heat_below_left, heat_below_right, ratio + 0.5);
    heat *= 0.97;


    if (iFrame == 0) {
        heat = 0.0;
    }
    float heat_source = clamp(1.0 - length((uv - vec2(0.35 + rand.y * 0.1 + rand1.y * 0.2, 0.0)) * vec2(2.0, 1.0)) * 10.0, 0.0, 1.0);


    heat += heat_source;

    fragColor = vec4(neon(pow(heat * 0.3, 1.0), vec3(1.0, 0.5, 0.1)),heat);
}

