vec3 circle(vec2 uv, vec2 pos, float radius)
{
    return vec3(float(dot(uv - pos, uv - pos) < radius * radius) * 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 ms = iMouse.xy / iResolution.xy;

    fragColor = vec4(vec3(0.0), 1.0);

    fragColor.rgb += circle(uv, vec2(0.5), 0.1);
    fragColor.rgb -= circle(uv, ms, 0.1);
    fragColor.rgb -= circle(uv, ms + vec2(0.2, 0.0), 0.05);
}

