#define cos3             0.8973775077 //cos((pi * 33.)/180.)*1.07
#define sin3             0.5827637675 //sin((pi * 33.)/180.)*1.07
#define sqrCos3          0.7526041040 //cos((pi * 33.)/180.)^2 *1.07
#define sqrSin3          0.3173958960 //sin((pi * 33.)/180.)^2 *1.07
#define sinCos3         -0.4887468198 //-sin((pi * 33.)/180.)*cos((pi * 33.)/180.)*1.07
// makes a 3D tiling cell pattern with two levels
float cellTiling3D(vec3 uvw) {
    float c = sphereGrid(uvw); // first grid

    // rotation/scale matric desmos.com/calculator/refsu6mvlj
    mat3 m = mat3(cos3, sinCos3, sqrSin3,
                  sin3, sqrCos3, sinCos3,
                  0., sin3, cos3);
    uvw *= m; //each iteration gets rotated and scaled one addional time
    c = min(c, sphereGrid(uvw)); // blending the voronoi with 

    return sqrt(c); //sqrt for center distance, 1.5 for 0-1 range
}

#define cos4             0.8660254038 //cos((pi * 30.)/180.)*1.
#define sin4             0.5 //sin((pi * 30.)/180.)*1.
#define cos5             0.5 //cos((pi * 60.)/180.)*1.
#define sin5             0.8660254038 //sin((pi * 60.)/180.)*1.
// makes a 3D tiling cell pattern with two levels with different rotation
float cellTiling3D2(vec3 uvw) {
    // rotation/scale matric desmos.com/calculator/refsu6mvlj
    mat3 mY = mat3(cos4, 0., sin4,
                   0., 1., 0.,
                  -sin4, 0., cos4); // rotation matric Y Axis
    float c = sphereGrid(uvw*mY); // first grid    

    mat3 mX = mat3(1., 0., 0.,
                   0., cos5, -sin5,
                   0., sin5, cos5); // rotation matric X Axis  
    c = min(c, sphereGrid(uvw*mX)); // blending the grids 

    return sqrt(c); //sqrt for center distance, 1.5 for 0-1 range
}

