#define SPLIT_RGB 0
#define SPLIT_COMPONENTS 1
#define SPLIT_PHASE 2

#define VIEW_MODE SPLIT_RGB

vec3 rainbow(float x)
{
    vec3 col = vec3(0);
    col.r = cos(x * tau - (0.0/3.0)*tau);
    col.g = cos(x * tau - (1.0/3.0)*tau);
    col.b = cos(x * tau - (2.0/3.0)*tau);

    return col * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float cubeRes = float(textureSize(iChannel0,0).x);

    #if(VIEW_MODE == SPLIT_RGB)
        vec2 uv = floor(fragCoord.xy) / (iResolution.x/2.);
        float side = floor(uv.x);
        uv.x = fract(uv.x);
        uv.y -= (iResolution.y - iResolution.x/2.)/iResolution.x;

        vec2 uvIn = floor(uv*fftSize);
        uvIn.x = reverseBits(mod(uvIn.x,fftSize), numStages);
        uvIn /= fftSize*2.;

        vec2 fftInR = textureCubeFace(iChannel0,0., uvIn+vec2(0.0,0.0)).xy;
        vec2 fftInG = textureCubeFace(iChannel0,0., uvIn+vec2(0.5,0.0)).xy;
        vec2 fftInB = textureCubeFace(iChannel0,0., uvIn+vec2(0.0,0.5)).xy;
        vec3 fftInRGB = vec3(length(fftInR),length(fftInG),length(fftInB));

        vec2 fftR = textureCubeFace(iChannel0,5., uv.yx*(fftSize/cubeRes)+vec2(0.0,0.0)).zw;
        vec2 fftG = textureCubeFace(iChannel0,5., uv.yx*(fftSize/cubeRes)+vec2(0.5,0.0)).zw;
        vec2 fftB = textureCubeFace(iChannel0,5., uv.yx*(fftSize/cubeRes)+vec2(0.0,0.5)).zw;
        vec3 fftRGB = log(-2.0+vec3(fftR.x,fftG.x,fftB.x))/log(fftSize*fftSize);

        fragColor = (side == 0.) ? vec4(fftInRGB,0) : 
                                   vec4(fftRGB,0);                      
        fragColor *= step(uv.y,1.0)*step(0.0,uv.y);
    #endif

    #if(VIEW_MODE == SPLIT_COMPONENTS)
        vec2 uv = floor(fragCoord.xy) / (iResolution.x/2.);
        float side = floor(uv.x);
        uv.x = fract(uv.x);
        uv.y -= (iResolution.y - iResolution.x/2.)/iResolution.x;

        vec2 uvIn = floor(uv*cubeRes);
        vec2 quadOffs = floor(uvIn/fftSize)*fftSize;
        uvIn.x = quadOffs.x + reverseBits(mod(uvIn.x,fftSize), numStages);
        vec2 uvFFT = clamp(mod(uv.yx,0.5) + floor(uv/0.5)*0.5, 0.0, (cubeRes-1.0)/cubeRes);

        vec2 fftIn = textureCubeFace(iChannel0,0., uvIn/cubeRes).xy;
        vec2 fftHV = textureCubeFace(iChannel0,5., uvFFT).zw;

        fragColor = (side == 0.) ? vec4(length(fftIn)) : 
                                   vec4(log(0.1+fftHV.x)/log(fftSize*fftSize));                      
        fragColor *= step(uv.y,1.0)*step(0.0,uv.y);
    #endif

    #if(VIEW_MODE == SPLIT_PHASE)
                vec2 uv = floor(fragCoord.xy) / (iResolution.x/2.);
        float side = floor(uv.x);
        uv.x = fract(uv.x);
        uv.y -= (iResolution.y - iResolution.x/2.)/iResolution.x;

        vec2 uvIn = floor(uv*cubeRes);
        vec2 offs = floor(uvIn/fftSize)*fftSize;
        uvIn.x = offs.x+reverseBits(mod(uvIn.x,fftSize), numStages);
        vec2 fftIn = textureCubeFace(iChannel0,0., uvIn/cubeRes).xy;

        vec2 uvFFT = clamp(mod(uv.yx,0.5)+floor(uv/0.5)*0.5, 0.0,(cubeRes-1.)/cubeRes);
        vec2 fftHV = textureCubeFace(iChannel0,5., uvFFT).zw;

        fragColor = (side == 0.) ? vec4(length(fftIn)) : 
                                   vec4(rainbow(fftHV.y),0);                      
        fragColor *= step(uv.y,1.0)*step(0.0,uv.y);
    #endif
}

