const float fftSize = 512.;
const float numStages = ceil(log2(float(fftSize)));

// -- Constants --
const float pi = 3.14159265;
const float tau = 6.28318531;

// -- Math Functions --

//Complex multiply
vec2 cmul(vec2 a,vec2 b)
{
	return vec2(dot(a,b*vec2(1,-1)), dot(a,b.yx));
}

//Reverse 0 to 'n' in integer 'x'
float reverseBits(float x, const float n)
{
    int r = 0;
    int xi = int(x);
    for(int i = 0;i < int(n);i++)
    {
        r = r << 1 | (xi & 1);
        xi >>= 1;
    }
    return float(r);
}

/*
Butterfly operation

(x0)____________(+)_(xy)
            \  /
             \/
             /\
(x1)_W(i,n)_/__\(-)_(zw)

xy = x0 + W(i,n)*x1
zw = x0 - W(i,n)*x1

*/
vec4 butterfly(vec2 x0, vec2 x1, float i, float n)
{
    float a = -floor(i)*tau/floor(n);
    vec2 w = vec2(cos(a),sin(a));
    x1 = cmul(x1, w);
    return vec4(x0 + x1, x0 - x1);
}

// -- Windowing Functions --

//Rectangular window
float Rectangle(float i, float N)
{
    return step(0.0,i)-step(N,i);
}

//Hann Window
float Hann(float i, float N)
{
    float x = clamp(floor(i)/floor(N),0.0,1.0);
    float w = sin(pi*x);
    return w*w;
}

//Triangular window
float Triangle(float i, float N)
{
    float x = clamp(floor(i)/floor(N),0.0,1.0);
    return 1.0-abs(2.0*x - 1.0);
}

// -- Cubemap Sampling Helper Functions --

//Converts a ray direction to uv coords (xy, normalized) & a face id (z, 0-5).
//for the cubemap face it's pointing to.
vec3 rayToFace(vec3 rd)
{
    vec3 asign = sign(rd);
    rd = abs(rd);
    float amax = max(max(rd.x,rd.y),rd.z);

    vec3 face = (rd.x == amax) ? vec3(asign.yz*rd.yz, floor(1.+.5*asign.x)) :
                (rd.y == amax) ? vec3(asign.xz*rd.xz, floor(3.+.5*asign.y)) :
                (rd.z == amax) ? vec3(asign.xy*rd.xy, floor(5.+.5*asign.z)) : 
                                 vec3(0,0,-1);
    face.xy = .5+.5*(face.xy/amax);
    face.xy = floor(face.xy*1024.)/1024.; 
    return face;
}

//opposite of rayToFace
//Converts normalized uv coords & a face id to a ray direction
//for cubemap sampling
vec3 faceToRay(vec3 face)
{
    face.xy = face.xy + 0.5/1024.;
    vec3 rd = vec3(2.0*fract(face.xy)-1.0, sign(mod(face.z,2.)-.5));
    rd = (face.z <= 1.) ? rd.zxy :
         (face.z <= 3.) ? rd.xzy :
         (face.z <= 5.) ? rd.xyz :
                          vec3(0);
    return normalize(rd);
}

//Sample a cubemap face (0-5) with normalized uv coords.
vec4 textureCubeFace(samplerCube cube, float face, vec2 uv)
{
    vec3 rd = faceToRay(vec3(uv,floor(face)));
    return texture(cube, rd, 0.0).rgba;  
}

