//Input Windowing Function (Rectangle/Triangle/Hann)
#define WINDOW_FUNCTION Hann

void mainCubemap( out vec4 fragColor, in vec2 fragCoord, in vec3 ro, in vec3 rd )
{
    float cubeRes = float(textureSize(iChannel0,0).x);
    vec3 face = rayToFace(rd);
    vec2 uv = face.xy*cubeRes;

    vec2 quadOffs = floor(uv/fftSize)*fftSize;
    int quadId = int(uv.x/fftSize) + 2*int(uv.y/fftSize);

    uv = mod(uv, vec2(fftSize));

    float idx = uv.x;
    float stage = face.z;
    vec4 cout = vec4(0.0); 

    if(stage == 0.0)
    {
        //XY = Horizonal FFT Input (Channel 1)
        //Input image is mapped 1:1 into the input buffer with no scaling.
        //Input image centered in the 512x512 buffers.
        vec2 texRes = iChannelResolution[1].xy;
        vec2 texUV = vec2(reverseBits(uv.x, numStages), uv.y);
        vec2 texOrig = vec2(fftSize/2.0)-vec2(texRes/2.0);

        vec2 windowOrig = texUV - max(vec2(0),texOrig);
        vec2 windowSize = min(vec2(fftSize),
                          min(vec2(fftSize) - texOrig,
                          min(texOrig + texRes, texRes)));

        vec4 col = texelFetch(iChannel1, ivec2(texUV - texOrig), 0);

        cout.xy = vec2(col[quadId], 0);         
        cout.xy *= WINDOW_FUNCTION(windowOrig.x, windowSize.x);
        cout.xy *= WINDOW_FUNCTION(windowOrig.y, windowSize.y);

        //ZW = Vertical FFT Input (Horizontal FFT output rotated 90deg)
        uv.x = float(reverseBits(uv.x, numStages));
        cout.zw = textureCubeFace(iChannel0, 5.0, (uv.yx + quadOffs.xy)/cubeRes).xy;
    }
    else
    {
        //2x Horizontal 4-sample FFTs on XY & ZW components
        float stage1Size = floor(exp2(float(stage)*2.0));
        float stage0Size = floor(stage1Size/2.0);

        float offs = floor(idx / stage1Size)*stage1Size;
        idx -= offs;

        float w0 = mod(idx, stage0Size/2.0);
        float w1 = mod(idx, stage1Size/2.0);

        vec4 soffs = mod(offs + w0 + vec4(0,1,2,3)*stage0Size/2., fftSize) + quadOffs.x;
        uv.y += quadOffs.y;

        vec4 x0 = textureCubeFace(iChannel0, stage-1.0, vec2(soffs[0], uv.y)/cubeRes);
        vec4 x1 = textureCubeFace(iChannel0, stage-1.0, vec2(soffs[1], uv.y)/cubeRes);
        vec4 x2 = textureCubeFace(iChannel0, stage-1.0, vec2(soffs[2], uv.y)/cubeRes);
        vec4 x3 = textureCubeFace(iChannel0, stage-1.0, vec2(soffs[3], uv.y)/cubeRes);

        vec4 y0y1_H = butterfly(x0.xy, x1.xy, w0 - fftSize/2.0, stage0Size);
        vec4 y2y3_H = butterfly(x2.xy, x3.xy, w0 - fftSize/2.0, stage0Size);

        vec4 y0y1_V = butterfly(x0.zw, x1.zw, w0 - fftSize/2.0, stage0Size);
        vec4 y2y3_V = butterfly(x2.zw, x3.zw, w0 - fftSize/2.0, stage0Size);

        vec4 z0z1_H, z0z1_V;

        if(mod(idx, stage0Size) < stage0Size/2.0)
        {
            z0z1_H = butterfly(y0y1_H.xy, y2y3_H.xy, w1 - fftSize/2.0, stage1Size);            
            z0z1_V = butterfly(y0y1_V.xy, y2y3_V.xy, w1 - fftSize/2.0, stage1Size);
        }
        else
        {
            z0z1_H = butterfly(y0y1_H.zw, y2y3_H.zw, w1 - fftSize/2.0, stage1Size);            
            z0z1_V = butterfly(y0y1_V.zw, y2y3_V.zw, w1 - fftSize/2.0, stage1Size);
        }

        cout = (idx < stage1Size/2.0) ? vec4(z0z1_H.xy,z0z1_V.xy): 
                                        vec4(z0z1_H.zw,z0z1_V.zw);
    }

    if(face.z == 5.0) //Convert to magnitude/phase on last stage of vertical FFT
    {
        cout.zw = vec2(length(cout.zw), atan(cout.w, cout.z)/pi + 0.5);
    } 

    fragColor = vec4(cout);
}

