/*
This is a variant of Shane's Cellular Tiling 
(https://www.shadertoy.com/view/4scXz2, https://www.shadertoy.com/view/MdKXDD).
His versions are pretty extensively commented so give it a read. The main 
feature of the celluar tiling is it looks very similar to Voronoi noise, 
but it is much faster, because it does not use hash. It essentiall rotates 
a number of sphere grids around to create the illusion of randomness.

If you want to see if this is faster than a texture lookup see here 
(https://www.shadertoy.com/view/sd3fDM).

I changed a number of things to make it simpler and easier to understand. 
You can also select a number of parameters with the mouse.
- GridRes controls the size of the first grid sample
- Level controls the number of grids added 3-4 looks pretty good.
- Degree is controlled by the y-axis of the mouse. It selects the degree of 
    rotation of each grid is offset to each other. You should avoid multiple 
    of 45 degrees and a few degrees around it.
- Scale selects the size difference for each grid layer. Note that values 
greater than about +-10% will make the edges rounded.

I have made a number of comments to make it clear what is happening and why. 
This probably only for myself, when I forgot how to do this in a year. 
*/

// Text on Screen from Piperoman https://www.shadertoy.com/view/XllSWl
float DigitBin(const in int x) {
    return x==0?480599.0:x==1?139810.0:x==2?476951.0:x==3?476999.0:x==4?350020.0:x==5?464711.0:x==6?464727.0:x==7?476228.0:x==8?481111.0:x==9?481095.0:0.0;
}

float PrintValue(const in vec2 fragCoord, const in vec2 vPixelCoords, const in vec2 vFontSize, const in float fValue, const in float fMaxDigits, const in float fDecimalPlaces) {
    vec2 vStringCharCoords = (fragCoord.xy - vPixelCoords) / vFontSize;
    if ((vStringCharCoords.y < 0.0) || (vStringCharCoords.y >= 1.0)) return 0.0;
	float fLog10Value = log2(abs(fValue)) / log2(10.0);
	float fBiggestIndex = max(floor(fLog10Value), 0.0);
	float fDigitIndex = fMaxDigits - floor(vStringCharCoords.x);
	float fCharBin = 0.0;
	if(fDigitIndex > (-fDecimalPlaces - 1.01)) 
    {
		if(fDigitIndex > fBiggestIndex) 
        {
			if((fValue < 0.0) && (fDigitIndex < (fBiggestIndex+1.5))) fCharBin = 1792.0;
		} 
        else 
        {		
			if(fDigitIndex == -1.0) 
            {
				if(fDecimalPlaces > 0.0) fCharBin = 2.0;
			} 
            else 
            {
				if(fDigitIndex < 0.0) fDigitIndex += 1.0;
				float fDigitValue = (abs(fValue / (pow(10.0, fDigitIndex))));
                float kFix = 0.0001;
                fCharBin = DigitBin(int(floor(mod(kFix+fDigitValue, 10.0))));
			}		
		}
	}
    return floor(mod((fCharBin / pow(2.0, floor(fract(vStringCharCoords.x) * 4.0) + (floor(vStringCharCoords.y * 5.0) * 4.0))), 2.0));
}

// makes inferno colormap from Mattz https://www.shadertoy.com/view/3lBXR3
vec3 inferno(float t) {
    const vec3 c0 = vec3(0.0002189403691192265, 0.001651004631001012, -0.01948089843709184);
    const vec3 c1 = vec3(0.1065134194856116, 0.5639564367884091, 3.932712388889277);
    const vec3 c2 = vec3(11.60249308247187, -3.972853965665698, -15.9423941062914);
    const vec3 c3 = vec3(-41.70399613139459, 17.43639888205313, 44.35414519872813);
    const vec3 c4 = vec3(77.162935699427, -33.40235894210092, -81.80730925738993);
    const vec3 c5 = vec3(-71.31942824499214, 32.62606426397723, 73.20951985803202);
    const vec3 c6 = vec3(25.13112622477341, -12.24266895238567, -23.07032500287172);

    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
}


// creates a sphere grid of the squared distance to the center
float sphereGrid(vec2 uv) {
    vec2 squaregrid = fract(uv) - .5; //rectangular grid with center in the middle
    float spheregrid = dot(squaregrid, squaregrid); //squared distance form scenter
    return spheregrid;
}

#define pi              3.1415926536
// makes a tiling cell pattern
float cellTiling(vec2 uv, float degree, float scale, int level) {
    float c = sphereGrid(uv); // first grid

    // choose a deg/scale and calculate it here desmos.com/calculator/8de0aaehdc
    float rad = (pi* degree)/180.; //degree to radians
    mat2 m = mat2(cos(rad), -sin(rad), sin(rad), cos(rad)); // rotation matric
    m *= scale; // this make the rmatrix also scale

    // precalculate the m*m for perfromance
    for (int i = 0; i < level-1; i++){    
        uv *= m; //each iteration gets rotated and scaled one addional time
        c = min(c, sphereGrid(uv)); // blending the voronoi with 
    }

    return sqrt(c)*1.5; //sqrt for center distance, 1.5 for 0-1 range
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float gridRes = 6.0; //Hex Grid Size
    int level = 4; //number of point grids, should be above 1. 

    // mouse controls
    float scale = 1.10; //default values for preview
    float degree = 24.; // default values for preview
    if(iMouse.x > 0.0)	{
        vec4 mouse = iMouse / iResolution.y;
        scale = .17 * mouse.x +.85;  // value range .85-1.15
        degree = mouse.y  * 90.; // it's a square gird going going larger than 90 is pointless
    }

    //UV needs to be offset by a large vector from the 0,0, and avoiding 120/90/60 ... deg
    vec2 offset = vec2(379., 769.); // two arbitrary large prime numbers
    vec2 ani = vec2(sin(iTime*0.1),cos(iTime*0.1)); //large circular motion
    vec2 uv = vec2(fragCoord/iResolution.y - offset + ani)*gridRes; // uv scaled and offset

    float vor = cellTiling(uv, degree, scale, level); // generating cell Tilling    

    vec3 vColour = inferno(vor); // applying cosmetic colormap

    // Text portion
    float fDigits = 1.0;
	float fDecimalPlaces = 3.0;	
    vec2 vFontSize = vec2(8.0, 15.0); // Multiples of 4x5 work best

    if(iMouse.x > 0.0)	{
        // Plot Mouse Pos Point
	    float fDistToPointB = length( vec2(iMouse.x, iMouse.y) - fragCoord.xy) - 4.0;
	    vColour = mix(vColour, vec3(0.0, 1.0, 0.0), (1.0 - clamp(fDistToPointB, 0.0, 1.0)));

		// Print Mouse X
		vec2 vPixelCoord2 = iMouse.xy + vec2(-62.0, 6.0);
		float fValue2 = scale;
		fDigits = 1.0;
		float fIsDigit2 = PrintValue(fragCoord, vPixelCoord2, vFontSize, fValue2, fDigits, fDecimalPlaces);
		vColour = mix( vColour, vec3(0.0, 1.0, 0.0), fIsDigit2);

		// Print Mouse Y
		vec2 vPixelCoord3 = iMouse.xy + vec2(0.0, 6.0);
		float fValue3 = degree;
		fDigits = 1.0;
		float fIsDigit3 = PrintValue(fragCoord, vPixelCoord3, vFontSize, fValue3, fDigits, fDecimalPlaces);
		vColour = mix( vColour, vec3(0.0, 1.0, 0.0), fIsDigit3);
	}

    fragColor = vec4(vColour,1.0);
}

// License CC0: BubbleSort in 2022?
// I saw a tweet from hexler with some neat circle based shader
// Made a try to recreate it. Although not the same I think the result 
// turned out neat
// WRT bubblesort. I do a bubble sort to sort the the circle heights.
// I am open for better ideas!


#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))


vec2 mod2_1(inout vec2 p) {
  p += 0.5;
  vec2 n = floor(p);
  p = fract(p)-0.5;
  return n;
}

// License: Unknown, author: Unknown, found: don't remember
vec2 hash(vec2 p) {
  p = vec2(dot (p, vec2 (127.1, 311.7)), dot (p, vec2 (269.5, 183.3)));
  return -1. + 2.*fract (sin (p)*43758.5453123);
}

// License: CC0, author: Mårten Rånge, found: https://github.com/mrange/glsl-snippets
vec3 alphaBlend(vec3 back, vec4 front) {
  // Based on: https://en.wikipedia.org/wiki/Alpha_compositing
  return mix(back, front.xyz, front.w);
}

// License: Unknown, author: Unknown, found: don't remember
float tanh_approx(float x) {
  //  Found this somewhere on the interwebs
  //  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

float cellhf(vec2 n) {
  float h = texture(iChannel0, 0.005*n+0.5).x; 
  h = sqrt(h);
  h = smoothstep(0.1, 0.85, h);
  return h;
}

float celldf(vec2 p, vec2 n) {
  vec2 h = hash(n);
  const float off = 0.25;
  p += off*h;
  float dd = length(p) - (sqrt(0.5)+off);
  return dd;
}

vec3 effect(vec2 p) {
  const float sz = 0.25;
  const float amp = 10.0;
  const float th = -TAU*sz*20.0;
  p += amp*cos(vec2(1.0, sqrt(0.5))*TAU*(TIME)/(amp*30.0));
  vec2 op = p;
  p /= sz;  
  float aa = 2.0/(sz*RESOLUTION.y);
  vec2 n = mod2_1(p);

  const int c = 1;
  const int l = (2*c+1)*(2*c+1);
  vec2 results[l];
  int j = 0;
  for (int x = -c; x <= c; ++x) {
    for (int y = -c; y <= c; ++y) {
      vec2 off = vec2(float(x), float(y));
      vec2 pp = p - off;
      vec2 nn = n + off;

      float d = celldf(pp, nn);
      float h = cellhf(nn);

      results[j] = vec2(d, h);
      ++j;
    }
  }

  // Bubble sort in 2022?
  for (int o = 1; o < l; ++o) {
    for (int i = o; i > 0; --i) {
      vec2 l = results[i-1];
      vec2 r = results[i];
      if (l.y > r.y) {
        results[i - 1] = r;
        results[i] = l;
      }
    }
  }

  vec3 col = vec3(0.0);
  for (int i = 0; i < l; ++i) {
      vec2 r = results[i];
      float d = r.x;
      float h = r.y;
      vec4 bcol0 = vec4(hsv2rgb(vec3(fract(0.05*length(op)),mix(0.125, 1.0, h), mix(0.25, 1.0, h))), 1.0);
      vec4 bcol1 = vec4(vec3(bcol0*bcol0*0.25), 0.5);
      vec4 bcol = mix(bcol0, bcol1, smoothstep(-th*aa, th*aa, sin(th*d)));
      float t = smoothstep(aa, -aa, d);
      vec4 ccol = bcol;
      ccol.w *= t;


      col *= mix(1.0, 0.25, exp(-10.0*max(d, 0.0)));
      col = alphaBlend(col, ccol);
  }
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p);
  col = clamp(col, 0.0, 1.0);
  col *= smoothstep(0.0, 4.0, TIME);
  col = sqrt(col);
  fragColor = vec4(col, 1.0);
}

#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

// thx iq! https://iquilezles.org/articles/distfunctions/
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

// thx iq! https://iquilezles.org/articles/distfunctions2d/
float sdBox(vec2 p, vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

vec2 Hash22(vec2 p) {
    p = fract(p*vec2(234.56,789.34));
    p+=dot(p,p+34.56);
    return fract(p+p);
}

float planeBody(vec2 p){
    vec2 prevP = p;
    float w = 0.15;
    p.x -= clamp( p.x, -w, w );
    float d = length(p)-0.05;
    p = prevP;
    float a = radians(-75.0);
    p.y+=0.04;
    float mask = dot(p,vec2(cos(a),sin(a)));
    d = max(mask,d);
    p = prevP;

    p.x+=0.19;
    p.y+=0.02;
    p.y*=2.0;
    float d2 = length(p)-0.04;

    d = smin(d,d2,0.05);

    return d;
}

float planeWingL(vec2 p){
    vec2 prevP = p;
    float d = sdBox(p,vec2(0.07,0.08));

    float a = radians(20.0);

    p.x+=0.03;
    float mask = dot(p,vec2(cos(a),sin(a)));
    d = max(-mask,d);
    p = prevP;

    a = radians(10.0);
    p.x-=0.03;
    mask = dot(p,vec2(cos(a),sin(a)));
    d = max(mask,d);

    return d;
}

float planeWingL_rear(vec2 p){
    vec2 prevP = p;
    float d = sdBox(p,vec2(0.06,0.03));

    float a = radians(40.0);

    p.x+=0.03;
    float mask = dot(p,vec2(cos(a),sin(a)));
    d = max(-mask,d);
    p = prevP;

    a = radians(30.0);
    p.x-=0.03;
    mask = dot(p,vec2(cos(a),sin(a)));
    d = max(mask,d);

    return d;
}

float planeWingR(vec2 p){
    vec2 prevP = p;
    float d = sdBox(p,vec2(0.07,0.05));

    float a = radians(-20.0);

    p.x+=0.03;
    float mask = dot(p,vec2(cos(a),sin(a)));
    d = max(-mask,d);
    p = prevP;

    a = radians(-10.0);
    p.x-=0.03;
    mask = dot(p,vec2(cos(a),sin(a)));
    d = max(mask,d);

    return d;
}

vec3 plane(vec2 p, vec3 col){
    p.x = mod(p.x,1.2)-0.6;

    vec2 prevP = p;
    float d = planeBody(p);
    col = mix(col,vec3(1.0),S(d,0.0));

    // front window
    d = sdBox(p-vec2(-0.18,0.02),vec2(0.03,0.012));
    d = max(planeBody(p),d);
    col = mix(col,vec3(0.8,0.9,1.0),S(d,0.0));

    // passenger windows
    p.x=abs(p.x)-0.02;
    p.x=abs(p.x)-0.02;
    p.x=abs(p.x)-0.02;
    p.x=abs(p.x)-0.02;
    d = sdBox(p-vec2(0.0,0.02),vec2(0.003,0.003))-0.01;
    col = mix(col,vec3(0.8,0.9,1.0),S(d,0.0));

    p = prevP;
    d = sdBox(p-vec2(-0.12,0.005),vec2(0.015,0.03));
    col = mix(col,vec3(0.95),S(d,0.0));

    // body paint

    d = sdBox(p-vec2(0.0,-0.06),vec2(0.3,0.03));
    d = max(planeBody(p),d);
    col = mix(col,vec3(0.2,0.6,1.),S(d,0.0));

    p = prevP;
    p.x+=0.04;
    p.y+=0.09;
    d = planeWingL(p);
    col = mix(col,vec3(0.9),S(d,0.0));
    p = prevP;

    p.x+=0.04;
    p.y-=0.1;
    d = planeWingR(p);
    col = mix(col,vec3(0.9),S(d,0.0));
    p = prevP;

    p.x-=0.16;
    p.y-=0.08;
    p.x*=1.5;
    p.y*=1.4;
    d = planeWingR(p);
    col = mix(col,vec3(0.9),S(d,0.0));

    p = prevP;

    p.x-=0.17;
    p.y+=0.01;
    p.x*=1.5;
    d = planeWingL_rear(p);
    col = mix(col,vec3(0.9),S(d,0.0));

    return col;
}

vec3 airship(vec2 p, vec3 col){
    p.x+=0.6;
    p.x = mod(p.x,1.2)-0.6;
    p.y+=sin(iTime*0.5)*0.03;
    vec2 prevP = p;

    p.x-=0.16;
    float d = sdBox(p,vec2(0.03,0.08));
    float a = radians(-30.0);
    p.y = abs(p.y)-0.04;
    p.y -=0.05;

    float mask = dot(p,vec2(cos(a),sin(a)));
    d = max(-mask,d);
    col = mix(col,vec3(0.3,0.7,1.0),S(d,0.0));
    p = prevP;

    p.y+=0.1;
    d = sdBox(p,vec2(0.06,0.03));
    a = radians(25.0);
    p.x+=0.04;
    mask = dot(p,vec2(cos(a),sin(a)));
    d = max(-mask,d);
    col = mix(col,vec3(0.3,0.7,1.0),S(d,0.0));

    p.y+=0.005;
    float d2 = sdBox(p,vec2(0.05,0.008));
    d2 = max(d,d2);
    col = mix(col,vec3(0.6,0.9,1.0),S(d2,0.0));

    p = prevP;


    p.x*=0.5;
    p.y*=1.2;
    d = length(p)-0.1;
    col = mix(col,vec3(1.0,0.95,0.90),S(d,0.0));

    p.y*=2.5;
    d = length(p)-0.1;
    col = mix(col,vec3(0.3,0.7,1.0),S(d,0.0));
    p = prevP;

    return col;
}

float cloud(vec2 p, float r){
    vec2 prevP = p;
    p.x = abs(p.x)-0.02;
    float d = length(p)-0.04;
    p = prevP;
    p.y-=0.06;
    p.x-=0.06;

    vec2 n = Hash22(vec2(r));
    vec2 p2 = sin(n*iTime*5.0)*0.02;

    float d2 = length(p-p2)-0.05;
    d = smin(d,d2,0.05);
    p.x+=0.06;
    p.y-=0.01;

    n = Hash22(vec2(r));
    p2 = sin(n*iTime*4.5)*0.02;    

    d2 = length(p-p2)-0.05;
    d = smin(d,d2,0.05);

    p = prevP;
    p.y-=0.025;
    p.x-=0.02;
    p.x = abs(p.x)-0.08;
    d2 = length(p)-0.05;
    d = smin(d,d2,0.03);
    return d;
}

vec3 clouds(vec2 p, vec3 col, float n){
    p.x = mod(p.x,0.8)-0.4;
    vec2 prevP = p;
    float d = cloud(p,n);
    col = mix(col,vec3(0.98),S(d,0.0));
    d = abs(cloud(p,n))-0.005;
    col = mix(col,vec3(0.9),S(d,0.0));
    return col;
}

vec3 staticPassengerWindow(vec2 p, vec3 col){
    p.x+=0.6;
    p.x = mod(p.x,1.2)-0.6;
    float mask = sdBox(p,vec2(0.25,0.35))-0.05;

    col = mix(col,vec3(1.0),S(mask,0.0));

    float d = sdBox(p-vec2(0.0,0.0),vec2(0.25,0.35))-0.05;

    d = max(mask,d);
    col = mix(col,vec3(0.9),S(d,0.0));
    d = sdBox(p-vec2(0.0,-0.35),vec2(0.1,0.01));
    col = mix(col,vec3(0.8),S(d,0.0));

    d = abs(sdBox(p,vec2(0.26,0.36))-0.05)-0.02;
    col = mix(col,vec3(0.85),S(d,0.0));
    return col;
}

float cubicInOut(float t) {
  return t < 0.5
    ? 4.0 * t * t * t
    : 0.5 * pow(2.0 * t - 2.0, 3.0) + 1.0;
}

float getTime(float t, float duration){
    return clamp(t,0.0,duration)/duration;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;

    float glitchBlock = 30.0;
    vec2 uv2 = fract(uv*glitchBlock)-0.5;
    vec2 id = floor(uv2);

    vec2 n2 = fract(sin(id*123.456)*789.125);
    n2+=dot(id.x,id.y*567.89);

    float glitchDist = 0.02;
    float glitchTime = iTime*10.0;
    uv.x-=(fract(floor(uv.y+n2.y*glitchBlock)*glitchTime)*glitchDist);
    uv.x+=sin(floor(uv.y*glitchBlock*10.0)*iTime*30.)*0.01;

    vec2 prevMainUV = uv;


    uv = prevMainUV;
    vec3 col = mix(vec3(0.8,0.9,1.0),vec3(0.5,0.8,0.9),uv.y+0.7);
    float d = length(uv-vec2(0.15,0.2))-0.13;
    col = mix(col,vec3(1.0,0.9,0.8),S(d,-0.1));


    uv.x+=iTime*0.2;
    col = clouds(uv-vec2(0.,0.12),col,0.3);
    uv = prevMainUV;

    uv.x+=iTime*0.25;
    uv.x+=0.55;
    uv.y+=0.23;
    col = airship(uv,col);
    uv = prevMainUV;    

    uv.x+=iTime*0.1;
    col = clouds(uv-vec2(0.4,-0.2),col,0.21);  
    uv = prevMainUV;

    uv.x+=iTime*0.3;
    uv.x+=0.55;
    col = plane(uv,col);
    uv = prevUV;

    bool displayWindows = true;
    float frame = mod(iTime,15.);    
    float time = frame;
    float zoom = 0.0;
    if(frame<1.5){
        time = getTime(time,1.5);
        zoom = cubicInOut(time)*2.0;
    } else if(frame>=1.5 && frame<11.5){
        zoom = 2.0;
    } else {
        time = getTime(time-11.5,1.5);
        zoom = 2.0-(cubicInOut(time)*2.0);
    }

    //fragColor = vec4(col,1.0);
    //return;

    uv*=3.0-zoom;

    float mask = sdBox(uv,vec2(0.25,0.35))-0.05;
    d = max(-mask,0.0);
    col = mix(col,vec3(1.0),S(d,0.0));

    float animY = 0.0;

    if(frame>=1.5 && frame<7.){
        time = getTime(time-1.5,1.5);
        animY = cubicInOut(time)*0.7;
        displayWindows = false;
    } else if(frame>=7. && frame<10.) {
        animY = 0.7;
        displayWindows = false;
    } else if(frame>=10. && frame<11.5){
        time = getTime(time-10.,1.5);
        animY = 0.7-(cubicInOut(time)*0.7);
        displayWindows = false;
    } else {
        displayWindows = true;
    }

    d = sdBox(uv-vec2(0.0,animY),vec2(0.25,0.35))-0.05;

    d = max(mask,d);
    col = mix(col,vec3(0.9),S(d,0.0));
    d = sdBox(uv-vec2(0.0,-0.35+animY),vec2(0.1,0.01));
    col = mix(col,vec3(0.8),S(d,0.0));

    d = abs(sdBox(uv,vec2(0.26,0.36))-0.05)-0.02;
    col = mix(col,vec3(0.85),S(d,0.0));    


    if(displayWindows)col = staticPassengerWindow(uv,col);

    // Output to screen
    fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Quartic Reflections, mla, 2022.
//
// Inspired by https://www.shadertoy.com/view/XddfW7 by @wyatt - this
// one uses a nice quartic solver and also computes the normals
// analytically.
//
// A central cyclide and four smaller Goursat surfaces, all contained
// in one larger Goursat surface.
//
// <mouse>: change view direction
// <up/down>: zoom
// <left/right>: cycle through some interesting Goursat parameters
// 'c': inner cyclide
// 'g': outer goursat quartic
// 'r': autorotation
// 'x': no light bounce
// 'w': white objects
// 't': textured objects
// 'p': postprocessing
// 'q': choose cubic solver
//
// http://mathworld.wolfram.com/GoursatsSurface.html
// https://www.mathcurve.com/surfaces/goursat/goursat.shtml
//
////////////////////////////////////////////////////////////////////////////////

int maxbounces = 3;

// Lighting
vec3 light;
float ambient;
float diffuse;
float specular = 0.4;
float specularpow = 4.0;
vec3 specularcolor = vec3(1);

// The Kahan cubic algorithm, for explanation see:
// https://people.eecs.berkeley.edu/~wkahan/Math128/Cubic.pdf

int quadratic(float A, float B, float C, out vec2 res) {
  float b = -0.5*B, b2 = b*b;
  float q = b2 - A*C;
  if (q < 0.0) return 0;
  float r = b + sgn(b)*sqrt(q);
  if (false && r == 0.0) {
    res[0] = C/A;
    res[1] = -res[0];
  } else {
    res[0] = C/r;
    res[1] = r/A;
  }
  return 2;
}

// Evaluate cubic and derivative.
void eval(float X, float A, float B, float C, float D,
          out float Q, out float Q1, out float B1,out float C2) {
  float q0 = A*X;
  B1 = q0+B;
  C2 = B1*X+C;
  Q1 = (q0+B1)*X + C2;
  Q = C2*X + D;
}

// Solve: Ax^3 + Bx^2 + Cx + D == 0
// Find one real root, then reduce to quadratic.
int cubic(float A, float B, float C, float D, out vec3 res) {
  float X,b1,c2;
  X = -(B/A)/3.0;
  float t,r,s,q,dq,x0;
  eval(X,A,B,C,D,q,dq,b1,c2);
  t = q/A; r = pow(abs(t),1.0/3.0); s = sgn(t);
  t = -dq/A; if (t > 0.0) r = 1.324718*max(r,sqrt(t));
  x0 = X - s*r;
  if (x0 != X) {
    // We really do need this many iterations here
    for (int i = 0; i < 6; i++) {
      X = x0;
      eval(X,A,B,C,D,q,dq,b1,c2);
      if (dq == 0.0) break;
      x0 -= (q/dq);
    }
    if (abs(A)*X*X > abs(D/X)) {
      c2 = -D/X; b1 = (c2 - C)/X;
    }
  }
  res.x = X;
  return 1 + quadratic(A,b1,c2,res.yz);
}

// Special wrapper for cubic function for solving quartic.
// Find largest real root of x**3 + a*x**2 + b*x + c
// Assume c < 0
float qcubic0(float a, float b, float c) {
  if (c == 0.0) return 0.0;
  vec3 res;
  int nroots = cubic(1.0,a,b,c,res);
  if (nroots == 1) return res.x;
  else return max(res.x,max(res.y,res.z));
}

float cuberoot(float x) {
  return sign(x)*pow(abs(x),1.0/3.0);
}

float dcubic(float p, float q) {
  // NB: solves x³+3px+q = 0 & returns largest real root,
  // which is just what we want for Lanczos.
  float h = q*q + 4.0*p*p*p;
  float r = sqrt(abs(h));
  if (h >= 0.0) {
    float u = -0.5*(q+sgn(q)*r); // Largest root of quadratic
    float t = cuberoot(u);
    return t - p/t;
  } else {
    float theta = atan(r,-q); // 0 <= theta < PI
    float k = 2.0*sqrt(abs(p));
    return k*cos(theta/3.0);
  }
}

float qcubic1(float b, float c, float d) {
  b /= 3.0; c /= 3.0;
  // Now we are solving x³ + 3bx² + 3cx + d = 0
  float p = c-b*b;
  float q = 2.0*b*b*b - 3.0*b*c + d;
  float x = dcubic(p,q); // Solve depressed cubic
  x -= b; // Undepress
  return x;
}

float qcubic(float b, float c, float d) {
  if (key(CHAR_Q)) return qcubic0(b,c,d);
  else {
    float x = qcubic1(b,c,d);
    for (int i = 0; i < 1; i++) {
      // Polish root with Newton-Raphson, one round is enough.
      float fx = d + x*(c+x*(b+x));
      float f1x = c+x*(2.0*b+3.0*x);
      if (f1x != 0.0) x -= fx/f1x;
    }
    return x;
  }
}

int quartic0(float c1, float c2, float c3, float c4, out vec4 res) {
  // Lanczos's quartic solution
  float alpha = 0.5*c1;
  float A = c2-alpha*alpha;
  float B = c3-alpha*A;
  float a,b,beta,psi;
  psi = qcubic(2.0*A-alpha*alpha, A*A+2.0*B*alpha-4.0*c4, -B*B);
  // There should be a positive root, but sometimes the cubic
  // solver doesn't find it directly (probably a double root
  // around zero).
  psi = max(0.0,psi);
  a = sqrt(psi);
  beta = 0.5*(A + psi);
  if (psi <= 0.0) {
    b = sqrt(max(beta*beta-c4,0.0));
  } else {
    b = 0.5*a*(alpha-B/psi);
  }
  int n0 = quadratic(1.0,alpha+a,beta+b,res.xy);
  int n1 = quadratic(1.0,alpha-a,beta-b,res.zw);
  if (n0 == 0) res.xy = res.zw;
  return n0 + n1;
}

// Ferrari's method, from Hall and Knight, 1887.
// Similar to Lanczos, but has some visible instabilities.
int quartic2(float p, float q, float r, float s, out vec4 roots) {
  p *= 0.5; r *= 0.5;
  float B = -0.5*q;
  float C = p*r-s;
  float D = 0.5*(s*(q-p*p)-r*r);
  float k = qcubic(B,C,D);
  float a = sqrt(p*p+2.0*k-q); // The sign is immaterial
  float b = (p*k-r)/a;         // Get b from a directly
  int n0 = quadratic(1.0,p-a,k-b,roots.xy);
  int n1 = quadratic(1.0,p+a,k+b,roots.zw);
  if (n0 == 0) roots.xy = roots.zw;
  return n0 + n1;
}

// Solver by @oneshade, based on Descartes' method
int quartic1(in float b, in float c, in float d, in float e, inout vec4 roots) {
    // Depress the quartic to x^4 + px^2 + qx + r by substituting x-b/4a
    // This can be found by substituting x+u and the solving for the value
    // of u that makes the t^3 term go away
    float bb = b * b;
    float p = (8.0 * c - 3.0 * bb) / 8.0;
    float q = (8.0 * d - 4.0 * c * b + bb * b) / 8.0;
    float r = (256.0 * e - 64.0 * d * b + 16.0 * c * bb - 3.0 * bb * bb) / 256.0;
    int n = 0; // Root counter

    // Solve for a root to (t^2)^3 + 2p(t^2)^2 + (p^2 - 4r)(t^2) - q^2 which resolves the
    // system of equations relating the product of two quadratics to the depressed quartic
    float ra =  2.0 * p;
    float rb =  p * p - 4.0 * r;
    float rc = -q * q;

    // Depress using the method above
    float ru = ra / 3.0;
    float rp = rb - ra * ru;
    float rq = rc - (rb - 2.0 * ra * ra / 9.0) * ru;

    float lambda;
    float rh = 0.25 * rq * rq + rp * rp * rp / 27.0;
    if (rh > 0.0) { // Use Cardano's formula in the case of one real root
        rh = sqrt(rh);
        float ro = -0.5 * rq;
        lambda = cuberoot(ro - rh) + cuberoot(ro + rh) - ru;
    }

    else { // Use complex arithmetic in the case of three real roots
        float rm = sqrt(-rp / 3.0);
        lambda = -2.0 * rm * sin(asin(1.5 * rq / (rp * rm)) / 3.0) - ru;
    }

    // Newton iteration to fix numerical problems (using Horners method)
    // Suggested by @NinjaKoala
    for(int i=0; i < 2; i++) {
        float a_2 = ra + lambda;
        float a_1 = rb + lambda * a_2;
        float b_2 = a_2 + lambda;

        float f = rc + lambda * a_1; // Evaluation of λ^3 + ra * λ^2 + rb * λ + rc
        float f1 = a_1 + lambda * b_2; // Derivative

        lambda -= f / f1; // Newton iteration step
    }

    // Solve two quadratics factored from the quartic using the cubic root
    if (lambda < 0.0) return n;
    float t = sqrt(lambda); // Because we solved for t^2 but want t
    float alpha = 2.0 * q / t, beta = lambda + ra;

    float u = 0.25 * b;
    t *= 0.5;

    float z = -alpha - beta;
    if (z > 0.0) {
        z = sqrt(z) * 0.5;
        float h = +t - u;
        roots.xy = vec2(h + z, h - z);
        n += 2;
    }

    float w = +alpha - beta;
    if (w > 0.0) {
        w = sqrt(w) * 0.5;
        float h = -t - u;
        roots.zw = vec2(h + w, h - w);
        if (n == 0) roots.xy = roots.zw;
        n += 2;
    }

    return n;
}

int quartic(float A, float B, float C, float D, float E, out vec4 roots) {
  int nroots;
  // Solve for the smallest cubic term, this produces the least wild behaviour.
  bool flip = abs(B/A) >= abs(D/E);
  float c1,c2,c3,c4;
  if (!flip) {
    c1 = B/A; c2 = C/A; c3 = D/A; c4 = E/A;
  } else {
    // Solve for reciprocal
    c1 = D/E; c2 = C/E; c3 = B/E; c4 = A/E;
  }
  nroots = quartic0(c1,c2,c3,c4,roots);
  if (flip) {
    // We have solved for the reciprocal.
    for (int i = 0; i < 4; i++) {
      if (i == nroots) break;
      roots[i] = 1.0/roots[i];
    }
  }
  return nroots;
}

int Goursat = 0;
int Cyclide = 1;

struct Surface {
  int type;
  vec4 params;
  vec3 p;
};

// Goursat Quartic Surfaces
// Equation: pp.pp + k(p.p)^2 + k'a^2(p.p) + k''a^4 = 0
// Derivative: 4ppp + 4k(p.p)p + 2k'a^2p
// Expansion with p => p+tr:
// pp => (p+tr)(p+tr) = pp + 2tpr + t^2rr
// pp.pp => (pp + 2tpr + t^2rr).(pp + 2tpr + t^2rr)
//  = pp.pp + 4tpp.pr + 6t^2pp.rr + 4t^3pr.rr + t^4rr.rr 
// p.p  => (p+tr).(p+tr) = p.p + 2tp.r + t^2r.r = p.p + 2tp.r + t^2
// (p.p)^2 = (p.p + 2tp.r + t^2)(p.p + 2tp.r + t^2) =
//         = p.p^2 + 4t^2(p.r)^2 + t^4 + 2(2t(p.p)(p.r) + (p.p)t^2 + 2t^3(p.r))
//         = p.p^2 + 4t^2(p.r)^2 + t^4 + 4t(p.p)(p.r) + 2(p.p)t^2 + 4t^3(p.r))
// ie.
// pp.pp + 4tpp.pr + 6t^2pp.rr + 4t^3pr.rr + t^4rr.rr +
// k(p.p^2 + t4[(p.p)(p.r)] + t^2[4(p.r)^2 + 2(p.p)] + t^3[4(p.r)] + t^4) +
// k'a^2(p.p + 2tp.r + t^2) +
// k''a^4
// collecting terms:
// t^0: pp.pp +   k(p.p)^2 +             k'a^2(p.p) + k''a^4
// t^1: 4pp.pr + 4k(p.p)(p.r) +         2k'a^2(p.r)
// t^2: 6pp.rr +  k[4(p.r)^2 + 2(p.p)] + k'a^2
// t^3: 4pr.rr + 4k(p.r)
// t^4: rr.rr +   k

int goursatsurface(Surface surface, vec3 p, vec3 r, out vec4 roots) {
  float t = dot(p,r);
  p -= t*r; // Now dot(p,r) == 0
  float k = surface.params[0];
  float k1 = surface.params[1];
  float k2 = surface.params[2];
  float a = surface.params[3];
  vec3 pp = p*p;
  vec3 pr = p*r;
  vec3 rr = r*r;
  float p2 = dot(p,p);
  float a2 = a*a;
  float a4 = a2*a2;

  float A =     dot(rr,rr) +     k;
  float B = 4.0*dot(pr,rr);
  float C = 6.0*dot(pp,rr) + 2.0*k*p2    + k1*a2;
  float D = 4.0*dot(pp,pr);
  float E =     dot(pp,pp) +     k*p2*p2 + k1*a2*p2 + k2*a4;

  int res = quartic(A,B,C,D,E,roots);
  roots -= t;
  return res;
}

vec3 goursatnormal(Surface surface, vec3 p) {
  // 4ppp + 4k(p.p)p + 2k'a^2p
  float k = surface.params[0];
  float k1 = surface.params[1];
  float a = surface.params[3];
  return 4.0*p*p*p + 4.0*k*dot(p,p)*p + 2.0*k1*a*a*p;
}

// Dupin Cyclides
int cyclidesurface(Surface surface, vec3 p, vec3 r, out vec4 roots) {
  float t = dot(p,r);
  p -= t*r; // Now p.r == 0
  float a = surface.params.x;
  float b = surface.params.y;
  float c = surface.params.z;
  float d = surface.params.w;
  // t0 = (p+tr).(p+tr) + K = (p.p + t^2 + K) (assuming p.r == 0)
  // t0*t0 = (p.p + K + t^2)(p.p + K + t^2) =   (p.p + K)^2 + 2t^2(p.p + K) + t^4
  // t = X.(p+tr) + Y = X.p + tX.r + Y      = tX.r + X.p + Y [X,Y = M,-L or N,0]
  // t*t = (X.p + Y + tX.r)(X.p + Y + tX.r) = (X.p + Y)^2 + 2t(X.p + Y)(X.r) + t^2(X.r)^2
  float K = (b+d)*(b-d); float L = c*d;
  vec3 M = vec3(a,0,0); vec3 N = vec3(0,b,0);
  float pp = dot(p,p);
  float Mr = dot(M,r); float Mp = dot(M,p);
  float Nr = dot(N,r); float Np = dot(N,p);
  float A = 1.0;
  float B = 0.0;
  float C = 2.0*(pp+K) - 4.0*Mr*Mr - 4.0*Nr*Nr;
  float D = -8.0*((Mp-L)*Mr + Np*Nr);
  float E = (pp+K)*(pp+K) - 4.0*(Mp-L)*(Mp-L) - 4.0*Np*Np;
  int res = quartic(A,B,C,D,E,roots);
  roots -= t;
  return res;
}

vec3 cyclidenormal(Surface surface, vec3 p) {
  float a = surface.params.x;
  float b = surface.params.y;
  float c = surface.params.z;
  float d = surface.params.w;
  float K = (b+d)*(b-d);
  float L = c*d;
  vec3 M = vec3(a,0,0);
  vec3 N = vec3(0,b,0);
  float t0 = dot(p,p) + K;
  float t1 = dot(M,p) - L;
  float t2 = dot(N,p);
  return 2.0*(2.0*t0*p - 4.0*t1*M - 4.0*N*t2);
}

vec3 applylighting(vec3 baseColor, vec3 p, vec3 n, vec3 r) {
  if (dot(r,n) > 0.0) n = -n; // Face forwards
  vec3 c = baseColor*ambient;
  c += baseColor*diffuse*(max(0.0,dot(light,n)));
  float s = pow(max(0.0,dot(reflect(light,n),r)),specularpow);
  c += specular*s*specularcolor;
  return c;
}

struct Result {
  vec3 p;
  vec3 n;
  vec3 basecolor;
  float t;
};

int dosurface(Surface surface, vec3 p, vec3 r, out vec4 roots) {
  p -= surface.p;
  if (surface.type == Goursat) return goursatsurface(surface,p,r,roots);
  if (surface.type == Cyclide) return cyclidesurface(surface,p,r,roots);
}

vec3 donormal(Surface surface, vec3 p) {
  p -= surface.p;
  if (surface.type == Goursat) return goursatnormal(surface,p);
  if (surface.type == Cyclide) return cyclidenormal(surface,p);
}

vec3 getcol(vec3 dir) {
  if (key(CHAR_W)) return vec3(0.8);
  if (key(CHAR_T)) return pow(texture(iChannel0,dir).rgb,vec3(2.2));
  return pow(abs(dir),vec3(1.4));
}

bool solve(Surface surface, vec3 p, vec3 r, inout Result result) {
  vec4 roots;
  int nroots = dosurface(surface,p,r,roots);
  bool found = false;
  for (int i = 0; i < nroots; i++) {
    // Find closest root that is a little away from p,
    // to avoid problems if p is in the surface.
    if (roots[i] > 0.0001 && roots[i] < result.t) {
      result.t = roots[i];
      found = true;
    }
  }
  if (!found) return false;
  result.p = p + result.t*r;
  result.n = normalize(donormal(surface, result.p));
  if (dot(result.n,r) > 0.0) result.n = -result.n;
  result.basecolor = getcol(result.n);
  return true;
}

// Interesting parameters from:
// https://www.mathcurve.com/surfaces.gb/goursat/goursat.shtml
const vec4 gparams[] =
  vec4[](vec4(0,0,-1,0.5),
         //vec4(0,0,-1,1),
         vec4(0,-1,0,1),
         vec4(-1,-0.25,0.25,1),
         //vec4(-1,1,1,1),
         vec4(0,-2,2,1),
         //vec4(-0.5,-1,0.5,1),
         //vec4(-0.5,1,-1.5,1),
         vec4(-1,4,-6,1),
         vec4(-1,1,1,1),
         vec4(-1,2,-2,1));

vec4 goursatparams(int i) {
  if (i >= 0 && i < gparams.length()) return gparams[i];
  return vec4(-0.333,-0.666,0.666,1);
}

int nparams = 10;

int imod(int n, int m) {
    return n-n/m*m;
}

vec4 tparams (float t) {
  float ttime = 0.5*t;
  float rtime = floor(ttime);
  ttime -= rtime;
  return mix(goursatparams(int(rtime)%nparams),
             goursatparams(int(rtime+1.0)%nparams),
             ttime);
}

vec3 scene(vec3 p, vec3 r) {
  vec3 col = vec3(0);
  float att = 0.5;
  for (int bounce = 0; bounce <= maxbounces; bounce++) {
    Result res = Result(vec3(0),vec3(0),vec3(0),1e8);
    bool hit = false;
    if (!key(CHAR_C)) {
      float a = 1.0; float b = 0.95;
      float c = sqrt(a*a-b*b); float d = 0.1;//sin(iTime);
      Surface s = Surface(Cyclide,vec4(a,b,c,d),vec3(0));
      hit = solve(s,p,r,res) || hit;
    }
    if (!key(CHAR_G)) {
      Surface s = Surface(Goursat,vec4(0,0,-1,2),vec3(0));
      hit = solve(s,p,r,res) || hit;
    }
    int isurface = max(0,3+keycount(KEY_RIGHT)-keycount(KEY_LEFT));
    for (int i = -1; i <= 1; i+=2)
      for (int j = -1; j <= 1; j+=2)
        for (int k = -1; k <= 1; k+=2) {
          Surface s = Surface(Goursat,gparams[isurface%gparams.length()],vec3(i,j,k));
          s.params.a = 0.4;
          hit = solve(s,p,r,res) || hit;
        }
    vec3 bgcol = vec3(0);
    if (key(CHAR_B)) bgcol = getcol(r);
    if (!hit) {
      col += att*bgcol;
      break;
    }
    col += att*applylighting(res.basecolor,res.p,res.n,r);
    if (key(CHAR_X)) break;
    p = res.p;
    r = reflect(r,res.n);
    r = normalize(r);
    att *= 0.5;
  }
  return col;
}

// GUI related code starts here
vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (!key(CHAR_R)) {
    float t = iTime;
    //p.yz = rotate(p.yz, 0.1*t);
    p.zx = rotate(p.zx, 0.1618*t);
  }
  return p;
}

// https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
#define float3 vec3
float3 ACESFilm(float3 x) {
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    x = x*(a*x+b)/(x*(c*x+d)+e);
    return x;
}
vec3 postprocess(vec3 color) {
  // Some experimental tone mapping.
  color = ACESFilm(color);
  //color = 2.0*color/(1.0+color);
  //color = color*color*(3.0-2.0*color);
  color = clamp(color,0.0,1.0);
  return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  light = vec3(1,1,-1);
  ambient = 0.4;
  diffuse = 1.2-ambient;
  specular = 0.8;
  specularpow = 10.0;

  float scale = 1.0;
  float camera = 2.0;
  camera *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  vec3 p = vec3(0,0,-camera);
  p = transform(p);
  light = transform(light);
  light = normalize(light);
  int AA = 1;
  //if (key(CHAR_A)) AA = 2;
  vec3 color = vec3(0);
  for (int i = 0; i < AA; i++) {
     for (int j = 0; j < AA; j++) {
       vec2 uv = scale*(2.0*(fragCoord.xy+vec2(i,j)/float(AA)) - iResolution.xy)/iResolution.y;
       vec3 r = vec3(uv, 2.0);
       r = transform(r);
       r = normalize(r);
       color += scene(p,r);
     }
  }
  color /= float(AA*AA);
  vec2 q = fragCoord/iResolution.xy;
  if (!key(CHAR_P)) color = postprocess(color);
  color = pow(color,vec3(0.4545));
  if (alert) color = vec3(1,0,0);
  fragColor = vec4(color,1);
}

