// WIP. test of "anti-panoramic" camera, rotating around (0,0,Z).
// mid screen: horizontal cylinder + balls around (0,0,Z).
// variant of  https://shadertoy.com/view/NdtBDN
// here, the camera position turns at a different speed than ray direction.
// variant of panoramic camera https://shadertoy.com/view/ftffWN

#define T(U)    ( V = smoothstep(0.,-.05,abs(fract(U)-.5) -.5 ),    \
                  vec4( texture(iChannel0, U).rgb * V.x*V.y,        \
                        smoothstep(0.,-1.5*fwidth(U).y, abs((U).y-.5) -.5 ) \
                )     ) // pattern
#define rot(a)    mat2(cos( a + vec4(0,11,33,0)))
#define blend(T)  O = O* (1.- (m=T.w)) + T , m>.5 ? z=min(z,l) :z, m=0.

void mainImage(out vec4 O, vec2 u)
{
    O-=O;
    vec2  R = iResolution.xy, V,
          U = ( 2.*u - R ) / R.x,                   // normalized screen coordinates
          M = iMouse.xy/R;
    if ( int(u) ==int(R)/2 ) { O = vec4(1,0,0,1); return; } // red separator

    float Z = 8.,F, H = 1.5,                        // Z: object distance
          a,b,c,d, l,m=0.,z=1e9,                    // F: anticylindrical focus distance
          W = iMouse.z > 1e4 ? .38/ M.x : 1.;       // 1/W: camera opening
    F = W = 1./max(0.,texelFetch(iChannel1,ivec2(0),0).y );
    vec3  P0 = vec3(0,0,0), P, D = vec3( U , W );   // ray direction: left: flat perspective

    if (iMouse.z > 0.) W *= 1.+ (2.*M.y-1.);

#if 1
    if (D.x>0.) {                                   // right: cylindrical perspective (panorama)
          a = U.x/W;
          D.xz = W* vec2(sin(a),cos(a));
          if (abs(a) > 3.14/2.) O.r+=.5;            // view > 180° : mid-red alert
          if (abs(a) > 3.14)    O.r+=.5;            // view > 360° : red alert
       }
#endif
    D = normalize(D);

#if 0
    if (iMouse.z > 0.)
        D.yz *= rot( 1.57*(2.*M.y-1.) ),
        D.xz *= rot( 1.57*(2.*M.x-1.) );
#endif

#if 1
    if (D.x>0.) 
          a = U.x/F,
          P0.xz = vec2(0,Z) - Z *vec2(sin(a),cos(a));  // anticylindrical camera
#endif   

    P = ( H - sign(D.y)*P0.y ) * D/abs(D.y) + P0;   // --- roof + floor
    if ( P.y > 0. )                                 // draw roof square grid
        O +=   T(P.xz+vec2(.5,0) ) 
             * vec4(0,1,1,0) * exp(-P.z/Z);         // color + darkening with z

    if ( P.y < 0. )                                 // draw floor polar grid
        O +=  T(vec2(Z*atan(P.x,P.z),Z*log(length(P.xz)))+vec2(.5,0) )
             * vec4(1,.5,0,0) * exp(-P.z/Z);        // color + darkening with z

                                                    // --- ribbons
    l = (Z-P0.z)/abs(D.z), P = l* D +P0;            // draw flat ribbon 
    if ( abs(P.y-1.) < .5 && P.z>0. )   
        blend( T(P.xy-.5) );                        // with mapped texture


    a = dot(D.xz,D.xz), b = dot(P0.xz,D.xz), c = dot(P0.xz,P0.xz) -Z*Z,
    d = b*b - a*c;
    if (d>0.) {
        l = ( -b +  sqrt(d) )/ a , P = l* D + P0;
     // P = Z* D/length(D.xz) + P0;                 // draw cylindrical ribbon
        if ( abs(P.y+1.) < .5 )                     // with mapped texture
            blend( T(vec2(Z*atan(P.x,P.z),P.y+2.)-.5) );  
    }

    float  t = 10.,s; c = 1., d=0.; P=P0;           // --- red balls
   // P.y+= 5.;
    for ( ; c > 0. && t > .01 && d < z; c -= .04 ) {        // march scene
        //a = mod( atan(P.x,P.z), 6.28/16. ),
        for( a = 0.; a < 6.29; a += 6.28/16. )
            t = min(t, length( vec3(4.*cos(a),round(P.y),Z+4.*sin(a)) - P ) -.2 ); 
     // t =  min(s=length(mod(P,10.) -5.)-1.,P.y+5.), // distance to objects
        d += t = max(s=t, abs(P.y)-H);
        P += t*D; }                                 // sphere-tracing step
    if (t<.01) { O = vec4(c,0,0,1); return; }       // coloring & shading


    P0.z -= Z; Z = 2.;                              // --- small cylindrical ribbon
    a = dot(D.xz,D.xz), b = dot(P0.xz,D.xz), c = dot(P0.xz,P0.xz) -Z*Z,
    d = b*b - a*c;
    if (d>0.) {
        P = ( -b - sqrt(d) )/ a * D + P0;           // draw small cylindrical ribbon
        if ( abs(P.y) < .5 )                        // with mapped texture
            blend( T(vec2(Z*atan(P.x,P.z),P.y+1.)-.5) ); 
    }

}

