precision highp float;
precision highp int;
#define V vec2
#define F float
#define S step
#define SS smoothstep

F R(V s,F t){F p=length(s);return SS(.255,.25,p)*SS(.20,.205,p)*S(atan(s.y,s.x)+3.,t*6.);}
F C(V s,F t){V p=abs(V(s.x+s.y,s.y-s.x));F x=S(t,.5),y=S(t-.5,.5);F h=.275-mod(t,.5)*.56;
return S(min(p.x,p.y),.025)*S(max(p.x+mix(h*y,.275,x),p.y+h*x),.3);}
F h(F n){return fract(sin(floor(n))*43758.5453123)*2.-1.;}

void mainImage(out vec4 o, in vec2 _f) {
  V r=iResolution.xy,u=(_f*2.-r)/min(r.x,r.y)*1.4,p=abs(u),g=mod(p-.3,.6)-.3,A,B,D,E,Z;
  F v=S(max(p.x,p.y),.9),w=S(min(g.x,g.y),-.28)*v,t=mod(iTime,9.),l=floor(iTime/9.),m=.62,n=.85,z=1.7,i,j,e,f,s;
  vec3 y,q=vec3(.2,.8,.8),x=vec3(.8,.2,.2),W=vec3(.6,.7,.2),c;
  int a=int[](809654321,-20218453,205987612,909128753,707631925)[int(mod(l,5.))],b;
  for(;i<t&&i<8.;i++){b=(abs(a)%10)-1;a/=10;if(b<0)break;V z=u+m*V(1-b%3,b/3-1);y+=mix(q*R(z,t-i),x*C(z,t-i),S(mod(i,2.),.5));}
  if(abs(a)<10){
    e=F(a);c=mix(W*W,mix(x,q,S(e,0.)),S(e,8.))*.6;e=abs(e)-1.;
    f=S(2.9,e);s=S(5.9,e);Z=V(n,n-z*min(1.1,(e-6.)));
    A=mix(V(mix(m*(e-1.),n,f),mix(n,m*(4.-e),f)),-Z,s)+V(h(l),h(l+5.37))*.1,
    B=mix(A-V(f,1.-f)*2.*n,Z,s)+V(h(l+7.53),h(11.47))*.1,D=u-A,E=B-A;
    j=SS(.051,.049,length(D-E*clamp(dot(D,E)/dot(E,E),0.,mix(1.,fract(t),S(t-F(i),1.)))));
  }
  o=vec4(mix(y+W*w,c,j),0.);
}

void mainImage(out vec4 C, in vec2 O)
{
    f = 2. / iResolution.y;
    vec2 uv = O - vec2(iResolution.x / 2., 0);
    uv *= f;
    uv.y -= 0.2;
    uv.x += 0.3;

    // animation blend states
    float t = .5+.5*cos(iTime/2.0); t*1.;
    float u = .5+.5*cos(iTime/3.2); u*=.5;
    float v = smoothstep(0.2,0.3,cos(iTime/2.7)); v*=1.;
    float w = smoothstep(0.5,0.6,cos(iTime/4.2+2.0)); w*=1.;
    float x = cos(iTime*12.); x*=smoothstep(0.5,1.0,cos(iTime/2.3));
    float y = .5+.5*cos(iTime*2.); y*=smoothstep(0.73,1.0,cos(iTime/3.9));
    u += .15 * y;

    float bend;

    // first ball
    Circle shoulder = Circle(vec3(0.0, 0.0, 0.0), vec2(0.00 + fade(.1,u), 0.53 + fade(.02,t) + fade(-.020,u)), 0.10, radians(5. + fade(-6.,u)));

    // right leg
    bend = 40. + fade(-2.,t) + fade(1.5,u);
