    Circle rightFoot = connect(right_4, vec3(0.50, 0.01, 0.00), radians(bend*-.5), 0.06);

    // back
    bend = -20.;
    Circle saddle = connect(shoulder, vec3(0.70, 0.10, 0.00), radians(-bend + fade(-2.,t)), 0.12);
    Circle hip = connect(saddle, vec3(0.70, 0.00, 0.00), radians(2.*bend), 0.10); turn(hip, radians(-bend));

    // tail
    bend = -20.+fade(25.,y);
    Circle tail_0 = connect(hip, vec3(0.90, 0.30, 0.02), radians(055.), 0.05);
    Circle tail_1 = connect(tail_0, vec3(0.70, 0.05, 0.00), radians(bend), 0.05);
