    // rear leg
    bend = -15. + fade(1.,t) + fade(-10.,u);
    Circle rear_0 = connect(hip, vec3(0.01, 0.01, 0.01), radians(310. + fade(20.,u)), 0.05);
    Circle rear_1 = connect(rear_0, vec3(0.05, 0.20, 0.02), radians(bend), 0.05);
    Circle rear_2 = connect(rear_1, vec3(0.00, 0.00, 0.00), radians(bend), 0.05);
    Circle rear_3 = connect(rear_2, vec3(0.80, 0.00, 0.00), radians(bend*1.5), 0.05);
    Circle rear_4 = connect(rear_3, vec3(0.00, 0.01, 0.03), radians(bend*.1), 0.05);
    Circle rearFoot = connect(rear_4, vec3(0.00, 0.00, 0.00), radians(bend*-.2), 0.06);

    // head
    bend = -15.;
    Circle neck_0 = connect(shoulder, vec3(0.80, 0.05, 0.00), radians(140.), 0.05);
    Circle neck_1 = connect(neck_0, vec3(0.00, 0.05, 0.20), radians(bend), 0.05);
    Circle neck_2 = connect(neck_1, vec3(0.00, 0.40, 0.00), radians(bend), 0.05);
    Circle head = connect(neck_2, vec3(0.80, 0.40, 0.03), radians(bend), 0.15);
    Circle hat = connect(head, vec3(0.70, 0.00, 0.00), radians(310.), 0.05); move(hat, -0.010, 0.000);
    // eye
    Circle eye = connect(head, vec3(0.90, 0.90, 0.90), radians(000.), 0.07); move(eye, -0.190, 0.020); turn(eye, radians(150.+fade(20.,u)));
    Circle pupil = connect(eye, vec3(0.00, 0.00, 0.00), radians(000.), 0.06); move(pupil, -0.132+fade(-.005,w), 0.014+fade(-.007,v));
    // nose
    Circle nose = connect(head, vec3(0.10, 0.01, 0.00), radians(080.), 0.06); move(nose, -0.030, 0.000);
    // mouth
    Circle mouth = connect(head, vec3(0.40, 0.05, 0.00), radians(125.), 0.03); move(mouth, -0.030+fade(.002,x), 0.000);

    // left leg
    bend = 25. + fade(-4., t);
    Circle left_0 = connect(shoulder, vec3(0.00, 0.00, 0.20), radians(215. + fade(7.,t) + fade(-4.,u)), 0.05);
    Circle left_1 = connect(left_0, vec3(0.00, 0.20, 0.00), radians(bend), 0.05);
    Circle left_2 = connect(left_1, vec3(0.00, 0.00, 0.15), radians(bend), 0.05);
    Circle left_3 = connect(left_2, vec3(0.01, 0.01, 0.01), radians(bend), 0.05);
    Circle left_4 = connect(left_3, vec3(0.40, 0.02, 0.00), radians(bend*-.2), 0.05);
    Circle leftFoot = connect(left_4, vec3(0.00, 0.00, 0.00), radians(bend*-.3), 0.06);

    C = vec4(0.11, 0.40, 0.90, 1);
    vec3 grass = vec3(0.6,0.7,0);
    vec2 shadow = (leftFoot.center + rightFoot.center + rearFoot.center * 2.) / 4.;
    //vec2 shadow = vec2(saddle.center.x, 0.);
    float rshadow = length((uv - shadow)*vec2(1.5,5));
    grass *= mix(.5, 1., smoothstep(0.1, 1., rshadow));
    C = blend(C, grass, -uv.y/f);
    C = blend(C, uv, shoulder);
    C = blend(C, uv, right_0);
	C = blend(C, uv, right_1);
	C = blend(C, uv, right_2);
	C = blend(C, uv, right_3);
	C = blend(C, uv, right_4);
	C = blend(C, uv, rightFoot);
	C = blend(C, uv, saddle);
	C = blend(C, uv, hip);
	C = blend(C, uv, tail_0);
	C = blend(C, uv, tail_1);
	C = blend(C, uv, tail_2);
	C = blend(C, uv, tail_3);
	C = blend(C, uv, rear_0);
	C = blend(C, uv, rear_1);
	C = blend(C, uv, rear_2);
	C = blend(C, uv, rear_3);
	C = blend(C, uv, rear_4);
	C = blend(C, uv, rearFoot);
	C = blend(C, uv, neck_0);
	C = blend(C, uv, neck_1);
	C = blend(C, uv, neck_2);
	C = blend(C, uv, head);
	C = blend(C, uv, hat);
	C = blend(C, uv, eye);
	C = blend(C, uv, pupil);
	C = blend(C, uv, nose);
	C = blend(C, uv, mouth);
	C = blend(C, uv, left_0);
	C = blend(C, uv, left_1);
	C = blend(C, uv, left_2);
	C = blend(C, uv, left_3);
	C = blend(C, uv, left_4);
	C = blend(C, uv, leftFoot);
    C.xyz = sqrt(C.xyz);
}

common_code:
struct Circle
{
    vec3 color;
    vec2 center;
    float radius;
    float angle;
};

mat2 R(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}
vec2 v(float a)
{
    return vec2(1, 0) * R(a);
}
Circle connect(Circle current, vec3 color, float angle, float radius)
{
    vec2 center = current.center;
    angle += current.angle;
    center += v(angle) * (current.radius + radius);
    return Circle(color, center, radius, angle);
}
void move(inout Circle current, float dx, float dy)
{
    current.center += vec2(dx, dy) * R(current.angle);
}
void turn(inout Circle current, float da)
{
    current.angle += da;
}

float fade(float target, float x) { return target * x; }

float l2(vec2 x)
{
    return dot(x, x);
}

float l2(vec3 x)
{
    return dot(x, x);
}

float f;
vec4 blend(vec4 C, vec3 c, float r)
{
    return mix(C, vec4(c, 1), clamp(r + .5, 0., 1.));
}

vec4 blend(vec4 C, vec2 uv, Circle circle) {
    vec3 light = normalize(vec3(10, 9, 5));
    vec3 rd = vec3(0, 0, -1);

    vec2 offset = uv - circle.center;
    float r = length(offset);
    vec3 normal = vec3(offset / circle.radius, 0);
    normal.z = sqrt(max(0., 1. - l2(normal.xy)));

    vec3 color = circle.color;
    color *= .5 + clamp(dot(normal, light), 0., 1.);
    color += vec3(.1) * pow(clamp(l2(cross(rd, normal)), 0., 1.), 4.) * clamp(dot(normal, light)+.1, 0., 1.);
    color += vec3(.5) * pow(clamp(dot(reflect(rd, normal), light), 0., 1.), 30.);
    C = blend(C, color, -(r-circle.radius)/f);
    return C;
}

image_code:
mat2 Rot2(float a ) 
{
    float c = cos( a );
    float s = sin( a );
    return mat2( c, -s, s, c );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    float angle = (0.3333)*3.1415*0.5;
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    uv*=1.5;
    uv *= Rot2(iMouse.x*0.002);

    uv.x = abs(uv.x);
    uv.x -= .075;

    uv.y = abs(uv.y);
    uv.x -= .5;
    uv *= Rot2(0.16667*3.1415);
    uv.x += .5;


    uv *= 3.;
    uv.x = abs(uv.x); 
    uv.x -= .5;

    uv *= -Rot2(angle);
    uv.x = abs(uv.x);
    uv *= Rot2(angle); 



    float b = 1.;
    for(float i = 0.; i < floor(iMouse.y*0.01); i++)
    {
        uv *= 3.;
        uv.x -= 1.5;

        uv.x = abs(uv.x); 
        uv.x -= .5;

        b *= -1.;

        uv *= -Rot2(b*angle);
        uv.x = abs(uv.x);
        uv *= Rot2(b*angle); 
    }


    float d = length(uv - vec2(clamp(uv.x, -1., 1.), 0.));
    col += smoothstep(pow(floor(iMouse.y*0.01),3.)*0.01+0.01, 0., d);

    fragColor = vec4(col,1.0);
}

#define BACKGROUND_COLOR vec3(0.9, 0.76, 0.55)
#define DESK_COLOR vec3(0.1, 0.06, 0.03)
#define WINDOW_SHADES_ON_COLOR vec3(0.855, 0.75, 0.58)
#define WINDOW_SHADES_OFF_COLOR vec3(0.235, 0.24, 0.266)
#define LIGHT_COLOR vec3(1.0, 0.98, 0.87)
#define TILES 5.0
// 0 = Off | 1 = On | 2 = On and Open
int STATE = 0; 

#define LIGHTS_ON (STATE == 1 || STATE == 2)

vec3 color = vec3(0);
vec3 col = vec3(0);
vec2 uv = vec2(0);
#define A (iResolution.x / iResolution.y)

float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
    float fl = floor(p);
    float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

float fbm(vec2 p, int octaves)
{
    float n = 0.0;
    float a = 1.0;
    float norm = 0.0;
    for(int i = 0; i < octaves; ++i)
    {
        n += noise(p) * a;
        norm += a;
        p *= 2.0;
        a *= 0.7;
    }
    return n / norm;
}

float luma(vec3 color)
{
    return dot(color, vec3(0.2126, 0.7152, 0.0722));
}

void rect(float x, float y, float w, float h)
{
    if(uv.x >= x && uv.x <= x + w && uv.y >= y && uv.y <= y + h)
        color = col;
}

void srect(float x, float y, float w, float h, float r, float s)
{
	vec2 offset = abs((uv - vec2(x + w * 0.5, y + h * 0.5)) / vec2(w,h)) - (0.5 - s - r);
	color = mix(col, color, smoothstep(0.0, s, length(max(offset, 0.0)) - r));
}

void background()
{
    vec2 luv = uv;
    uv.x = abs(uv.x);

    col = (LIGHTS_ON ? LIGHT_COLOR : WINDOW_SHADES_OFF_COLOR);
    srect(A * 0.3, 0.6, A * 0.55, 0.06, 0.05, 0.1);
    srect(A * 0.28, 0.1, A * 0.53, 0.06, 0.05, 0.1);
    vec3 lcolor = color;
    float l = luma(color); 

    col = (LIGHTS_ON ? BACKGROUND_COLOR * (0.8 + 0.4 * fbm(uv * 8.0, 4)) : WINDOW_SHADES_OFF_COLOR);
    rect(-A, -1.0, A * 2.0, 2.0);

    col = (LIGHTS_ON ? DESK_COLOR : WINDOW_SHADES_OFF_COLOR * 0.7);
    srect(-A * 2.0, -1.0, A * 4.0, 0.5, 0.0, 0.05);
    col = col * (LIGHTS_ON ? 2.0 : 1.5);
    srect(-A * 2.0, -0.54, A * 4.0, 0.15, 0.0, 0.05);
    col = col * 0.5;
    srect(0.0, -1.0, A, 0.4, 0.0, 0.05);
    col = (LIGHTS_ON ? DESK_COLOR : WINDOW_SHADES_OFF_COLOR);
    srect(A * 0.25, -0.55, 0.08, 0.15, 0.0, 0.2);
    srect(A * 0.23, -0.6, 0.08, 0.17, 0.0, 0.2);
    srect(A * 0.21, -0.6, 0.08, 0.14, 0.0, 0.2);
    srect(-0.1, -0.6, 0.6, 0.125, 0.0, 0.2);

    color = mix(color, lcolor, l * l * l * l);

    color += l * l * l * l * 0.2;

    uv = luv;
}

void shades()
{
    float shades = cos(uv.x * 50.0);
    float shade_col = 0.5 + 0.5 * abs(shades);
    shade_col /= 1.0 + smoothstep(-0.25, -1.0, -shades);
    shade_col *= 0.8 + fbm(uv * 2.0, 8) * 0.4;
    shade_col *= 0.9 + noise(uv.x * 200.0) * 0.2;
    color = mix(color, sqrt(sqrt(luma(color))) * 1.3 * (LIGHTS_ON ? WINDOW_SHADES_ON_COLOR : WINDOW_SHADES_OFF_COLOR) * shade_col, step(shades, STATE == 2 ? -0.2 : 0.95));
}

void frame()
{
    float w = 0.15;
    float h = 0.6;
    col = vec3(0.5) * (0.8 + 0.4 * fbm(uv * 12.0, 8));

    vec2 luv = uv;
    uv = abs(uv);

    //Lighting
    float l = 0.2;
    float b = 0.2;
    float f = float(uv.y <= 1.0 - w);

    col += sqrt(smoothstep(1.0 - w * l, 1.0, abs(uv.y))) * b;
    col += float(uv.y >= 1.0 - w && uv.x <= A - w * h + w * l && (uv.x >= A * 0.6 - w * h * l || uv.x <= A * 0.6 - w * h + w * h * l)) * sqrt(smoothstep(1.0 - w + w * l, 1.0 - w, uv.y)) * b;

    col += sqrt(smoothstep(A - w * l, A, uv.x)) * b;  
    col += f * float(uv.x >= A - w * h) * sqrt(smoothstep(A - w * h + w * l, A - w * h, uv.x)) * b;

    col += f * float(uv.x <= A * 0.6) * sqrt(smoothstep(A * 0.6 - w * h + w * l * 0.8, A * 0.6 - w * h, uv.x)) * b;
    col += f * float(uv.x <= A * 0.6) * sqrt(smoothstep(A * 0.6 - w * l * 0.8, A * 0.6, uv.x)) * b;   

    //Shadow rectangles
    vec3 lcol = col;
    col = color * 0.6;
    float s = 0.3;
    rect(-A, 1.0 - w * (1.0 + s / A), 2.0 * A, w * h * (1.0 + s * 2.0));
    rect(A - w * h * (1.0 + s), -1.0, w * h * (1.0 + s * 2.0), 2.0);  
    rect(A * 0.6 - w * h * (1.0 + s), -1.0, w * h * (1.0 + s * 2.0), 2.0);
    col = color * 0.6;
    s *= 0.63;
    rect(-A, 1.0 - w * (1.0 + s / A), 2.0 * A, w * h * (1.0 + s * 2.0));
    rect(A - w * h * (1.0 + s), -1.0, w * h * (1.0 + s * 2.0), 2.0);  
    rect(A * 0.6 - w * h * (1.0 + s), -1.0, w * h * (1.0 + s * 2.0), 2.0);

    col = min(lcol, lcol + b);

    //Frame   
    rect(-A, 1.0 - w, 2.0 * A, w);
    rect(A - w * h, -1.0, w * h, 2.0);
    rect(A * 0.6 - w * h, -1.0, w * h, 2.0);  

    uv = luv; 
}

void window()
{
    background();
    shades();
    frame();
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 index = floor(fragCoord * TILES / iResolution.xy);
    fragCoord = mod(fragCoord * TILES, iResolution.xy);
    uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    STATE = int(noise(index + iTime * 0.5) * 3.0);
    window();   

    fragColor = vec4(color, 1.0);
}

// Black Hole Raycasting

const float pi = 3.1415926536;

mat2 rotate(float a) {
    float sinA = sin(a);
    float cosA = cos(a);
    return mat2(cosA, -sinA, sinA, cosA);
}

vec3 sky(vec3 rayDir) {
    //vec3 background = 10. * vec3(rayDir.xy, 0.);
    vec3 background;
    background = texture(iChannel0, 1. * vec2(atan(rayDir.z, rayDir.x), rayDir.y)).rrr;
    background = 1.5 * pow(background, vec3(25.));
    background += .1 * pow(texture(iChannel1, vec2(atan(rayDir.z, rayDir.x), rayDir.y)).rgb, vec3(2.));
    //background = 2. * pow(background, vec3(2.2));
    return background * pow(length(rayDir.xz), 2.0);
}

// A pattern I made for the accretion disk.
vec3 diskTexture (vec2 pos) {
    float D = length(pos);
    float F = min(1., 30. * (D - .95/6.2));
    F *= F;
    D = 1. - D;
    D *= min(1., 10. * D);
    float D0 = D;
    D = pow(D, 1.5);
    D += D0 * D0 * D0 * 0.08 * smoothstep(0., 1., texture(iChannel0, rotate(8. * iTime) * pos/10. + 0.05 * vec2(iTime)).r);
    float atanvalue = atan(pos.y, pos.x);
    float sinPatterns = sin(-80. * D + 5. * iTime + -1. * atanvalue) + 0.3 * sin(-152. * D + 3. * iTime + -1. * atanvalue) + 0.2 * sin(-134. * D + 10. * iTime + -1. * atanvalue);
    //vec3 diskCol = vec3(0.1, 0.7, 1.); // Cyan
    //vec3 diskCol = vec3(1., 0.45, .2); // Orange
    float colorShift = -iTime / 2. + 3.;
    colorShift += 4. * D0;
    vec3 diskCol = 0.5 * vec3(sin(colorShift) + 1., sin(colorShift + 2. * pi/3.) + 1., sin(colorShift + 4. * pi/3.) + 1.);
    diskCol = mix(diskCol, vec3(1.), 0.45);

    diskCol = pow(diskCol, vec3(2.2));
    return F * D * (0.5 * abs(sinPatterns) + 0.5) * 8. * diskCol;
}

float plane (vec3 rayPos, float y) {
    return abs(rayPos.y - y);
}

vec3 ray (vec3 rayPos, vec3 rayDir) { // Might be better described as a "photon" instead of a ray
    vec3 cam = rayPos;
    float moveDist = 1.;
    for (int i = 0; i < 600; i++) {
        rayDir = normalize(rayDir - 10. * 0.5 * moveDist * normalize(rayPos)/(20. * length(rayPos) * length(rayPos)) ); // Gravity

        bool diskBool = false || (length(rayPos.xz) < 6. && length(rayPos.xz) > .95);
        bool diskBool2 = false || (length(rayPos.xz) < 6. && length(rayPos.xz) > .95);

        float minDist = plane(rayPos, 0.) - 0.04;
        moveDist = min(minDist + float(!diskBool2), .1);
        //moveDist = .1;
        rayPos += 0.5 * moveDist * rayDir;

        if (diskBool && minDist < 0.01) {
            return diskTexture(rayPos.xz/6.2);
        }

    }
    vec3 sky = mix(sky(rayDir), sky(rayDir.yzx), 0.5);
    return float(length(rayPos) > 10.) * sky;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Screen coordinates
    vec2 screen = (fragCoord - iResolution.xy/2.)/min(iResolution.x, iResolution.y);

    vec3 col;

    // texture test
    col = diskTexture(screen.xy);

    vec2 mouse = iMouse.xy/iResolution.xy - vec2(0.5);

    if (mouse == vec2(-0.5)) {
        mouse = vec2(0., -.02);
    }

    vec3 cam = vec3(0, 0, -11);
    vec3 rayDir = normalize(vec3(screen.xy, 1.2));

    cam.yz *= rotate(pi * -mouse.y);
    cam.xz *= rotate(2. * pi * -mouse.x);
    //rayDir.xy *= rotate(-.4);
    rayDir.yz *= rotate(pi * -mouse.y);
    rayDir.xz *= rotate(2. * pi * -mouse.x);

    col = ray(cam, rayDir);
    col *= 1.5; col = vec3(tanh(col.r), tanh(col.g), tanh(col.b)); // Lazy tonemapping

    // Output to screen
    fragColor = vec4(pow(col, vec3(1./2.2)), 1.0);
}

vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)) );



	return -1.0 + 2.0*fract(sin(p)*43758.5453123   ) ;
}


float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y)*K1 );

  //  i+=+ iTime*.01;

    vec2 a = p - i + (i.x+i.y)*K2;
    vec2 o = step(a.yx,a.xy) ;    
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2 ;

    vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

    return dot( n, vec3(70.0) );

}

