void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (fragCoord-iResolution.xy/2.)/iResolution.y;
    float unit = 1./iResolution.y;

    // timing
    float time = iTime*10.;
    float anim = fract(time);
    float index = floor(time);

    // displace
    vec2 rnd1 = hash21(index)*2.-1.;
    vec2 target = p + rnd1;
    float mask = hash12(floor(uv*hash11(index+78.)*32.)+index);
    float a = 6.283*floor(hash11(index*72.)*4.)/4.;
    vec2 dir = vec2(cos(a),sin(a));
    vec2 offset = (mask) * dir * unit * 10. * hash12(floor(uv*16.));

    // spawn frame
    bool spawn = hash12(floor(uv*hash11(index+78.)*8.)+index) > 0.9;
    if (iFrame < 1 || spawn)
    {
        fragColor = texture(iChannel1, uv);
    }
    else
    {
        // frame buffer
        fragColor = texture(iChannel0, uv-offset);
    }
}

