void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    const float BORDER_DROPOFF = 20.0,
                BORDER_SIZE = 0.5;
    const int N_SEGMENTS_ON_SIDE = 3;
    const float SEGMENT_SIZE = 1.0 / float(N_SEGMENTS_ON_SIDE);
    vec2 segmentPointer = uv;
    if (iMouse.z > 0.5)
        segmentPointer = iMouse.xy / iResolution.xy;
    vec2 segmentPos = fract(segmentPointer / SEGMENT_SIZE);
    vec2 segmentBorder2D = abs(segmentPos - 0.5);
    float segmentBorder = 1.0 - max(border(segmentBorder2D.x, 0.5, BORDER_SIZE, BORDER_DROPOFF),
                                    border(segmentBorder2D.y, 0.5, BORDER_SIZE, BORDER_DROPOFF));

    ivec2 segment = ivec2(segmentPointer / SEGMENT_SIZE);
    vec2 panning = iTime * vec2(0.4, 0.2);
    vec2 noisePos = uv * 10.0 + panning;

    //Note that segment's Y index increases as you go upwards.
    float noise = 0.0;
    //Column 1: plain noise.
    if (segment == ivec2(0, 0))
    {
        noise = perlinNoise(noisePos, 1.430);
    }
    else if (segment == ivec2(0, 1))
    {
        noise = perlinNoise(noisePos, 1.430);
        noise = pow(2.0 * abs(noise - 0.5), 0.75);
    }
    else if (segment == ivec2(0, 2))
    {
        noise = perlinNoise(noisePos, 1.430);
        noise *= noise;
    }
    //Column 2: octave noise.
    else if (segment == ivec2(1, 0))
    {
        IMPL_OCTAVE_NOISE(
            noisePos,
            octaveNoise,
            2.0, 1.430, 5,
            perlinNoise,
            ,

        );
        noise = octaveNoise;
    }
    else if (segment == ivec2(1, 1))
    {
        IMPL_OCTAVE_NOISE(
            noisePos,
            octaveNoise,
            2.0, 1.430, 5,
            perlinNoise,
            ,
            octaveValue = pow(2.0 * abs(octaveValue - 0.5), 0.75)
        );
        noise = octaveNoise;
    }
    else if (segment == ivec2(1, 2))
    {
        IMPL_OCTAVE_NOISE(
            noisePos,
            octaveNoise,
            2.0, 1.430, 5,
            perlinNoise,
            ,
            octaveValue *= octaveValue
        );
        noise = octaveNoise;
    }
    //Column 3: Interesting experiments.
    else if (segment == ivec2(2, 0))
    {
        IMPL_OCTAVE_NOISE(
            noisePos,
            noiseA,
            2.0, 1.430, 5,
            perlinNoise,
            ,
            octaveValue *= octaveValue
        );
        IMPL_OCTAVE_NOISE(
            noisePos,
            noiseB,
            2.0, 0.825, 5,
            perlinNoise,
            ,
            octaveValue = pow(2.0 * abs(octaveValue - 0.5), 0.75)
        );
        noise = pow(noiseA, noiseB);
    }
    else if (segment == ivec2(2, 1))
    {
        IMPL_OCTAVE_NOISE(
            noisePos,
            noiseA,
            2.0, 1.430, 5,
            perlinNoise,
            ,
            octaveValue *= octaveValue
        );
        IMPL_OCTAVE_NOISE(
            noisePos,
            noiseB,
            2.0, 0.825, 5,
            perlinNoise,
            ,
            octaveValue = pow(2.0 * abs(octaveValue - 0.5), 0.75)
        );
        noise = min(noiseA, noiseB) / max(noiseA, noiseB);
        noise = 1.0 - noise;
    }
    else if (segment == ivec2(2, 2))
    {
        IMPL_OCTAVE_NOISE(
            noisePos * 0.25,
            noiseA,
            2.0, 1.430, 5,
            perlinNoise,
            ,
            octaveValue = pow(2.0 * abs(octaveValue - 0.5), 0.75)
        );
        IMPL_OCTAVE_NOISE(
            noisePos / pow(noiseA, 0.01),
            noiseB,
            2.0, 0.825, 5,
            perlinNoise,
            ,
            octaveValue *= octaveValue
        );
        noise = noiseB;
    }

    float saturation = step(segmentPos.x, 0.5);
    vec3 col = mix(vec3(noise),
                   texture(iChannel0, vec2(0.5 + (noise * 0.0625))).rgb,
                   saturation);
    fragColor = vec4(col * segmentBorder, 1.0);
}

