// doing some simple bounds testing (for voxel engine)
// cut sphere distance funcs by IQ:
// https://www.shadertoy.com/view/7tVXRt
// https://www.shadertoy.com/view/stKSzc

float sdCutHollowSphere( vec3 p, float r, float h, float t )
{
    vec2 q = vec2( length(p.xz), p.y );
    float w = sqrt(r*r-h*h);
    return ((h*q.x<w*q.y) ? length(q-vec2(w,h)) : 
                            abs(length(q)-r) ) - t;
}

float sdCutSphere( in vec3 p, in float r, in float h )
{
    float w = sqrt(r*r-h*h); // constant for a given shape
    vec2 q = vec2( length(p.xz), p.y );
    float s = max( (h-r)*q.x*q.x+w*w*(h+r-2.0*q.y), h*q.x-w*q.y );
    return (s<0.0) ? length(q)-r :
           (q.x<w) ? h - q.y     :
                     length(q-vec2(w,h));
}

float sdBoundingBox( vec3 p, vec3 b, float e)
{
  p = abs(p)-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

// pos, rad, signed cutvalue (-1.0 to 1.0), hollow flag
float cutspheretest(vec3 pos, float rad, float scut, bool hollow)
{
    //scut = sin(iTime*0.5); // test cut
    float s = scut<0.0 ? -1.0 : 1.0;
    scut = abs(scut);

    float plane = -((scut-0.5)*(rad*2.0));
    float d = 0.0;
    float thick = 0.0;
    if (hollow)
    {
        thick = .1;
        d = sdCutHollowSphere(pos*s,rad,plane,thick);
    }
    else
    {
        d = sdCutSphere(pos*-s,rad,-plane);
    }

    // bounding box distance
    float h = (plane+rad)*0.5;
    float yoff = rad-h;
    yoff*=s;

    vec3 bbmid = vec3(0.0,yoff,0.0);
    vec3 bbsize = vec3(rad,h,rad)+thick;
    float d2 = sdBoundingBox(pos+bbmid,bbsize,0.025);
    return min(d,d2);
}


float map( in vec3 pos )
{
    float rad = 2.0;
    float scut = sin(iTime*0.9); // signed normalized cut value
    float d1 = cutspheretest(pos+vec3(2.5,0.0,0.0),rad,scut,false);
    scut = sin(iTime*0.6); // signed normalized cut value
    float d2 = cutspheretest(pos+vec3(-2.5,0.0,0.0),rad,scut,true);
    return min(d1,d2);
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

#define AA 2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.5*iTime;

    float yy = 4.0;
    if (iMouse.z>0.5)
        yy=0.0;

	vec3 ro = vec3( 9.*cos(an), yy, 9.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        // raymarch
        const float tmax = 30.0;
        float t = 0.0;
        for( int i=0; i<128; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }

        // shading/lighting
        float v = 1.0-abs(p.y);
        vec3 col = vec3(v*0.1);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.7,0.6,0.4)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,0.8,0.6));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.8,0.7,0.5)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

// Dreamy Geometry by Leon Denise 2022-06-28
// using code from Inigo Quilez, Dave Hoskins, Blackle, NuSan and many more

// Softracing geometry sketch using temporal accumulation to trace motion
// trying a trick of floored hashed seeds to duplicate shapes
// a remix of Taste of Noise 7 https://www.shadertoy.com/view/NddSWs

// code is in Buffer A
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

