float delay = 15.;

float anim, aanim, seed, index, ttime, rng1, rng2;
vec3 rng, rndMove1, rndMove2, rndMove3, rndMove4, rndScale1, cam;
vec4 rndRot1, rndRot2;

float map (vec3 pos)
{
    vec3 p = pos;
    float dist = 100.;
    float shape = 100.;

    // expansion anim
    float r = .01*rng.x+.4*aanim;
    float t = r*.1+index+aanim;

    // twist and fold
    const float count = 3.;
    float a = 1.;
    for (float index = 0.; index < count; ++index)
    {
        p.xy *= rot(t/a);
        p.xz *= rot(t/a);
        p = abs(p)-r*a;
        a /= 2.8;
    }

    // shapes
    dist = length(p)-0.01; // curves
    dist = smin(dist, length(pos)-.1, .1); // kernel
    shape = max(abs(length(pos)-.5)-.01, pos.z-.1); // cocoon
    shape = min(shape, length(pos+rndMove1*.8)-.1*rng1); // spheres

    // tubes
    p = rndrot(pos, rndRot1) + rndMove2 * .5;
    shape = smin(shape, max(abs(p.y)-2.*aanim, length(p.xz)-.02), .2);

    // rings
    p += rndMove3 * 1.5;
    shape = smin(shape, sdTorus(p, vec2(.2,.01) * rng2), .2);

    // boxes
    p = rndrot(pos, rndRot2) + rndMove4 * 2.;
    shape = min(shape, sdBoxFrame(p, rndScale1*.5, .01));

    // blend
    dist = smin(dist, shape, .1);
    return dist;
}


// NuSan 
// https://www.shadertoy.com/view/3sBGzV
vec3 getNormal (vec3 pos, float e) {
    vec2 noff = vec2(e,0);
    return normalize(map(pos)-vec3(map(pos-noff.xyy), map(pos-noff.yxy), map(pos-noff.yyx)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // timeline
    ttime = iTime/delay;
    anim = fract(ttime);
    index = floor(ttime);
    aanim = pow(anim, .5);

    // randoms
    rng = hash33(vec3(gl_FragCoord.xy, iTime));
    seed = floor(rng.y*32.)+index*72.;
    rng1 = hash11(seed);
    rng2 = hash11(seed+763.);
    rndMove1 = normalize(tan(hash31(seed)*2.-1.));
    rndMove2 = hash31(seed+76.)*2.-1.;
    rndMove3 = hash31(seed+192.)*2.-1.;
    rndRot1 = hash41(seed)*2.-1.;
    seed = floor(rng.x*32.)+index*76.;
    rndMove4 = hash31(seed+32.)*2.-1.;
    rndScale1 = hash31(seed+65.);
    rndRot2 = hash41(seed)*2.-1.;
    cam = (hash31(index)*2.-1.)*.5 + vec3(0,0,4.5);
    vec3 blur = hash33(vec3(gl_FragCoord.xy, iTime))*2.-1.;

    // coordinates
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec3 at = vec3(0);
    vec3 ray = lookAt(cam, at, p, .5+.5*length(cam));
    blur *= 0.1 * ss(.5, 1., length(p));
    vec3 pos = cam + ray * rng.z + blur;

    // ray shooting
    float shade = 0.;
    const float count = 30.;
    for (shade = 1.; shade > 0.; shade -= 1./count) {
        float dist = map(pos);
        if (dist < .001) break;
        dist *= .5 + 0.5 * rng.z;
        pos += ray *dist;
    }

    // coloring
    vec3 color = vec3(1);
    vec3 normal = getNormal(pos, .001);
    float angle = abs(dot(ray, normal));
    color = .5+.5*cos(vec3(1,2,3)*2.+pos*3.+normal);
    color = mix(color, vec3(1), angle);
    color *= shade*shade;

    // fade in
    color *= ss(.0,1./delay,anim);

    // temporal accumulation
    vec4 frame = texture(iChannel0, uv);
    color = max(frame.rgb, color);

    // fade out
    if (anim * delay > delay - 1.)
    {
        color = frame.rgb;
        color -= iTimeDelta;
    }

    fragColor = vec4(color, 1);
}

