int leftFoot;
int rightFoot;
int rearFoot;
int saddle;

void connectHorble()
{
    // animation blend states
    float t = .5+.5*cos(iTime/2.0); t*1.;
    float u = .5+.5*cos(iTime/3.2); u*=.5;
    float v = smoothstep(0.2,0.3,cos(iTime/2.7)); v*=1.;
    float w = smoothstep(0.5,0.6,cos(iTime/4.2+2.0)); w*=1.;
    float x = cos(iTime*12.); x*=smoothstep(0.5,1.0,cos(iTime/2.3));
    float y = .5+.5*cos(iTime*2.); y*=smoothstep(0.73,1.0,cos(iTime/3.9));
    u += .15 * y;

    float bend;

    // first ball
    root(vec3(0.0, 0.0, 0.0), vec2(0.00 + fade(.1,u), 0.53 + fade(.02,t) + fade(-.020,u)), 0.10); turn(radians(5. + fade(-6.,u)));

    // right leg
    bend = 40. + fade(-2.,t) + fade(1.5,u);
    connect(vec3(0.70, 0.50, 0.40), radians(200. + fade(3.,t) + fade(-7.,u)), 0.05);
    connect(vec3(0.80, 0.20, 0.00), radians(bend), 0.05);
    connect(vec3(0.70, 0.01, 0.00), radians(bend), 0.05);
    connect(vec3(0.60, 0.10, 0.00), radians(bend*1.2), 0.05);
    connect(vec3(0.60, 0.00, 0.00), radians(bend*-.5), 0.05);
    connect(vec3(0.50, 0.01, 0.00), radians(bend*-.5), 0.06); rightFoot = current;
    up(6);

    // back
    bend = -20.;
    connect(vec3(0.70, 0.10, 0.00), radians(-bend + fade(-2.,t)), 0.12); saddle = current;
    connect(vec3(0.70, 0.00, 0.00), radians(2.*bend), 0.10); turn(radians(-bend)); 

    // tail
