struct Circle
{
    int parent;
    vec3 color;
    vec2 center;
    float radius;
    float angle;
};

const int N = 3 + (3+1) + (3+(1+(1+2+1+1))) + 3 * (5+1);

int free, current;
Circle[N] circles;

mat2 R(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}
vec2 v(float a)
{
    return vec2(1, 0) * R(a);
}
void root(vec3 color, vec2 center, float radius)
{
    free = 0;
    circles[free] = Circle(free, color, center, radius, 0.);
    current = free++;
}
void connect(vec3 color, float angle, float radius)
{
    vec2 center = circles[current].center;
    angle += circles[current].angle;
    center += v(angle) * (circles[current].radius + radius);
    circles[free] = Circle(current, color, center, radius, angle);
    current = free++;
}
void move(float dx, float dy)
{
    circles[current].center += vec2(dx, dy) * R(circles[current].angle);
}
void turn(float da)
{
    circles[current].angle += da;
}
void up(int n)
{
    for (int i=0 ; i<n ; ++i)
        current = circles[current].parent;
}

float fade(float target, float x) { return mix(0., target, x); }

float f;
vec4 blend(vec4 C, vec3 c, float r)
{
    return mix(C, vec4(c, 1), clamp(r + .5, 0., 1.));
}

float l2(vec2 x)
{
    return dot(x, x);
}

float l2(vec3 x)
{
    return dot(x, x);
}

