// 2022.06.27 rev 1

#define PI 3.14159265

// https://www.wolframalpha.com/input?i=f%28x%29%3D0.5%2B%28cos%28%28%28x%2B0.5%2F1%29*20*1%29%2F%28PI%29%29%2F2.%29
float cycle(float perSecond) {
    // let n be the ammount of cycles per second
    // f(x)=0.5 + (cos(((x + 0.5/ n ) * 20 * n ) / (PI)) / 2.)
    return 0.5+(cos(((iTime+0.5/perSecond)*20.*perSecond)/(PI))/2.);
}

float horizontalCycle() {
    return 0.5 + (cycle(0.34) * 0.5) - 0.25;
}

float diagonalLine(vec2 uv, float width) {
    return step(distance(uv.x, uv.y), width);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    // Now, the coord system is 0 to 1
    // [0, 0] = left bottom corner
    // [1, 1] = top right corner

    // I had a huge problem trying to discover how to mix the middle
    // screen divided colors with the line
    // I noticed that I can use the step function to "exclude" what should be
    // drawn to the screen
    //
    // red is only 1, when l is 1 AND (through a multiplication) is above the h threshould
    // h is like the middle of the screen, but animated between 0.25 and 0.75
    //
    // green is only 1, when l is 1 AND (through a multiplication) is below the h threshould
    // h is like the middle of the screen, but animated between 0.25 and 0.75
    //
    // blue is only 1, when l is 0 through a abs invert of abs(1-1) -> 0 and abs(0-1) -> 1

    float l = diagonalLine(uv, (0.1 + (cycle(0.233) * 0.1)));
    float h = horizontalCycle();

    l = step(l, uv.x);
    fragColor = vec4(
        l * step(h, uv.x),
        l * step(uv.x, h), 
        abs(1.-l), 
        1
    );

    // cute bug :)
//        fragColor = vec4(
//            max(step(horizontalCycle(), l), uv.x),
//            max(uv.x, step(l, horizontalCycle())), 
//            0, 
//            1
//        );

}

