#define SURF_DIST .01
#define a .12

mat3 rotX(float angle){
    return mat3(1., 0., 0.,0., cos(angle), -sin(angle),0., sin(angle), cos(angle));
}

mat3 rotY(float angle){
    return mat3(cos(angle), 0., sin(angle), 0., 1., 0. ,-sin(angle), 0., cos(angle));
}

mat3 rotZ(float angle){
    return mat3(cos(angle), -sin(angle), 0., sin(angle), cos(angle), 0. ,0., 0., 1.);
}


float N11(float p){
	return fract(sin(p*123.230+p*p*2345.36)*256.753);
}

vec3 N13(float p){
    float t = iTime*2.5;
    vec3 r = vec3(sin(N11(p+.255)*235.235+t*2.),sin(N11(p-.12432)*123.234+t*1.12423),sin(N11(p*2.)*546.36+t*3.));
    return (0.7 - (r*.5 + .5) * 0.5);
}

float GetDist(vec3 p){

    float id = floor(p.x*.015/a);

    p.x = fract((p.x+a)*a)/a-4.3;

    p.y += 2.;
    //p.y = (p.y - fract((p.y+iTime*4.)/20.));


    float dist = 21.;


    p -= vec3(0.,0.,dist);
    p *= rotX(3.141592653589793238464*0.5);
    //p *= rotZ(iTime*4.23);
    //p *= rotX(iTime*3.9786);
    p += vec3(0.,2.,dist);


    float dS1 = length(p-vec3(1,0,dist))-1.;
    float dS2 = length(p-vec3(-1,0,dist))-1.;
    float y2 = min(max(p.y,1.),4.);
    float dC = length(p-vec3(0,y2,dist))-1.;
	return(min(min(dS1,dS2),dC));
}

float RayMarch(vec3 ro, vec3 rd){
	float dO = 0.;
    for(int i = 0; i <= MAX_STEPS; i++){
        vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += dS;
        if(dS < SURF_DIST || dO > MAX_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 5, 0);

    lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l);
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}

