// License CC0: checkerboard distance field
// Needed a checkerboard so threw this together. 
// Checkerboard can be created by sign(p.x*p.y) but
// the issue is the lines might look jagged once 
// rotating the board
#define TIME        iTime
#define RESOLUTION  iResolution

#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

float checkerboard(vec2 p) {
  p = fract(p)-0.5;
  float s = sign(p.x*p.y);
  p = abs(abs(p)-0.25);
  return s*(max(p.x, p.y) - 0.25);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec2 op = p;
  p *= ROT(0.2*TIME);

  const float sz = 2.0;
  float aa = sz*2.0/RESOLUTION.y; 

  float d = checkerboard(p*sz);

  vec3 col = mix(vec3(0.0), vec3(0.5), smoothstep(aa, -aa, d));
  if (op.x > 0.0) col += 0.5*vec3(0.0, 0.5, 1.0)*sin(TAU*d/(10.0*aa));
  col = sqrt(col);
  fragColor = vec4(col, 1.0);
}

