    vec3 on = vec3(1.4, 1.3, 1.15);
    col = mix(off, on, brightness);
    circle(x, y, s + s * brightness * 0.2, brightness * 1.0 + 0.03);
}

void grid()
{
    float s = 0.04;
    float a = iResolution.x / iResolution.y;
    for(float x = -a + s * a; x <= a + s * a; x += s * 4.0)
    {
        for(float y = -1.0 + s * 2.0; y <= 1.0 + s; y += s * 5.0)
        {
            float brightness = noise((x * (a + s * a) + y) * 617.2 + iTime);
            bulb(x, y, s, brightness);
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y ;

    color = vec3(0.84, 0.81, 0.80);
    color += (noise(uv * 16.0) * 0.5 + noise(uv * 32.0) * 0.3 + noise(uv * 64.0) * 0.2) * 0.12 - 0.06;

    grid();

    color = pow(color, vec3(1.5));

    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    float p = cos(uv.x*100.);
    float c = sin(uv.y*100.);
    p = max(sign(p-0.95),0.);
    c = max(sign(c-0.95),0.);
    fragColor = vec4(vec3(c)+vec3(p),1.);
}

// Perlin Noise

float hash12(vec2 p) // Hash by Dave_Hoskins
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 randUnitVector(vec2 pos) {
    float angle = 2. * 3.1415926536 * hash12(pos);
    return vec2(cos(angle), sin(angle));
}

float perlin(vec2 pos) {
    // Grid cell corner points
    vec2 p00 = floor(pos);
    vec2 p01 = p00 + vec2(0, 1);
    vec2 p11 = p00 + vec2(1, 1);
    vec2 p10 = p00 + vec2(1, 0);

    // Random vectors at corner points
    vec2 r00 = randUnitVector(p00);
    vec2 r01 = randUnitVector(p01);
    vec2 r11 = randUnitVector(p11);
    vec2 r10 = randUnitVector(p10);

    // Dot product between random vector and distance vector
    float d00 = dot(r00, pos - p00);
    float d01 = dot(r01, pos - p01);
    float d11 = dot(r11, pos - p11);
    float d10 = dot(r10, pos - p10);


    // Bicubic interpolation
    vec2 blend = smoothstep(0., 1., fract(pos));

    float upper = float(mix(d01, d11, blend.x));
    float lower = float(mix(d00, d10, blend.x));
    return 0.8 * mix(lower, upper, blend.y) + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    vec3 col = vec3(uv, 0.);
    col = vec3(perlin(10. * uv + 0.8 * iTime));

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Create your own shader using GLSL Shader Language!
void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    float dy = 5.0 / iResolution.y;
    // xy is LR Frequency domain data
    vec2 fv = texture(iChannel0, vec2(uv.x, 0.5)).xy;
    // zw is LR Time domain data
    vec2 tv = texture(iChannel0, vec2(uv.x, 0.5)).zw;
    vec3 col = vec3(0.);

    // Time domain data
    if (!(uv.y + dy < tv.x) && (uv.y - dy < tv.x)) {
        // Color the pixel that crosses the time domain sample value
        col += 100.;
    }

    // Frequency domain data
    if (uv.y < fv.x) {
        col.gb += fv.x + 0.4;
    }

    fragColor = vec4(col,5);
}

#define STEPS 256
#define OCC_STEPS 32
#define EPSILON 0.01

//////////// 3d Math //////////////

vec3 scaleTo(vec3 v, float length) {
  return normalize(v) * length;
}

float map(float numer, float denom, float low, float high) {
  float span = high - low;
  return (numer / denom * span) + low;
}

mat3 rotX(float rads) {
  return mat3(
    1., 0.,         0.,
    0., cos(rads), -sin(rads),
    0., sin(rads),  cos(rads)
  );
}

mat3 rotY(float rads) {
  return mat3(
    cos(rads), 0., -sin(rads),
    0.,        1.,  0.,
    sin(rads), 0.,  cos(rads)
  );
}

mat3 rotZ(vec3 p, float rads) {
  return mat3(
    cos(rads), -sin(rads), 0.,
    sin(rads),  cos(rads), 0.,
    0.,         0.,        1.
  );
}

float max3(vec3 v) {
  return max(max(v.x, v.y), v.z);
}

//////////// end 3d Math //////////////

//////////// SDFs //////////////

// https://iquilezles.org/articles/distfunctions/
float sdPlane(vec3 p, vec3 n, float h) {
  // n must be normalized
  return dot(p,n) + h;
}

float sdSphere(vec3 p, float r) {
  return length(p) - r;
}

// https://iquilezles.org/articles/distfunctions/
float sdRoundCone(vec3 p, float r1, float r2, float h) {
  // sampling independent computations (only depend on shape)
  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);

  // sampling dependant computations
  vec2 q = vec2( length(p.xz), p.y );
  float k = dot(q,vec2(-b,a));
  if( k<0.0 ) return length(q) - r1;
  if( k>a*h ) return length(q-vec2(0.0,h)) - r2;
  return dot(q, vec2(a,b) ) - r1;
}

// https://iquilezles.org/articles/smin/
float smin(float a, float b, float k) {
    float h = max(k-abs(a-b), 0.) / k;
    return min(a, b) - h*h*k*(1./4.);
}

//////////// end SDFs //////////////

////////////// Scene //////////////////

const vec3 bearColor = vec3(.4, .2, .05);

struct RenderData {
  vec3 c;  // color
  float d; // distance
  float s; // specularity
};

RenderData minRd(RenderData a, RenderData b, float k) {
  RenderData rd;
  rd.d = smin(a.d, b.d, k);
  if (a.d < b.d) {
    rd.c = a.c;
    rd.s = a.s;
  } else {
    rd.c = b.c;
    rd.s = b.s;
  }
  return rd;
}

RenderData minRd(RenderData a, RenderData b) {
  return minRd(a, b, .7);
}

bool groundTile(vec3 p) {
  p /= 5.0;
  int xSign = p.x > 0.0 ? 1 : 0;
  int zSign = p.z > 0.0 ? 1 : 0;
  return ((int(p.x) ^ int(p.z) ^ xSign ^ zSign) & 1) == 1;
}

RenderData ground(vec3 p) {
  RenderData rd;
  rd.d = sdPlane(p, vec3(0., 1., 0.), 0.);
  rd.c = groundTile(p) ?
           vec3(.0, .1, .0) :
           vec3(.1, .2, .0);
  rd.s = 42.;
  return rd;
}

RenderData head(vec3 p) {
  RenderData rd;
  rd.d = sdSphere(p - vec3(0., 10, 0.), 3.);
  rd.c = bearColor;
  rd.s = 8.;
  return rd;
}

RenderData ears(vec3 p) {
  RenderData rd;
  p.x = abs(p.x);
  rd.d = sdSphere(p - vec3(3., 12, 0.), 1.);
  rd.c = bearColor;
  rd.s = 8.;
  return rd;
}

RenderData body(vec3 p) {
  RenderData rd;
  rd.d = sdRoundCone(p - vec3(0., 3.5, 0.), 2., 1., 3.);
  rd.c = bearColor;
  rd.s = 8.;
  return rd;
}

RenderData render(vec3 p) {
  RenderData rd = ground(p);
  rd = minRd(head(p), rd);
  rd = minRd(ears(p), rd);
  rd = minRd(body(p), rd);
  return rd;
}

float f(vec3 p) {
  return render(p).d;
}

// https://iquilezles.org/articles/normalsSDF/
vec3 calcNormal(vec3 p) {
    const float eps = 0.0001;
    const vec2 h = vec2(eps, 0);
    return normalize(
      vec3(f(p+h.xyy) - f(p-h.xyy),
           f(p+h.yxy) - f(p-h.yxy),
           f(p+h.yyx) - f(p-h.yyx))
    );
}

RenderData raycast(inout vec3 pos, in vec3 ray) {
  RenderData rd;
  for (int i = 0; i < STEPS; ++i) {
    rd = render(pos);
    if (rd.d < EPSILON) {
      break;
    }
    pos += scaleTo(ray, rd.d * .7);
  }
  return rd;
}

float obstruction(vec3 pos, vec3 ray, float k) {
  float res = 1.;
  float d = 0.1;
  for (int i = 0; i < OCC_STEPS; ++i) {
    RenderData rd = render(pos + scaleTo(ray, d));
    if (rd.d < EPSILON) {
      return 0.0;
    }
    res = min(res, k*rd.d/d);
    d += max(rd.d, EPSILON);
  }
  return res;
}

vec3 lighting(vec3 pos, vec3 camera, vec3 lightVector, vec3 lightIntensity, float specularIntensity) {
  vec3 normal = calcNormal(pos);

  float obVal = obstruction(pos, -lightVector, 16.);
  vec3 diffuse = max(dot(normal, -lightVector), 0.) * lightIntensity * obVal;

  vec3 cameraVector = normalize(camera - pos);
  float reflection = max(dot(cameraVector, reflect(lightVector, normal)), 0.);
  vec3 specular = lightIntensity * pow(reflection, specularIntensity) * specularIntensity / 32.;
  return diffuse + specular;
}

////////////// end Scene //////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    float rads = iTime / 6.0;
    mat3 rot = rotY(rads) * rotX(sin(rads/1.9)*.2+.2) * mat3(1.);
    vec3 camera = rot * vec3(0.0, 10.0, 30.0);
    vec3 xUnit = rot * vec3(1.0, 0.0, 0.0);
    vec3 yUnit = rot * vec3(0.0, 1.0, 0.0);
    vec3 zUnit = rot * vec3(0.0, 0.0, 1.0);

    vec3 center = camera - zUnit;
    float dx = map(fragCoord.x, iResolution.x, -1.0, 1.0);
    float dy = map(fragCoord.y, iResolution.y, -1.0, 1.0);
    vec3 point = center + (dx*xUnit) + (iResolution.y/iResolution.x*dy*yUnit);
    vec3 ray = point - camera;

    vec3 pos = camera;
    RenderData rd = raycast(pos, ray);

    vec3 sky = vec3(.8, .9, 1.);
    vec3 ambientIntensity = vec3(.1, .2, .3);
    vec3 sunVector = normalize(vec3(-1.0, -1.0, -1.0));
    vec3 sunIntensity = vec3(.9, .8, .7);
    vec3 backLightVector = vec3(0., 0., 1.);
    vec3 backLightIntensity = vec3(.4);


    vec3 color = vec3(0.0);

    if (rd.d >= EPSILON) {
        color = sky;
    } else {
        color = ambientIntensity * rd.c;
        color += rd.c * lighting(pos, camera, sunVector, sunIntensity, rd.s);
        color += rd.c * lighting(pos, camera, backLightVector, backLightIntensity, rd.s);
    }

    // Gamma correction
    fragColor.rgb = pow(color, vec3(1./2.2));
}

