		Ray ray_tmp = ray;
		mat4 r = rotate_y(-0.4);
		ray_tmp.origin -= vec3(0.35, -1, -0.20);
		ray_tmp.dir = vec3(r * vec4(ray_tmp.dir, 0));
		ray_tmp.origin = vec3(r * vec4(ray_tmp.origin, 1.0));

