            CurrentHitHiddenUV = PositionToLightmap_G(ray_tmp, t, normal_tmp, i) + vec2(0.0,128.0);
		}
	}

#ifdef FULL_SCENE
	for(int i = 0; i < coordinates_2.length(); i++) {
		vec3 normal_tmp;

		Ray ray_tmp = ray;
		mat4 r = rotate_y(0.0);
		ray_tmp.origin -= vec3(0.1, -0.2, -1.0);
		ray_tmp.dir = vec3(r * vec4(ray_tmp.dir, 0));
