			if(all(lessThanEqual(p_tmp.yz, vec2(1))) && all(greaterThanEqual(p_tmp.yz,
							vec2(-1))))
			{
				normal = n;
				p = p_tmp;

				t_min = t;

				material = MAT_LEFT;
                CurrentHitHiddenUV = PositionToLightmap_L(p) + vec2(0.0,256.0);
			}
		}
	}
	// right
	{
		vec3 n = vec3(-1, 0, 0);
