			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThan(p_tmp.xz, vec2(1))) && all(greaterThan(p_tmp.xz,
							vec2(-1))))
			{
				normal = n;
				p = p_tmp;

				t_min = t;
				material = MAT_FLOOR;
                CurrentHitHiddenUV = PositionToLightmap_D(p) + vec2(0.0,384.0);
			}
		}
	}
	// ceiling
	{
		vec3 n = vec3(0, -1, 0);
		float t = intersect_plane(ray, vec3(0, 1, 0), n);
		if(t < t_min) {
			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThan(p_tmp.xz, vec2(1))) && all(greaterThan(p_tmp.xz,
							vec2(-1))))
			{
				normal = n;
				p = p_tmp;
				material = MAT_CEILING;

				t_min = t;
                CurrentHitHiddenUV = PositionToLightmap_U(p) + vec2(128.0,256.0);
			}
		}
	}
	// back wall
	{
		vec3 n = vec3(0, 0, 1);
		float t = intersect_plane(ray, vec3(0, 0, -1), n);
		if(t < t_min) {
			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThan(p_tmp.xy, vec2(1))) && all(greaterThan(p_tmp.xy,
							vec2(-1))))
			{
				normal = n;
				p = p_tmp;
				material = MAT_BACK;

				t_min = t;
                CurrentHitHiddenUV = PositionToLightmap_B(p) + vec2(256.0,256.0);
			}
		}
	}
	normal = normalize(normal);
    if(CurrentHitHiddenUV.x > 0.0)
    {
        vec3 albedo;
        switch(material) 
        {
	case MAT_LEFT   : albedo = vec3(0.9, 0.1, 0.1); break;
	case MAT_RIGHT  : albedo = vec3(0.1, 0.9, 0.1); break;
	case MAT_CEILING: albedo = vec3(0.7, 0.7, 0.7); break;
	case MAT_FLOOR  : albedo = vec3(0.7, 0.7, 0.7); break;
	case MAT_BACK   : albedo = vec3(0.7, 0.7, 0.7); break;
	case MAT_H      : albedo = vec3(1.0, 0.0, 0.0); break;
	case MAT_P      : albedo = vec3(0.0, 0.7, 0.7); break;
	case MAT_G      : albedo = vec3(0.1, 0.1, 0.7); break;
	case MAT_2      : albedo = vec3(0.8, 0.8, 0.8); break;
	default         : albedo = light_color[material - MAT_LIGHT0];color = albedo;return t_min; break;
	    }
        Hidden hidden = GetHidden(CurrentHitHiddenUV);
        if(material == MAT_LEFT || material == MAT_RIGHT || 
        material == MAT_CEILING || material == MAT_BACK || material == MAT_2)
        {
            color = Lightmap_Render_LRBU22(ray.dir, hidden);
            color = color + vec3(1.0,1.0,1.0);
            color = exp(color * 3.3994);
            color = color - vec3(1.0,1.0,1.0);
            color = color / 10.0;
            color = color * (albedo + vec3(0.5,0.5,0.5));
        }
        else if(material == MAT_FLOOR)
        {
            color = Lightmap_Render_Floor(ray.dir, hidden);
            color = color + vec3(1.0,1.0,1.0);
            color = exp(color * 3.71775);
            color = color - vec3(1.0,1.0,1.0);
            color = color / 10.0;
            color = color * (albedo + vec3(0.5,0.5,0.5));
        }
        else
        {
            color = Lightmap_Render_HPG(ray.dir, hidden);
            color = color + vec3(1.0,1.0,1.0);
            color = exp(color * 4.636095);
            color = color - vec3(1.0,1.0,1.0);
            color = color / 10.0;
            color = color * (albedo + vec3(0.5,0.5,0.5));
        }
    }
	return t_min;
}

