			if(all(lessThan(p_tmp.xz, vec2(1))) && all(greaterThan(p_tmp.xz,
							vec2(-1))))
			{
				normal = n;
				p = p_tmp;

				t_min = t;
                faceindex = 23 * 6 + 4 + 2;
				material = MAT_FLOOR;
                CurrentHitHiddenUV = PositionToLightmap_D(p) + vec2(0.0,384.0);
			}
		}
	}
	// ceiling
	{
		vec3 n = vec3(0, -1, 0);
		float t = intersect_plane(ray, vec3(0, 1, 0), n);
		if(t < t_min) {
			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThan(p_tmp.xz, vec2(1))) && all(greaterThan(p_tmp.xz,
							vec2(-1))))
			{
				normal = n;
				p = p_tmp;
				material = MAT_CEILING;

				t_min = t;
                faceindex = 23 * 6 + 4 + 3;
                CurrentHitHiddenUV = PositionToLightmap_U(p) + vec2(128.0,256.0);
			}
		}
	}
	// back wall
	{
		vec3 n = vec3(0, 0, 1);
		float t = intersect_plane(ray, vec3(0, 0, -1), n);
		if(t < t_min) {
			vec3 p_tmp = ray_at(ray, t);
			if(all(lessThan(p_tmp.xy, vec2(1))) && all(greaterThan(p_tmp.xy,
							vec2(-1))))
			{
				normal = n;
				p = p_tmp;
				material = MAT_BACK;

				t_min = t;
                faceindex = 23 * 6 + 4 + 4;
                CurrentHitHiddenUV = PositionToLightmap_B(p) + vec2(256.0,256.0);
			}
		}
	}
	//normal = normalize(normal);
    albedo = vec3(0.0);
    if(CurrentHitHiddenUV.x > 0.0)
    {
        switch(material) 
        {
	case MAT_LEFT   : albedo = vec3(0.9, 0.1, 0.1); break;
	case MAT_RIGHT  : albedo = vec3(0.1, 0.9, 0.1); break;
	case MAT_CEILING: albedo = vec3(0.7, 0.7, 0.7); break;
	case MAT_FLOOR  : albedo = vec3(0.7, 0.7, 0.7); break;
	case MAT_BACK   : albedo = vec3(0.7, 0.7, 0.7); break;
	case MAT_H      : albedo = vec3(1.0, 0.0, 0.0); break;
	case MAT_P      : albedo = vec3(0.0, 0.7, 0.7); break;
	case MAT_G      : albedo = vec3(0.1, 0.1, 0.7); break;
	case MAT_2      : albedo = vec3(0.8, 0.8, 0.8); break;
	default         : albedo = light_color[material - MAT_LIGHT0];color = albedo;return t_min; break;
	    }
        if(material == MAT_LEFT || material == MAT_RIGHT || 
        material == MAT_CEILING || material == MAT_BACK || material == MAT_2)
        {
            neuralmark = vec3(CurrentHitHiddenUV,0.0);
        }
        else if(material == MAT_FLOOR)
        {
            neuralmark = vec3(CurrentHitHiddenUV,1.0);
        }
        else if(material == MAT_H||material == MAT_P || material == MAT_G)
        {
            neuralmark = vec3(CurrentHitHiddenUV,2.0);
        }
        else
        {
            neuralmark = vec3(0.0,0.0,-1.0);
        }
    }
    else
    {
        neuralmark = vec3(0.0,0.0,-1.0);
    }
    faceid = faceindex;
	return t_min;
}

const float aaclamp = 1.0;
vec3 clampcolor(vec3 c)
{
    return c;//clamp(c,0.0,aaclamp);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	seed = iFrame * NUM_SAMPLES;
	flat_idx = int(dot(gl_FragCoord.xy, vec2(1, 4096)));
    vec2 texelsize = 1.0/vec2(iResolution);
	vec2 p = fragCoord.xy / vec2(iResolution) - vec2(0.5);
	float a = float(iResolution.x) / float(iResolution.y);
	if(a < 1.0)
		p.y /= a;
	else
		p.x *= a;

	//vec3 cam_center = vec3(0, 0, 3.0);
	vec3 cam_center = vec3(sin(iTime) * 0.25, sin(iTime * 0.7345) * 0.4 + 0.2, 6.0);
	vec3 cam_target = vec3(0, -0.1, 0);

	mat4 cam = transpose(look_at(cam_center, cam_target, vec3(0, 1, 0)));

	vec3 s = vec3(0);

    if(iFrame == 0)
    {
    	Ray ray;
		ray.origin = cam_center;
		vec2 r = get_random();
		vec3 ray_dir = normalize(vec3(p + r.x * dFdx(p) + r.y * dFdy(p), -2.5));
		ray.dir = vec3(cam * vec4(ray_dir, 0.0));
        vec3 c;
        vec3 normal;
        vec3 position;
        intersect_simple(ray, position, normal, c);
		s = c;
    }
    else
    {
        vec4 centerresult = texelFetch(iChannel1,ivec2(fragCoord),0);
        float eigen = centerresult.w;
        //float depth = fract(eigen)*10.0;
        int faceindex = int(floor(eigen));
        vec2 msaa_pattern[4] = vec2[4](
        vec2(-0.3162277660168379,0.9486832980505137),vec2(0.9486832980505137,0.3162277660168379),
        vec2(0.3162277660168379,-0.9486832980505137),vec2(-0.9486832980505137,-0.3162277660168379));
        int cnt = 0;
        int sourcecnt = 0;
        int addcnt = 0;
        for(int i=0;i<4;i++)
        {
            vec2 r = msaa_pattern[i]*0.4995;
            Ray ray;
            ray.origin = cam_center;
            vec3 ray_dir = normalize(vec3(p + r*texelsize, -2.5));
            ray.dir = vec3(cam * vec4(ray_dir, 0.0));
            vec3 c;
            vec3 normal;
            vec3 position;
            int cfaceid;
            vec3 neuralinfo;
            vec3 albedo;
            float cdepth = intersect_msaa(ray, position, normal, c, cfaceid, neuralinfo, albedo);
            if(cfaceid != faceindex - 1)// && cdepth <= depth)
            {
                vec3 color;//me write this like sorry QAQ
                {
                    //s += vec3(1.0,0.0,0.0);
                    //break;
                    if(neuralinfo.z < -0.5)
                    {
                        color = albedo;
                        s += clampcolor(color);
                        cnt++;
                        addcnt++;
                    }
                    else if(neuralinfo.z < 0.5)
                    {
                        Hidden hidden = GetHidden(neuralinfo.xy);
                        color = Lightmap_Render_LRBU22(ray.dir, hidden);
                        color = color + vec3(1.0,1.0,1.0);
                        color = exp(color * 3.3994);
                        color = color - vec3(1.0,1.0,1.0);
                        color = color / 10.0;
                        color = color * (albedo + vec3(0.5,0.5,0.5));
                        s += clampcolor(color);
                        cnt++;
                        addcnt++;
                        break;
                    }
                    else if(neuralinfo.z < 1.5)
                    {
                        Hidden hidden = GetHidden(neuralinfo.xy);
                        color = Lightmap_Render_Floor(ray.dir, hidden);
                        color = color + vec3(1.0,1.0,1.0);
                        color = exp(color * 3.71775);
                        color = color - vec3(1.0,1.0,1.0);
                        color = color / 10.0;
                        color = color * (albedo + vec3(0.5,0.5,0.5));
                        s += clampcolor(color);
                        cnt++;
                        addcnt++;
                        break;
                    }
                    else
                    {
                        Hidden hidden = GetHidden(neuralinfo.xy);
                        color = Lightmap_Render_HPG(ray.dir, hidden);
                        color = color + vec3(1.0,1.0,1.0);
                        color = exp(color * 4.636095);
                        color = color - vec3(1.0,1.0,1.0);
                        color = color / 10.0;
                        color = color * (albedo + vec3(0.5,0.5,0.5));
                        s += clampcolor(color);
                        cnt++;
                        addcnt++;
                        break;
                    }
                }
            }
            else
            {
                s += clampcolor(centerresult.xyz);
                cnt++;
                sourcecnt++;
            }
        }
        s += float(4- sourcecnt - addcnt) * centerresult.xyz;
        s /= 4.0;
    }
	fragColor = vec4(s,1.0);

    fragColor = pow(exposure * fragColor, vec4(1.0 / 2.2));


}

