int CoordToIndex(vec2 fragCoord)
{
    int xx = fragCoord.x >= 512.0 ? 1 : 0;
    int yy = fragCoord.y >= 512.0 ? 2 : 0;
    return yy + xx;
}
vec2 CoordToLocalCoord(vec2 fragCoord)
{
    float modx = fragCoord.x >= 512.0 ? fragCoord.x - 512.0 : fragCoord.x;
    float mody = fragCoord.y >= 512.0 ? fragCoord.y - 512.0 : fragCoord.y; 
    return vec2(modx, mody);
}

int NormalToIndex(vec3 Normal)
{
    bool kk = any(greaterThan(Normal,vec3(0.0)));
    int eigen = Normal.x != 0.0 ? 0 : (Normal.y != 0.0 ? 2 : 4);
    return int(kk) + eigen;
}

mat4 rotate_y(float a)
{
	mat4 ret = mat4(1.0);
	ret[0][0] = ret[2][2] = cos(a);
	ret[0][2] = sin(a);
	ret[2][0] = -ret[0][2];
	return ret;
}

