vec2 PositionToLightmap_G(Ray TempRay, float HitDistance, vec3 HitNormal, int CubeIndex)
{
    vec3 ObjectSpace_Position = TempRay.origin + TempRay.dir * HitDistance;
    int FaceIndex = NormalToIndex(HitNormal);
    int EigenIndex = FaceIndex + 6 * CubeIndex;
//UV-Min:int2(0,0) | UV-Max:int2(19,62) | Real-Min:float3(0.077000,0.610000,0.150000) | Real-Max:float3(0.550000,0.750000,0.150000) | FaceIndex:5 | ObjectIndex:5 | bFlip:1
    if(EigenIndex == 35)
    {
        vec2 AlphaCoord = vec2(ObjectSpace_Position.xy - vec2(0.077000, 0.610000)) / vec2(0.473000, 0.140000);
        return vec2(mix(0.500000, 18.500000, AlphaCoord.y), mix(0.500000, 61.500000, AlphaCoord.x));//2F
    }
//UV-Min:int2(0,62) | UV-Max:int2(62,81) | Real-Min:float3(0.077000,0.610000,0.000000) | Real-Max:float3(0.550000,0.750000,0.000000) | FaceIndex:4 | ObjectIndex:5 | bFlip:0
    else if(EigenIndex == 34)
    {
        vec2 AlphaCoord = vec2(ObjectSpace_Position.xy - vec2(0.077000, 0.610000)) / vec2(0.473000, 0.140000);
