//YCZ:
//Version2 - 2022.06.27(Tokyo time)

//*************About Setting******************//
//No need additional setting. Time axis is as same as the offical one

//*************About Exposure******************//
//Exposure is under Image

//*************About Time******************//
//If time shift is needed for measurement, plz replace all iTime variable with desired time value(included in Buffer A and Image)

//*************About Measurement******************//
//SSIM data example(under exposure of 2):
//Frame0(t = 0.0) ssim 0.961
//Frame1(t = 1.0) ssim 0.963
//Frame2(t = 5.0) ssim 0.946

//(idk how my render will be measured with gt.
//And this Shader will take around 6 seconds to compile (gtx 2070, cpu i7 11xxxx)
//In order to avoid problems such as graphics card model browser model conflict or sth.
//if the value is significantly lower than this value means that this shader is not compiled correctly, 
//please change the browser, 
//thank you very much ^_^
vec4 GetOneFace2(vec2 uv)
{
    return texture(iChannel0, vec3(2.0*vec2(uv.x,1.0 - uv.y)-1.0,-1.0f).xzy);
}
vec4 GetOneFace3(vec2 uv)
{
    return texture(iChannel0, vec3(2.0*vec2(uv.x,uv.y)-1.0,1.0f).xzy);
}
vec4 GetOneFace4(vec2 uv)
{
    return texture(iChannel0, vec3(2.0*vec2(1.0-uv.x,1.0-uv.y)-1.0,-1.0f));
}
vec4 GetOneFace5(vec2 uv)
{
    return texture(iChannel0, vec3(2.0*vec2(uv.x,1.0-uv.y)-1.0,1.0f));
}
Hidden GetHidden(vec2 FragCoord)
{
    Hidden R;
    vec2 LocalUV = FragCoord / 1024.0;
    R.H_0 = GetOneFace2(LocalUV);
    R.H_1 = GetOneFace2(LocalUV + vec2(0.5,0.0));
    R.H_2 = GetOneFace2(LocalUV + vec2(0.0,0.5));
    R.H_3 = GetOneFace2(LocalUV + vec2(0.5,0.5));
    R.H_4 = GetOneFace3(LocalUV);
    R.H_5 = GetOneFace3(LocalUV + vec2(0.5,0.0));
    R.H_6 = GetOneFace3(LocalUV + vec2(0.0,0.5));
    R.H_7 = GetOneFace3(LocalUV + vec2(0.5,0.5));
    return R;
}

