void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	seed = iFrame * NUM_SAMPLES;
	flat_idx = int(dot(gl_FragCoord.xy, vec2(1, 4096)));

	vec2 p = fragCoord.xy / vec2(iResolution) - vec2(0.5);
	float a = float(iResolution.x) / float(iResolution.y);
	if(a < 1.0)
		p.y /= a;
	else
		p.x *= a;

	//vec3 cam_center = vec3(0, 0, 3.0);
	vec3 cam_center = vec3(sin(iTime) * 0.25, sin(iTime * 0.7345) * 0.4 + 0.2, 6.0);
	vec3 cam_target = vec3(0, -0.1, 0);

	mat4 cam = transpose(look_at(cam_center, cam_target, vec3(0, 1, 0)));

	vec3 s = vec3(0);
    float eigen = -100.0;
    if(iFrame == 0)
    {
        return;
    }
    else
    {
		Ray ray;
		ray.origin = cam_center;
		vec3 ray_dir = normalize(vec3(p, -2.5));
		ray.dir = vec3(cam * vec4(ray_dir, 0.0));
        vec3 c;
        vec3 normal;
        vec3 position;
        int faceindex;
        float depth = intersect_simple(ray, position, normal, c, faceindex);
        //depth = depth * 0.1;//make sure 0~1
        eigen = float(faceindex + 1)+0.1;//faceindex == -1 ? 0.999 : float(1 + faceindex) + depth;
		s = c;
    }
	fragColor = vec4(s,eigen);
}

