const float PI = 3.14159265359;
int AA = 2;
float zoom = 100.0;
float cycle = 60.0;
float maxlayers = 20.0;
float time;

float wave(float theta, vec2 p) {
	return 0.5+0.5*cos(dot(p,vec2(cos(theta),sin(theta))));
}

vec3 quasicrystal(vec2 p, float layers) {
    float sum = 0.;
    for (float theta = 0.0; theta < PI; theta += PI/layers) {
      float k = smoothstep(0.0,1.0,min((PI-theta)*layers/PI,1.0));
      sum += k*wave(theta, p);
    }
    if (iMouse.z > 0.0) {
      vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
      sum += length(m)*wave(atan(m.y,m.x),p);
    }
    vec3 t = (1.0+0.5*cos(0.5*time))*sum*vec3(2,3,5);
    t /= layers;
    // Sawtooth
    return fract(t);
    // Triangle wave alternative coloring
    t *= 2.0;
    t = mod(t,2.0);
    t = min(t,2.0-t);
    return t;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  vec3 col = vec3(0);
  time = iTime;
  float t = mod(time/cycle,2.0);
  t = min(t,2.0-t);
  float layers = 3.0+(maxlayers-3.0)*smoothstep(0.0,1.0,t);
  if (key(CHAR_X)) layers = floor(layers);
  if (key(CHAR_Z)) zoom *= 2.0;
  for (int i = 0; i < AA; i++) {
    for (int j = 0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord.xy+vec2(i,j)/float(AA))-iResolution.xy)/iResolution.y;
      uv *= zoom;
      col += quasicrystal(uv,layers);
    }
  }
  col /= float(AA*AA);
  col = pow(col,vec3(0.8));
  fragColor = vec4(col,1);
}

