void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    uvec2 start = uvec2(iResolution.xy * vec2(1.0/8.0));
    uvec2 end = uvec2(iResolution.xy * vec2(7.0/8.0));
    uvec2 ufragCoord = uvec2(fragCoord);
    vec2 frac = mod(fragCoord, vec2(1));

    if (all(greaterThanEqual(ufragCoord, start)) && all(lessThan(ufragCoord, end))) {
        uvec2 radius = RADIUS;
        uvec2 leading = min(radius, ufragCoord - start);
        uvec2 trailing = min(radius, end - ufragCoord);

        vec4 sumy = vec4(0);
        uint dnomy = 0u;

        for (uint y = 0u; y < leading.y + 1u + trailing.y; y++) {
            uint contribution = radius.y + 1u - udiff(y, leading.y);
            sumy += texture(iChannel0, (vec2(ufragCoord - uvec2(0, leading.y) + uvec2(0, y)) + frac) / iResolution.xy) * vec4(contribution);
            dnomy += contribution;
        }

        fragColor = linearToSrgb(sumy / vec4(dnomy));
        //fragColor = linearToSrgb(vec4(vec2(leading + trailing) / 16.0 - 1.0, 0, 1));
    } else {
        fragColor = texture(iChannel0, (vec2(ufragCoord) + frac) / iResolution.xy);
    }
}

