#define RADIUS uvec2(64u)

vec4 srgbToLinear(vec4 col) {
    vec3 linear = col.rgb / 12.9232102;
    vec3 exp = pow((col.rgb + 0.055) / 1.055, vec3(2.4));
    return vec4(mix(linear, exp, greaterThan(col.xyz, vec3(0.0392857))), col.a);
}

vec4 linearToSrgb(vec4 col) {
    vec3 linear = col.rgb * 12.9232102;
    vec3 exp = pow(col.rgb, vec3(1.0 / 2.4)) * 1.055 - 0.055;
    return vec4(mix(linear, exp, greaterThan(col.xyz, vec3(0.003039933))), col.a);
}

uint udiff(uint a, uint b) {
    return a > b ? a - b : b - a;
}

